import React, { useEffect, useState } from "react";
import "../Css/Cart.css";
import Sidebar from "../components/Sidebar";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

function Cart() {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const refreshCart = () => {
      const saved = JSON.parse(localStorage.getItem("cart")) || [];
      setCart(saved);
    };
    refreshCart();
    window.addEventListener("focus", refreshCart);
    return () => window.removeEventListener("focus", refreshCart);
  }, []);

  useEffect(() => {
    const savedUser = JSON.parse(localStorage.getItem("userData"));
    if (savedUser) setUser(savedUser);
  }, []);

  const updateCart = (newCart) => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
    window.dispatchEvent(new Event("cartUpdated"));
  };

  const updateKg = (cropId, enteredQty, availableQty) => {
    let qty = Number(enteredQty);
    if (qty < 1) qty = 1;
    if (qty > availableQty) qty = availableQty;

    const updated = cart.map((item) =>
      item.cropId === cropId ? { ...item, qty } : item
    );
    updateCart(updated);
  };

  const removeItem = (id) => {
    const updated = cart.filter((c) => c.cropId !== id);
    updateCart(updated);
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  const getImage = (imgStr) => {
    if (!imgStr) return "/Images/noimg.png";
    return `http://localhost:9090/backend/uploads/${imgStr.split(",")[0].trim()}`;
  };

  return (
    <>
      <Navbar />

      <div className="dashboard-wrapper">
        {/* SIDEBAR */}
        <Sidebar cartCount={cart.length} />

        {/* RIGHT SIDE CONTENT */}
        <div className="dashboard-content">
          <main className="dashboard-main">

            <div className="cart-header">
              <h2>Your Cart</h2>
              <p>Manage your selected crops before checkout</p>
            </div>

            {/* CART LIST */}
            <section className="cart-container">
              {cart.length === 0 ? (
                <p className="empty-cart">Your cart is empty.</p>
              ) : (
                cart.map((item) => {
                  const available = Number(item.quantity);

                  return (
                    <div className="cart-card" key={item.cropId}>

                      {/* LEFT: IMAGE */}
                      <div className="cart-image-box">
                        <img
                          src={getImage(item.images)}
                          alt={item.cropName}
                          className="cart-img"
                        />
                      </div>

                      {/* RIGHT: DETAILS */}
                      <div className="cart-details">

                        <h3 className="crop-title">{item.cropName}</h3>

                        <p><strong>Farmer ID:</strong> {item.farmerId}</p>
                        <p><strong>Price:</strong> ₹{item.price} / kg</p>

                        <p className="available-stock">
                          <strong>Available Stock:</strong> 
                          <span className={available > 0 ? "green" : "red"}>
                            {available} Kg
                          </span>
                        </p>

                        {/* Quantity Input */}
                        <div className="qty-input-row">
                          <label><strong>Buy Quantity (Kg):</strong></label>

                          <input
                            type="number"
                            min="1"
                            max={available}
                            value={item.qty}
                            className="qty-input"
                            onChange={(e) =>
                              updateKg(item.cropId, e.target.value, available)
                            }
                          />
                        </div>

                        <p className="item-total">
                          Subtotal: <strong>₹{item.price * item.qty}</strong>
                        </p>
                      </div>

                      {/* REMOVE BUTTON */}
                      <button className="remove-btn" onClick={() => removeItem(item.cropId)}>
                        <i className="fa-solid fa-trash"></i> Remove
                      </button>
                    </div>
                  );
                })
              )}

              {/* TOTAL SUMMARY */}
              {cart.length > 0 && (
                <div className="cart-summary">
                  <h3>Total Amount: ₹{total}</h3>
                  <button className="checkout-btn">Proceed to Checkout</button>
                </div>
              )}
            </section>

          </main>

          <Footer />
        </div>
      </div>
    </>
  );
}

export default Cart;
