import React from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";

import Navbar from "./components/Navbar";
import Home from "./components/Home";
import Footer from "./components/Footer";

import Signup from "./pages/Signup";
import Login from "./pages/Login";
import Profile from "./pages/Profile";

import Marketplace from "./pages/Marketplace";
import Sell from "./pages/Sell";
import Buy from "./pages/Buy";
import Seeds from "./pages/Seeds";
import Pesticides from "./pages/Pesticides";
import Fertilizers from "./pages/Fertilizers";
import Cart from "./components/Cart";

import Dashboard from "./pages/Dashboard";


// ⭐ WRAPPER COMPONENT FOR CONDITIONAL NAVBAR/FOOTER
function AppWrapper() {
  const location = useLocation();

  // Pages where Navbar & Footer must be hidden
  const hideLayout = location.pathname.startsWith("/dashboard");

  return (
    <>
      {/* SHOW NAVBAR ONLY IF NOT IN DASHBOARD */}
      {!hideLayout && <Navbar />}

      <Routes>
        {/* HOME */}
        <Route path="/" element={<Home />} />
        <Route path="/home" element={<Home />} />

        {/* AUTH */}
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login />} />
        <Route path="/profile" element={<Profile />} />

        {/* ⭐ DASHBOARD */}
        <Route path="/dashboard" element={<Dashboard />} />

        {/* CART */}
        <Route path="/cart" element={<Cart />} />

        {/* MARKETPLACE */}
        <Route path="/marketplace" element={<Marketplace />}>
          <Route index element={<Seeds />} />
          <Route path="sell" element={<Sell />} />
          <Route path="buy" element={<Buy />} />
          <Route path="seeds" element={<Seeds />} />
          <Route path="pesticides" element={<Pesticides />} />
          <Route path="fertilizers" element={<Fertilizers />} />
        </Route>
      </Routes>

      {/* SHOW FOOTER ONLY IF NOT IN DASHBOARD */}
      {!hideLayout && <Footer />}
    </>
  );
}


// ⭐ MAIN ROOT APP
function App() {
  return (
    <Router>
      <AppWrapper />
    </Router>
  );
}

export default App;
