import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import { useNavigate } from "react-router-dom";
import "../Css/AdminDashboard.css";

function AdminDashboard() {
  const [data, setData] = useState({
    users: [],
    crops: [],
    loans: [],
    insurance: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const loadAdminData = async () => {
      try {
        const res = await fetch("/api/admin/dashboard", {
          credentials: "include",
          cache: "no-store",
        });

        if (res.status === 403) {
          throw new Error("NOT_AUTHORIZED");
        }

        if (!res.ok) {
          throw new Error("SERVER_ERROR");
        }

        const json = await res.json();
        setData({
          users: json.users || [],
          crops: json.crops || [],
          loans: json.loans || [],
          insurance: json.insurance || []
        });
      } catch (err) {
        console.error("Admin dashboard error:", err);

        if (err.message === "NOT_AUTHORIZED") {
          setError("You are not authorized to view this page.");
          setTimeout(() => navigate("/home"), 1500);
        } else {
          setError("Something went wrong. Try again.");
        }
      } finally {
        setLoading(false);
      }
    };

    loadAdminData();
  }, [navigate]);

  /* ================= LOADING ================= */
  if (loading) {
    return (
      <>
        <Navbar />
        <div className="admin-loading">Loading admin dashboard...</div>
      </>
    );
  }

  /* ================= ERROR ================= */
  if (error) {
    return (
      <>
        <Navbar />
        <div className="admin-loading error">{error}</div>
      </>
    );
  }

  /* ================= DASHBOARD ================= */
  return (
    <>
      <Navbar />

      <div className="admin-container">
        <h1>Admin Dashboard</h1>
        <p className="admin-subtitle">
          Overview of platform statistics and records
        </p>

        {/* ===== STATS ===== */}
        <div className="admin-stats">
          <div className="stat-card">
            <h3>Total Users</h3>
            <p>{data.users.length}</p>
          </div>
          <div className="stat-card">
            <h3>Total Crops</h3>
            <p>{data.crops.length}</p>
          </div>
          <div className="stat-card">
            <h3>Loan Applications</h3>
            <p>{data.loans.length}</p>
          </div>
          <div className="stat-card">
            <h3>Insurance Applications</h3>
            <p>{data.insurance.length}</p>
          </div>
        </div>

        {/* ===== USERS ===== */}
        <section className="admin-section">
          <h2>Users</h2>
          <div className="admin-table">
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Mobile</th>
                  <th>Role</th>
                  <th>System Role</th>
                </tr>
              </thead>
              <tbody>
                {data.users.length ? (
                  data.users.map((u, i) => (
                    <tr key={i}>
                      <td>{u.fullName}</td>
                      <td>{u.mobile}</td>
                      <td>{u.role}</td>
                      <td>
                        <span
                          className={`role-badge ${
                            u.systemRole === "ADMIN" ? "admin" : "user"
                          }`}
                        >
                          {u.systemRole}
                        </span>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="4" className="empty">
                      No users found
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </section>

        {/* ===== CROPS ===== */}
        <section className="admin-section">
          <h2>Crops</h2>
          <div className="admin-table">
            <table>
              <thead>
                <tr>
                  <th>Crop</th>
                  <th>Farmer ID</th>
                  <th>Category</th>
                  <th>Quantity</th>
                  <th>Price</th>
                </tr>
              </thead>
              <tbody>
                {data.crops.length ? (
                  data.crops.map((c, i) => (
                    <tr key={i}>
                      <td>{c.cropName}</td>
                      <td>{c.farmerId}</td>
                      <td>{c.category}</td>
                      <td>{c.quantity}</td>
                      <td>₹{c.price}</td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="5" className="empty">
                      No crops found
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </section>

        {/* ===== LOANS ===== */}
        <section className="admin-section">
          <h2>Loan Applications</h2>
          <div className="admin-table">
            <table>
              <thead>
                <tr>
                  <th>Loan ID</th>
                  <th>Farmer Name</th>
                  <th>Amount</th>
                </tr>
              </thead>
              <tbody>
                {data.loans.length ? (
                  data.loans.map((l, i) => (
                    <tr key={i}>
                      <td>{l.loanId}</td>
                      <td>{l.farmerName}</td>
                      <td>₹{l.amount}</td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="3" className="empty">
                      No loan applications found
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </section>

        {/* ===== INSURANCE ===== */}
        <section className="admin-section">
          <h2>Insurance Applications</h2>
          <div className="admin-table">
            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Farmer Name</th>
                  <th>Crop</th>
                  <th>Coverage</th>
                </tr>
              </thead>
              <tbody>
                {data.insurance.length ? (
                  data.insurance.map((i, idx) => (
                    <tr key={idx}>
                      <td>{i.insuranceId}</td>
                      <td>{i.farmerName}</td>
                      <td>{i.crop}</td>
                      <td>{i.coverage}</td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="4" className="empty">
                      No insurance applications found
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </section>
      </div>
    </>
  );
}

export default AdminDashboard;
