import React, { useState, useEffect } from "react";
import "../Css/Sell.css";

function Sell() {
  const [cropImages, setCropImages] = useState([]);
  const [user, setUser] = useState(null);
  const [loadingProfile, setLoadingProfile] = useState(false);

  // Load user after login (may contain farmerId)
  useEffect(() => {
    const stored = localStorage.getItem("userData");
    if (stored) {
      try {
        const parsed = JSON.parse(stored);
        console.log("Loaded userData:", parsed);
        setUser(parsed);
      } catch (err) {
        console.warn("Invalid userData in localStorage");
      }
    } else {
      // try mobile only (older flows stored mobile under userMobile)
      const mobile = localStorage.getItem("userMobile");
      if (mobile) {
        // fetch profile to get farmerId
        fetchProfileByMobile(mobile);
      }
    }
  }, []);

  async function fetchProfileByMobile(mobile) {
    setLoadingProfile(true);
    try {
      const res = await fetch(`http://localhost:9090/backend/api/user/get-profile?mobile=${encodeURIComponent(mobile)}`, {
        method: "GET",
        credentials: "include"
      });
      const data = await res.json();
      if (data.status === "success" && data.user) {
        setUser(data.user);
        // store for future
        localStorage.setItem("userData", JSON.stringify(data.user));
      } else {
        console.warn("Cannot load profile:", data.message || data);
      }
    } catch (err) {
      console.error("Profile fetch error:", err);
    } finally {
      setLoadingProfile(false);
    }
  }

  const [form, setForm] = useState({
    cropName: "",
    category: "Grains",
    quantity: "",
    price: "",
    deliveryTime: "1 Day",
    grade: "A (Premium)",
    description: ""
  });

  const updateField = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files || []);
    const previewFiles = files.map((file) => ({ file, preview: URL.createObjectURL(file) }));
    setCropImages((prev) => [...prev, ...previewFiles]);
  };

  const removeImage = (index) => setCropImages((prev) => prev.filter((_, i) => i !== index));

  const submitCrop = async () => {
    // ensure we have farmerId; if not, try to reload from mobile
    if (!user) {
      const mobile = localStorage.getItem("userMobile");
      if (mobile) {
        await fetchProfileByMobile(mobile);
        if (!user) {
          alert("User not found. Please login.");
          return;
        }
      } else {
        alert("User not found. Please login.");
        return;
      }
    }

    if (!user || !user.farmerId) {
      alert("Farmer ID not found. Please login again.");
      return;
    }

    const formData = new FormData();
    formData.append("farmerId", user.farmerId);
    Object.keys(form).forEach((k) => formData.append(k, form[k] || ""));

    cropImages.forEach((img) => formData.append("images", img.file));

    try {
      const res = await fetch("http://localhost:9090/backend/api/crop/sellingcrop", {
        method: "POST",
        body: formData
      });

      const resultText = await res.text();
      console.log("Server response:", resultText);

      // try to parse JSON if server sent JSON
      let payload = null;
      try { payload = JSON.parse(resultText); } catch (e) {}

      if (payload && payload.status === "SUCCESS") {
        alert("Crop posted successfully! ID: " + (payload.cropId || ""));
        setCropImages([]);
        setForm({
          cropName: "",
          category: "Grains",
          quantity: "",
          price: "",
          deliveryTime: "1 Day",
          grade: "A (Premium)",
          description: ""
        });
      } else {
        // show server message if provided
        const msg = (payload && payload.message) || resultText || "Failed to save crop.";
        alert("Failed to save crop: " + msg);
      }
    } catch (err) {
      console.error("Fetch error:", err);
      alert("Server not reachable. Check backend.");
    }
  };

  return (
    <div className="sell-container">
      <h2 className="sell-title"><i className="fa fa-store"></i> Sell Your Crops</h2>
      {loadingProfile && <p>Loading profile…</p>}
      <div className="sell-layout">
        <div className="sell-image-box">
          <div className="sell-placeholder"><i className="fa fa-image"></i><p>Upload Crop Images</p></div>
          <label className="upload-btn"><i className="fa fa-upload"></i> Upload Images
            <input type="file" accept="image/*" multiple onChange={handleImageUpload} />
          </label>

          <div className="preview-section">
            {cropImages.map((img, index) => (
              <div className="preview-item" key={index}>
                <img src={img.preview} className="preview-img" alt="" />
                <button className="preview-remove" onClick={() => removeImage(index)}>✖</button>
              </div>
            ))}
          </div>
        </div>

        <div className="sell-form">
          <label className="sell-label"><i className="fa fa-leaf"></i> Crop Name</label>
          <input name="cropName" value={form.cropName} onChange={updateField} className="sell-input" />

          <label className="sell-label"><i className="fa fa-list"></i> Category</label>
          <select name="category" value={form.category} onChange={updateField} className="sell-input">
            <option>Grains</option><option>Vegetables</option><option>Fruits</option><option>Pulses</option><option>Oil Seeds</option>
          </select>

          <div className="sell-row">
            <div>
              <label className="sell-label"><i className="fa fa-weight-hanging"></i> Qty (Kg)</label>
              <input name="quantity" type="number" value={form.quantity} onChange={updateField} className="sell-input" />
            </div>

            <div>
              <label className="sell-label"><i className="fa fa-rupee-sign"></i> Price/Kg</label>
              <input name="price" type="number" value={form.price} onChange={updateField} className="sell-input" />
            </div>
          </div>

          <label className="sell-label"><i className="fa fa-clock"></i> Delivery Time</label>
          <select name="deliveryTime" value={form.deliveryTime} onChange={updateField} className="sell-input">
            <option>1 Day</option><option>2–3 Days</option><option>Within a Week</option>
          </select>

          <label className="sell-label"><i className="fa fa-star"></i> Grade</label>
          <select name="grade" value={form.grade} onChange={updateField} className="sell-input">
            <option>A (Premium)</option><option>B (Good)</option><option>C (Average)</option>
          </select>

          <label className="sell-label"><i className="fa fa-align-left"></i> Description</label>
          <textarea name="description" value={form.description} onChange={updateField} className="sell-textarea" />

          <button className="sell-submit" onClick={submitCrop}><i className="fa fa-check-circle"></i> Post Crop For Sale</button>
        </div>
      </div>
    </div>
  );
}

export default Sell;
