import React, { useState } from "react";
import "../Css/Buy.css";

function Buy() {
  const [search, setSearch] = useState("");

  const products = [
    { id: 1, name: "Tomatoes", price: 25, unit: "Kg", img: "/Images/tomato.jpg" },
    { id: 2, name: "Potatoes", price: 20, unit: "Kg", img: "/Images/potato.jpg" },
    { id: 3, name: "Wheat", price: 32, unit: "Kg", img: "/Images/wheat.jpg" },
    { id: 4, name: "Green Chillies", price: 80, unit: "Kg", img: "/Images/chilli.jpg" }
  ];

  return (
    <div className="buy-container">

      {/* Title */}
      <h2 className="buy-title">
        <i className="fa fa-handshake"></i> Buy Fresh Produce
      </h2>

      {/* Search Bar */}
      <div className="buy-search-box">
        <i className="fa fa-search"></i>
        <input
          type="text"
          placeholder="Search crops... (Tomato, Wheat, etc)"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      {/* Products Section */}
      <div className="buy-grid">
        {products
          .filter((p) => p.name.toLowerCase().includes(search.toLowerCase()))
          .map((item) => (
            <div className="buy-card" key={item.id}>
              <img src={item.img} alt={item.name} className="buy-img" />

              <h3 className="buy-name">{item.name}</h3>

              <p className="buy-price">
                <i className="fa fa-rupee-sign"></i> {item.price} / {item.unit}
              </p>

              <button className="buy-btn">
                <i className="fa fa-shopping-cart"></i> Buy Now
              </button>
            </div>
          ))}
      </div>
    </div>
  );
}

export default Buy;
