import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../Css/Checkout.css";
import Navbar from "./Navbar";
import Sidebar from "./Sidebar";
import Footer from "./Footer";

function Checkout() {
  const navigate = useNavigate();

  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);
  const [placing, setPlacing] = useState(false);

  const [form, setForm] = useState({
    fullName: "",
    phone: "",
    addressLine1: "",
    addressLine2: "",
    city: "",
    state: "",
    pincode: "",
    payment: "cod",
  });

  /* =========================
     LOAD CART + USER
  ========================= */
  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);

    const savedUser = JSON.parse(localStorage.getItem("userData"));
    if (savedUser) {
      setUser(savedUser);
      setForm((f) => ({
        ...f,
        fullName: savedUser.fullName || "",
        phone: savedUser.mobile || savedUser.phone || "",
      }));
    }
  }, []);

  /* =========================
     IMAGE HANDLER
  ========================= */
  const getImage = (imgStr) => {
    if (!imgStr) return "/Images/noimg.png";
    if (imgStr.startsWith("http") || imgStr.startsWith("data:")) return imgStr;

    const first = imgStr.split(",")[0].trim();
    return `http://localhost:9090/backend/uploads/${first}`;
  };

  /* =========================
     TOTALS
  ========================= */
  const totals = useMemo(() => {
    const subtotal = cart.reduce(
      (sum, item) => sum + item.price * item.qty,
      0
    );
    const shipping = 0;
    const tax = Math.round(subtotal * 0.06);
    const total = subtotal + shipping + tax;
    return { subtotal, shipping, tax, total };
  }, [cart]);

  const fmt = (n) => new Intl.NumberFormat("en-IN").format(n);

  /* =========================
     VALIDATION
  ========================= */
  const canPlace =
    cart.length > 0 &&
    form.fullName.trim() &&
    form.phone.trim().length >= 10 &&
    form.addressLine1.trim() &&
    form.city.trim() &&
    form.state.trim() &&
    form.pincode.trim().length === 6;

  /* =========================
     PLACE ORDER
  ========================= */
  const placeOrder = async () => {
    if (!canPlace || placing) return;

    try {
      setPlacing(true);

      // ðŸ”¹ BACKEND API (enable when ready)
      /*
      const res = await fetch("http://localhost:9090/backend/api/order/place", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          shipping: form,
          items: cart,
          totals,
          payment: form.payment,
        }),
      });

      const data = await res.json();
      if (data.status !== "success") {
        alert(data.message || "Order failed");
        return;
      }
      */

      // UI demo delay
      await new Promise((res) => setTimeout(res, 800));

      localStorage.setItem("cart", JSON.stringify([]));
      window.dispatchEvent(new Event("cartUpdated"));

      alert("Order placed successfully! ðŸŽ‰");
      navigate("/dashboard", { replace: true });
    } catch (err) {
      alert("Failed to place order");
    } finally {
      setPlacing(false);
    }
  };

  return (
    <>
      <Navbar />

      <div className="layout">
        <Sidebar cartCount={cart.length} />

        <div className="checkout-content">
          <main className="checkout-main" role="main">
            <header className="checkout-header">
              <h2>Checkout</h2>
              <p>Confirm shipping address and payment</p>
            </header>

            <div className="checkout-grid">
              {/* ================= SHIPPING ================= */}
              <section className="card form-card">
                <h3>Shipping Details</h3>

                <label>Full Name</label>
                <input
                  value={form.fullName}
                  onChange={(e) =>
                    setForm({ ...form, fullName: e.target.value })
                  }
                />

                <label>Phone</label>
                <input
                  value={form.phone}
                  onChange={(e) =>
                    setForm({ ...form, phone: e.target.value })
                  }
                />

                <label>Address Line 1</label>
                <input
                  value={form.addressLine1}
                  onChange={(e) =>
                    setForm({ ...form, addressLine1: e.target.value })
                  }
                />

                <label>Address Line 2</label>
                <input
                  value={form.addressLine2}
                  onChange={(e) =>
                    setForm({ ...form, addressLine2: e.target.value })
                  }
                />

                <div className="two-col">
                  <div>
                    <label>City</label>
                    <input
                      value={form.city}
                      onChange={(e) =>
                        setForm({ ...form, city: e.target.value })
                      }
                    />
                  </div>

                  <div>
                    <label>State</label>
                    <input
                      value={form.state}
                      onChange={(e) =>
                        setForm({ ...form, state: e.target.value })
                      }
                    />
                  </div>
                </div>

                <label>Pincode</label>
                <input
                  value={form.pincode}
                  onChange={(e) =>
                    setForm({ ...form, pincode: e.target.value })
                  }
                />

                {/* PAYMENT */}
                <h3>Payment Method</h3>
                <label className="radio active">
                  <input
                    type="radio"
                    checked
                    readOnly
                  />
                  Cash on Delivery
                </label>

                <label className="radio disabled">
                  <input type="radio" disabled />
                  UPI (Coming Soon)
                </label>
              </section>

              {/* ================= SUMMARY ================= */}
              <aside className="card summary-card">
                <h3>Order Summary</h3>

                {cart.length === 0 ? (
                  <p>Your cart is empty</p>
                ) : (
                  cart.map((item) => (
                    <div className="item-row" key={item.cropId}>
                      <img
                        src={getImage(item.images)}
                        alt={item.cropName}
                        className="item-thumb"
                      />
                      <div>
                        <strong>{item.cropName}</strong>
                        <span>
                          {item.qty} Ã— â‚¹{fmt(item.price)}
                        </span>
                      </div>
                      <b>â‚¹{fmt(item.qty * item.price)}</b>
                    </div>
                  ))
                )}

                <div className="summary-rows">
                  <div className="row">
                    <span>Subtotal</span>
                    <b>â‚¹{fmt(totals.subtotal)}</b>
                  </div>
                  <div className="row">
                    <span>Tax (6%)</span>
                    <b>â‚¹{fmt(totals.tax)}</b>
                  </div>
                  <div className="row total">
                    <span>Total</span>
                    <b>â‚¹{fmt(totals.total)}</b>
                  </div>
                </div>

                <button
                  className="place-order-btn"
                  disabled={!canPlace || placing}
                  onClick={placeOrder}
                >
                  {placing ? "Placing Order..." : "Place Order"}
                </button>

                <p className="secure-note">
                  <i className="fa-solid fa-lock"></i>
                  &nbsp;Secure checkout
                </p>
              </aside>
            </div>
          </main>

          <Footer />
        </div>
      </div>
    </>
  );
}

export default Checkout;
