import React, { useState } from "react";
import "../Css/LoanPage.css";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

function LoanPage() {
  // EMI calculator state
  const [loanAmount, setLoanAmount] = useState(50000);
  const [interest, setInterest] = useState(7);
  const [tenure, setTenure] = useState(12);

  const emi =
    (loanAmount * interest * 0.01 * Math.pow(1 + interest * 0.01, tenure)) /
    (Math.pow(1 + interest * 0.01, tenure) - 1);

  // MULTI-STEP FORM STATE
  const [step, setStep] = useState(1);
  const [showSuccess, setShowSuccess] = useState(false);

  const [formData, setFormData] = useState({
    fullName: "",
    dob: "",
    aadhaar: "",
    address: "",
    state: "",
    pinCode: "",
    phone: "",
    email: "",
    loanAmountNeeded: "",
    loanPurpose: "",
    cropActivity: "",
    landArea: "",
    totalLandActivity: "",
    idProof: null,
    addressProof: null,
    landProof: null,
    declaration: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files && files[0] ? files[0] : null,
    }));
  };

  const handleSendOtp = () => {
    // just UI for now – backend can be added later
    alert("OTP sent to your mobile number (demo).");
  };

  // validation per step
  const validateStep = () => {
    if (step === 1) {
      const { fullName, dob, aadhaar, address, state, pinCode, phone } =
        formData;
      if (
        !fullName ||
        !dob ||
        !aadhaar ||
        !address ||
        !state ||
        !pinCode ||
        !phone
      ) {
        alert("Please complete all personal & contact details.");
        return false;
      }
    }
    if (step === 2) {
      const {
        loanAmountNeeded,
        loanPurpose,
        cropActivity,
        landArea,
        totalLandActivity,
      } = formData;
      if (
        !loanAmountNeeded ||
        !loanPurpose ||
        !cropActivity ||
        !landArea ||
        !totalLandActivity
      ) {
        alert("Please complete loan requirement & agricultural information.");
        return false;
      }
    }
    if (step === 3) {
      const { idProof, addressProof, landProof, declaration } = formData;
      if (!idProof || !addressProof || !landProof) {
        alert("Please upload all required documents.");
        return false;
      }
      if (!declaration) {
        alert("Please confirm that the information provided is true.");
        return false;
      }
    }
    return true;
  };

  const nextStep = () => {
    if (validateStep()) setStep((s) => Math.min(3, s + 1));
  };

  const prevStep = () => setStep((s) => Math.max(1, s - 1));

  const handleSubmitApplication = () => {
    if (!validateStep()) return;
    // TODO: send data to backend (Java/JDBC)
    setShowSuccess(true);
  };

  const progressPercent = (step / 3) * 100;

  return (
    <>
      <Navbar />

      <div className="loan-container">
        {/* ===== HERO SECTION ===== */}
        <div className="loan-hero">
          <h1>Apply for Your Agri-Loan</h1>
          <p>Quick, hassle-free financing designed for your crop cycle.</p>
        </div>

        {/* ===== LOAN PRODUCTS (keep as before) ===== */}
        <h2 className="section-title">Loan Products</h2>
        <div className="loan-cards">
          <div className="loan-card">
            <h3>Crop Loan</h3>
            <p>Short-term loan for seeds, fertilizers, pesticides & working capital.</p>
            <span>Interest: 7% p.a.</span>
          </div>
          <div className="loan-card">
            <h3>Equipment Loan</h3>
            <p>Finance tractors, harvesters, irrigation tools & machinery.</p>
            <span>Interest: 6.5% p.a.</span>
          </div>
          <div className="loan-card">
            <h3>Livestock Loan</h3>
            <p>Support to expand cattle, goats, poultry & dairy.</p>
            <span>Interest: 8% p.a.</span>
          </div>
          <div className="loan-card">
            <h3>Agri-Business Loan</h3>
            <p>Funding for agri-shops, storage, and food processing.</p>
            <span>Interest: 9% p.a.</span>
          </div>
        </div>

        {/* ===== EMI CALCULATOR ===== */}
        <h2 className="section-title">EMI Calculator</h2>
        <div className="emi-section">
          <div className="emi-inputs">
            <label>Loan Amount (₹)</label>
            <input
              type="number"
              value={loanAmount}
              onChange={(e) => setLoanAmount(Number(e.target.value))}
            />
            <label>Interest Rate (%)</label>
            <input
              type="number"
              value={interest}
              onChange={(e) => setInterest(Number(e.target.value))}
            />
            <label>Tenure (Months)</label>
            <input
              type="number"
              value={tenure}
              onChange={(e) => setTenure(Number(e.target.value))}
            />
          </div>
          <div className="emi-result">
            <h3>Your EMI</h3>
            <p>₹ {emi ? emi.toFixed(2) : "0.00"} / month</p>
          </div>
        </div>

        {/* ===== APPLY SECTION (KisanLend-style multi-step card) ===== */}
        <h2 className="section-title">Apply for Loan</h2>

        <div className="loan-application-card">
          {/* Top title & progress */}
          <div className="loan-app-header">
            <div>
              <h3>Apply for Your Kisan Agri-Loan</h3>
              <p>
                Step {step} of 3 ·{" "}
                {step === 1
                  ? "Personal & Contact Details"
                  : step === 2
                  ? "Loan Requirements & Agricultural Info"
                  : "Document Uploads & Declaration"}
              </p>
            </div>
          </div>

          <div className="loan-app-progress">
            <div
              className="loan-app-progress-fill"
              style={{ width: `${progressPercent}%` }}
            ></div>
          </div>

          {/* STEP 1 */}
          {step === 1 && (
            <div className="loan-step-content">
              <h4 className="loan-step-title">
                Step 1: Personal & Contact Details
              </h4>

              <div className="loan-grid">
                <div className="field">
                  <label>Full Legal Name *</label>
                  <input
                    type="text"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>Date of Birth *</label>
                  <input
                    type="date"
                    name="dob"
                    value={formData.dob}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>Aadhaar / PAN Number *</label>
                  <input
                    type="text"
                    name="aadhaar"
                    value={formData.aadhaar}
                    onChange={handleChange}
                  />
                </div>

                <div className="field mobile-otp-field">
                  <div className="field-label-row">
                    <label>Mobile Number *</label>
                    <button
                      type="button"
                      className="otp-btn"
                      onClick={handleSendOtp}
                    >
                      Send OTP
                    </button>
                  </div>
                  <input
                    type="text"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                  />
                </div>

                <div className="field wide">
                  <label>Complete Address *</label>
                  <input
                    type="text"
                    name="address"
                    value={formData.address}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>State *</label>
                  <input
                    type="text"
                    name="state"
                    value={formData.state}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>Pin Code *</label>
                  <input
                    type="text"
                    name="pinCode"
                    value={formData.pinCode}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>Email (optional)</label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div className="loan-step-actions">
                <button type="button" className="next-btn" onClick={nextStep}>
                  Next
                </button>
              </div>
            </div>
          )}

          {/* STEP 2 */}
          {step === 2 && (
            <div className="loan-step-content">
              <h4 className="loan-step-title">
                Step 2: Loan Requirements & Agricultural Information
              </h4>

              <div className="loan-grid">
                <div className="field">
                  <label>Loan Amount Needed (₹) *</label>
                  <input
                    type="number"
                    name="loanAmountNeeded"
                    value={formData.loanAmountNeeded}
                    onChange={handleChange}
                    placeholder="50,000 - 10,00,000"
                  />
                </div>

                <div className="field">
                  <label>Loan Purpose *</label>
                  <input
                    type="text"
                    name="loanPurpose"
                    value={formData.loanPurpose}
                    onChange={handleChange}
                    placeholder="Crop input / equipment / livestock / others"
                  />
                </div>

                <div className="field">
                  <label>Primary Crop / Activity *</label>
                  <input
                    type="text"
                    name="cropActivity"
                    value={formData.cropActivity}
                    onChange={handleChange}
                    placeholder="e.g. Paddy, Cotton, Dairy"
                  />
                </div>

                <div className="field">
                  <label>Total Land Area (Acres) *</label>
                  <input
                    type="number"
                    name="landArea"
                    value={formData.landArea}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>Total Land Activity *</label>
                  <input
                    type="text"
                    name="totalLandActivity"
                    value={formData.totalLandActivity}
                    onChange={handleChange}
                    placeholder="Crop production / mixed / allied"
                  />
                </div>
              </div>

              <div className="loan-step-actions">
                <button
                  type="button"
                  className="back-btn"
                  onClick={prevStep}
                >
                  Back
                </button>
                <button
                  type="button"
                  className="next-btn"
                  onClick={nextStep}
                >
                  Next
                </button>
              </div>
            </div>
          )}

          {/* STEP 3 */}
          {step === 3 && (
            <div className="loan-step-content">
              <h4 className="loan-step-title">Step 3: Document Uploads</h4>

              <div className="upload-grid">
                <label className="upload-card">
                  <div className="upload-icon">👤</div>
                  <p>ID Proof</p>
                  <span>{formData.idProof ? formData.idProof.name : "Upload ID Proof"}</span>
                  <input
                    type="file"
                    name="idProof"
                    onChange={handleFileChange}
                  />
                </label>

                <label className="upload-card">
                  <div className="upload-icon">🏠</div>
                  <p>Address Proof</p>
                  <span>
                    {formData.addressProof
                      ? formData.addressProof.name
                      : "Upload Address Proof"}
                  </span>
                  <input
                    type="file"
                    name="addressProof"
                    onChange={handleFileChange}
                  />
                </label>

                <label className="upload-card">
                  <div className="upload-icon">📄</div>
                  <p>Land Ownership Proof</p>
                  <span>
                    {formData.landProof
                      ? formData.landProof.name
                      : "Upload Land Proof"}
                  </span>
                  <input
                    type="file"
                    name="landProof"
                    onChange={handleFileChange}
                  />
                </label>
              </div>

              <p className="encryption-note">
                Your documents are secured with 256-bit encryption.
              </p>

              <label className="declaration-row">
                <input
                  type="checkbox"
                  name="declaration"
                  checked={formData.declaration}
                  onChange={handleChange}
                />
                <span>
                  I declare that the information provided is true and accurate.
                </span>
              </label>

              <div className="loan-step-actions single">
                <button
                  type="button"
                  className="back-btn"
                  onClick={prevStep}
                >
                  Back
                </button>
                <button
                  type="button"
                  className="submit-main-btn"
                  onClick={handleSubmitApplication}
                >
                  Submit Application
                </button>
              </div>
            </div>
          )}
        </div>

        {/* SUCCESS POPUP */}
        {showSuccess && (
          <div className="success-popup">
            <div className="success-box">
              <h2>🎉 Application Submitted!</h2>
              <p>
                Thank you. Your agri-loan application has been submitted
                successfully. Our team will contact you shortly.
              </p>
              <button onClick={() => setShowSuccess(false)}>Close</button>
            </div>
          </div>
        )}
      </div>

      <Footer />
    </>
  );
}

export default LoanPage;
