import React, { useEffect, useState } from "react";
import "../Css/Profile.css";

function Profile() {
  const mobile = localStorage.getItem("userMobile");

  const [loading, setLoading] = useState(false);
  const [showPopup, setShowPopup] = useState(false);

  const [user, setUser] = useState({
    fullName: "",
    email: "",
    address: "",
    state: "",
    district: "",
    pincode: "",
    farmSize: "",
    mainCrops: "",
    bankAccount: "",
    ifsc: "",
    gst: "",
    pan: "",
    aadhar: "",
    profilePhoto: "",
    document1: "",
    document2: "",
    verified: 0,
    role: "Farmer",
    farmerId: ""
  });

  const [photoFile, setPhotoFile] = useState(null);
  const [doc1, setDoc1] = useState(null);
  const [doc2, setDoc2] = useState(null);

  useEffect(() => {
    if (!mobile) return;

    setLoading(true);
    fetch(`http://localhost:9090/backend/api/user/get-profile?mobile=${mobile}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setUser((prev) => ({ ...prev, ...data.user }));
        }
      })
      .catch((err) => console.error("Profile fetch error:", err))
      .finally(() => setLoading(false));
  }, [mobile]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((p) => ({ ...p, [name]: value }));
  };

  const handleSave = async () => {
    if (!mobile) {
      alert("Mobile missing. Login again.");
      return;
    }

    const fd = new FormData();
    Object.keys(user).forEach((k) => {
      if (["profilePhoto", "document1", "document2", "farmerId"].includes(k))
        return;
      fd.append(k, user[k] ?? "");
    });
    fd.append("mobile", mobile);

    if (photoFile) fd.append("profilePhoto", photoFile);
    if (doc1) fd.append("document1", doc1);
    if (doc2) fd.append("document2", doc2);

    try {
      setLoading(true);
      const res = await fetch(
        "http://localhost:9090/backend/api/user/update-profile",
        { method: "POST", body: fd }
      );

      const data = await res.json();

      if (data.status === "success") {
        setShowPopup(true);

        const ref = await fetch(
          `http://localhost:9090/backend/api/user/get-profile?mobile=${mobile}`
        );
        const newData = await ref.json();
        if (newData.status === "success") setUser(newData.user);

        setTimeout(() => setShowPopup(false), 2000);
      } else {
        alert(data.message);
      }
    } catch (err) {
      alert("Update failed: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  const profileImg = photoFile
    ? URL.createObjectURL(photoFile)
    : user.profilePhoto
    ? "http://localhost:9090/backend/" + user.profilePhoto
    : "/default-avatar.png";

  return (
    <div className="profile-page-background">
      {showPopup && (
        <div className="success-popup">
          <div className="popup-box">
            <div className="popup-check">✔</div>
            <h3>Update Complete</h3>
            <p>Your profile has been successfully updated.</p>
          </div>
        </div>
      )}

      <div className="profile-card">
        <div className="profile-header">
          <h2>User Profile Setup</h2>
          <button className="logout-btn" onClick={logout}>Logout</button>
        </div>

        {loading && <p>Loading…</p>}

        <div className="profile-form-layout">

          {/* LEFT SIDE */}
          <div>
            <h3 className="section-title">Personal Details</h3>

            {/* PHOTO BLOCK */}
            <div className="vertical-photo-block">
              <img src={profileImg} className="profile-photo" alt="profile" />

              {user.farmerId && (
                <div className="farmer-id-display">
                  <label>Farmer ID:</label>
                  <span>{user.farmerId}</span>
                </div>
              )}

              <label className="custom-file-label">
                <i className="fas fa-camera"></i> Choose Photo
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => setPhotoFile(e.target.files[0])}
                />
              </label>
            </div>

            {/* ROLE SELECT */}
            <label className="field-label">
              <i className="fas fa-user-tag"></i> User Role
            </label>
            <select name="role" value={user.role} onChange={handleChange} className="form-input">
              <option value="Farmer">Farmer</option>
              <option value="Buyer">Buyer</option>
            </select>

            {/* NAME */}
            <label className="field-label">
              <i className="fas fa-user"></i> Full Name
            </label>
            <input className="form-input" name="fullName" value={user.fullName} onChange={handleChange}/>

            {/* EMAIL */}
            <label className="field-label">
              <i className="fas fa-envelope"></i> Email
            </label>
            <input className="form-input" name="email" value={user.email} onChange={handleChange}/>

            {/* ADDRESS */}
            <label className="field-label">
              <i className="fas fa-map-marker-alt"></i> Address
            </label>
            <textarea className="form-input textarea" name="address" value={user.address} onChange={handleChange}/>

            {/* STATE + DISTRICT */}
            <div className="input-group-row">
              <div style={{ width: "50%" }}>
                <label className="field-label"><i className="fas fa-flag"></i> State</label>
                <input className="form-input" name="state" value={user.state} onChange={handleChange}/>
              </div>

              <div style={{ width: "50%" }}>
                <label className="field-label"><i className="fas fa-city"></i> District</label>
                <input className="form-input" name="district" value={user.district} onChange={handleChange}/>
              </div>
            </div>

            {/* PINCODE */}
            <label className="field-label"><i className="fas fa-mail-bulk"></i> Pincode</label>
            <input className="form-input" name="pincode" value={user.pincode} onChange={handleChange}/>

            {/* AADHAR */}
            <label className="field-label"><i className="fas fa-id-card"></i> Aadhar Number</label>
            <div className="aadhar-section">
              <input className="form-input" name="aadhar" value={user.aadhar} onChange={handleChange}/>
              <span className="verify-status">
                {user.verified === 1 ? <span className="verified">✔ Verified</span> : <span className="pending">⏳ Pending</span>}
              </span>
            </div>
          </div>

          {/* RIGHT SIDE */}
          <div>
            <h3 className="section-title">Farm & Business</h3>

            <div className="input-group-row">
              <div style={{ width: "50%" }}>
                <label className="field-label"><i className="fas fa-seedling"></i> Farm Size</label>
                <input className="form-input" name="farmSize" value={user.farmSize} onChange={handleChange}/>
              </div>

              <div style={{ width: "50%" }}>
                <label className="field-label"><i className="fas fa-leaf"></i> Main Crops</label>
                <input className="form-input" name="mainCrops" value={user.mainCrops} onChange={handleChange}/>
              </div>
            </div>

            <div className="input-group-row">
              <div style={{ width: "50%" }}>
                <label className="field-label"><i className="fas fa-file-invoice"></i> GST</label>
                <input className="form-input" name="gst" value={user.gst} onChange={handleChange}/>
              </div>

              <div style={{ width: "50%" }}>
                <label className="field-label"><i className="fas fa-id-badge"></i> PAN</label>
                <input className="form-input" name="pan" value={user.pan} onChange={handleChange}/>
              </div>
            </div>

            <h3 className="section-title">Bank Information</h3>

            <label className="field-label"><i className="fas fa-university"></i> Bank Account</label>
            <input className="form-input" name="bankAccount" value={user.bankAccount} onChange={handleChange}/>

            <label className="field-label"><i className="fas fa-code"></i> IFSC Code</label>
            <input className="form-input" name="ifsc" value={user.ifsc} onChange={handleChange}/>

            <h3 className="section-title">Upload Documents</h3>

            <div className="doc-upload-container">
              <label className="doc-upload-label">Land Proof:</label>
              <label className="circle-upload">
                <img src="/Images/cloud-upload.png" className="upload-icon" />
                <input type="file" className="hidden-file-input" onChange={(e) => setDoc1(e.target.files[0])}/>
              </label>
              <span className="uploaded-file-name">
                {doc1 ? doc1.name : user.document1 ? user.document1.split("/").pop() : ""}
              </span>
            </div>

            <div className="doc-upload-container">
              <label className="doc-upload-label">Other Document:</label>
              <label className="circle-upload">
                <img src="/Images/cloud-upload.png" className="upload-icon" />
                <input type="file" className="hidden-file-input" onChange={(e) => setDoc2(e.target.files[0])}/>
              </label>
              <span className="uploaded-file-name">
                {doc2 ? doc2.name : user.document2 ? user.document2.split("/").pop() : ""}
              </span>
            </div>
          </div>
        </div>

        <button className="save-profile-btn" onClick={handleSave} disabled={loading}>
          {loading ? "Saving..." : "Save Profile"}
        </button>
      </div>
    </div>
  );
}

export default Profile;
