import React, { useEffect, useState } from "react";
import "../Css/Dashboard.css";

function Dashboard() {
  const [user, setUser] = useState(null);
  const [crops, setCrops] = useState([]);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("userData"));
    if (stored) {
      setUser(stored);

      fetch(`http://localhost:9090/backend/api/crop/farmer?farmerId=${stored.farmerId}`)
        .then((res) => res.json())
        .then((data) => setCrops(data))
        .catch((err) => console.error("Failed to load crops", err));
    }
  }, []);

  return (
    <div className="dashboard-container">

      <div className="dashboard-header">
        <h2>Welcome, {user?.fullName} ðŸ‘‹</h2>
        <p>Your farming performance and activity overview</p>
      </div>

      {/* STATS */}
      <div className="dashboard-stats">
        <div className="stat-card">
          <i className="fa-solid fa-wheat-awn"></i>
          <h3>{crops.length}</h3>
          <p>Total Crops Listed</p>
        </div>

        <div className="stat-card">
          <i className="fa-solid fa-gauge-high"></i>
          <h3>Coming Soon</h3>
          <p>Earnings</p>
        </div>
      </div>

      {/* CROPS TABLE */}
      <div className="dashboard-section">
        <h3>Your Crop Listings</h3>

        <table className="crop-table">
          <thead>
            <tr>
              <th>Crop</th>
              <th>Category</th>
              <th>Quantity</th>
              <th>Price</th>
            </tr>
          </thead>

          <tbody>
            {crops.length === 0 ? (
              <tr>
                <td colSpan="5" style={{ textAlign: "center", padding: "20px" }}>
                  No crops posted yet.
                </td>
              </tr>
            ) : (
              crops.map((c, index) => (
                <tr key={index}>
                  <td>{c.cropName}</td>
                  <td>{c.category}</td>
                  <td>{c.quantity} kg</td>
                  <td>â‚¹{c.price}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
