import React, { useState } from "react";
import "../Css/Marketplace.css";

function Marketplace() {
  const [activeTab, setActiveTab] = useState("sell");

  return (
    <div className="marketplace-container">

      {/* INTERNAL NAVBAR */}
      <div className="marketplace-nav">

        <button
          className={activeTab === "sell" ? "mp-tab active" : "mp-tab"}
          onClick={() => setActiveTab("sell")}
        >
          <i className="fa fa-store"></i> Sell
        </button>

        <button
          className={activeTab === "buy" ? "mp-tab active" : "mp-tab"}
          onClick={() => setActiveTab("buy")}
        >
          <i className="fa fa-handshake"></i> Buy
        </button>

        <button
          className={activeTab === "seeds" ? "mp-tab active" : "mp-tab"}
          onClick={() => setActiveTab("seeds")}
        >
          <i className="fa fa-seedling"></i> Seeds
        </button>

        <button
          className={activeTab === "pesticides" ? "mp-tab active" : "mp-tab"}
          onClick={() => setActiveTab("pesticides")}
        >
          <i className="fa fa-vial"></i> Pesticides
        </button>

        <button
          className={activeTab === "fertilizers" ? "mp-tab active" : "mp-tab"}
          onClick={() => setActiveTab("fertilizers")}
        >
          <i className="fa fa-leaf"></i> Fertilizers
        </button>

      </div>

      {/* PAGE CONTENT */}
      <div className="marketplace-content">
        {activeTab === "sell" && <h2><i className="fa fa-store"></i> Sell Your Crops</h2>}
        {activeTab === "buy" && <h2><i className="fa fa-handshake"></i> Buy Fresh Produce</h2>}
        {activeTab === "seeds" && <h2><i className="fa fa-seedling"></i> Seeds Marketplace</h2>}
        {activeTab === "pesticides" && <h2><i className="fa fa-vial"></i> Pesticides Store</h2>}
        {activeTab === "fertilizers" && <h2><i className="fa fa-leaf"></i> Fertilizers & Nutrition</h2>}
      </div>

    </div>
  );
}

export default Marketplace;
