import React, { useEffect, useState } from "react";
import "../Css/Settings.css";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import Footer from "../components/Footer";

const BASE_URL = "http://localhost:9090/backend/api";

function Settings() {
  const [loading, setLoading] = useState(true);

  const [form, setForm] = useState({
    fullName: "",
    phone: "",
    email: "",
  });

  const [passwords, setPasswords] = useState({
    oldPass: "",
    newPass: "",
    confirmPass: "",
  });

  /* =========================
     LOAD PROFILE (SESSION)
  ========================= */
  useEffect(() => {
    fetch(`${BASE_URL}/user/get-profile`, {
      credentials: "include",
    })
      .then((res) => res.json())
      .then((data) => {
        if (!data || data.status === "error") {
          window.location.href = "/login";
          return;
        }

        setForm({
          fullName: data.fullName || "",
          phone: data.mobile || "",
          email: data.email || "",
        });

        // keep navbar/sidebar in sync
        localStorage.setItem("userData", JSON.stringify(data));
        setLoading(false);
      })
      .catch(() => {
        alert("Failed to load profile");
        setLoading(false);
      });
  }, []);

  /* =========================
     SAVE PROFILE
  ========================= */
  const saveProfile = async () => {
    if (!form.fullName || !form.phone || !form.email) {
      alert("All fields are required");
      return;
    }

    try {
      const res = await fetch(`${BASE_URL}/user/update-profile`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(form),
      });

      const data = await res.json();

      if (data.status === "success") {
        localStorage.setItem("userData", JSON.stringify(data.user));
        alert("Profile updated successfully!");
      } else {
        alert(data.message || "Update failed");
      }
    } catch (err) {
      alert("Server error while updating profile");
    }
  };

  /* =========================
     UPDATE PASSWORD
  ========================= */
  const updatePassword = async () => {
    const { oldPass, newPass, confirmPass } = passwords;

    if (!oldPass || !newPass || !confirmPass) {
      alert("All password fields are required");
      return;
    }

    if (newPass !== confirmPass) {
      alert("New passwords do not match");
      return;
    }

    if (newPass.length < 6) {
      alert("Password must be at least 6 characters");
      return;
    }

    try {
      const res = await fetch(`${BASE_URL}/user/change-password`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          oldPassword: oldPass,
          newPassword: newPass,
        }),
      });

      const data = await res.json();

      if (data.status === "success") {
        alert("Password updated successfully");
        setPasswords({ oldPass: "", newPass: "", confirmPass: "" });
      } else {
        alert(data.message || "Password update failed");
      }
    } catch (err) {
      alert("Server error while updating password");
    }
  };

  if (loading) return null;

  return (
    <>
      <Navbar />

      <div className="layout">
        <Sidebar cartCount={0} />

        <div className="content-area">
          <main className="settings-page">
            <h2>⚙️ Account Settings</h2>
            <p className="subtitle">Manage profile and security settings</p>

            {/* ================= PROFILE ================= */}
            <section className="settings-box">
              <h3>Profile Information</h3>

              <div className="profile-row">
                <div className="profile-photo">
                  <i className="fa-solid fa-user"></i>
                </div>

                <div className="profile-fields">
                  <label>Full Name</label>
                  <input
                    type="text"
                    value={form.fullName}
                    onChange={(e) =>
                      setForm({ ...form, fullName: e.target.value })
                    }
                  />

                  <label>Email</label>
                  <input
                    type="email"
                    value={form.email}
                    onChange={(e) =>
                      setForm({ ...form, email: e.target.value })
                    }
                  />

                  <label>Phone</label>
                  <input
                    type="tel"
                    value={form.phone}
                    onChange={(e) =>
                      setForm({ ...form, phone: e.target.value })
                    }
                  />

                  <button className="save-btn" onClick={saveProfile}>
                    <i className="fa-solid fa-check"></i> Save Changes
                  </button>
                </div>
              </div>
            </section>

            {/* ================= PASSWORD ================= */}
            <section className="settings-box">
              <h3>Change Password</h3>

              <label>Old Password</label>
              <input
                type="password"
                value={passwords.oldPass}
                onChange={(e) =>
                  setPasswords({ ...passwords, oldPass: e.target.value })
                }
              />

              <label>New Password</label>
              <input
                type="password"
                value={passwords.newPass}
                onChange={(e) =>
                  setPasswords({ ...passwords, newPass: e.target.value })
                }
              />

              <label>Confirm New Password</label>
              <input
                type="password"
                value={passwords.confirmPass}
                onChange={(e) =>
                  setPasswords({
                    ...passwords,
                    confirmPass: e.target.value,
                  })
                }
              />

              <button className="save-btn" onClick={updatePassword}>
                <i className="fa-solid fa-lock"></i> Update Password
              </button>
            </section>
          </main>

          <Footer />
        </div>
      </div>
    </>
  );
}

export default Settings;
