import React, { useEffect, useState } from "react";
import "../Css/Dashboard.css"; 
import "../Css/Cart.css";

function Cart() {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);

  // â­ LOAD CART when page opens + refresh on focus
  useEffect(() => {
    const refreshCart = () => {
      const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
      setCart(savedCart);
    };

    refreshCart(); // Load once when page appears

    // Refresh when window (tab/page) becomes active
    window.addEventListener("focus", refreshCart);

    return () => window.removeEventListener("focus", refreshCart);
  }, []);

  // Load user details
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("userData"));
    if (storedUser) setUser(storedUser);
  }, []);

  // Save cart changes
  const updateCart = (newCart) => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
  };

  // Increase quantity
  const increaseQty = (id) => {
    const updated = cart.map((c) =>
      c.cropId === id ? { ...c, qty: c.qty + 1 } : c
    );
    updateCart(updated);
  };

  // Decrease quantity
  const decreaseQty = (id) => {
    const updated = cart.map((c) =>
      c.cropId === id ? { ...c, qty: Math.max(1, c.qty - 1) } : c
    );
    updateCart(updated);
  };

  // Remove item
  const removeItem = (id) => {
    const updated = cart.filter((c) => c.cropId !== id);
    updateCart(updated);
  };

  // Total sum
  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  // Build image
  const getImage = (imgStr) => {
    if (!imgStr) return "/Images/noimg.png";
    const first = imgStr.split(",")[0].trim();
    return `http://localhost:9090/backend/uploads/${first}`;
  };

  return (
    <div className="dashboard-wrapper">

      {/* ---------------- SIDEBAR ---------------- */}
      <aside className="sidebar">
        <h2 className="logo">AgroLink</h2>

        <ul className="menu">
          <li onClick={() => (window.location.href = "/dashboard")}>
            <i className="fa-solid fa-gauge"></i> Dashboard
          </li>

          <li>
            <i className="fa-solid fa-leaf"></i> My Crops
          </li>

          {/* ACTIVE CART PAGE */}
          <li className="active">
            <i className="fa-solid fa-cart-shopping"></i> Cart
            <span className="cart-badge">{cart.length}</span>
          </li>

          <li>
            <i className="fa-solid fa-chart-line"></i> Analytics
          </li>

          <li>
            <i className="fa-solid fa-gear"></i> Settings
          </li>

          <li>
            <i className="fa-solid fa-headset"></i> Support
          </li>
        </ul>
      </aside>

      {/* ---------------- MAIN CONTENT ---------------- */}
      <main className="dashboard-main">

        {/* HEADER */}
        <header className="top-header">
          <div>
            <h2>Hello, {user?.fullName} ðŸ‘‹</h2>
            <p>Your shopping cart summary</p>
          </div>

          <div className="profile-box">
            <i className="fa-solid fa-user"></i>
          </div>
        </header>

        {/* ---------------- CART SECTION ---------------- */}
        <section className="cart-container">

          <h2 className="cart-title">ðŸ›’ Your Cart</h2>

          {cart.length === 0 ? (
            <p className="empty-cart">Your cart is empty.</p>
          ) : (
            <div className="cart-list">
              {cart.map((item) => (
                <div className="cart-card" key={item.cropId}>
                  
                  <img
                    src={getImage(item.images)}
                    alt={item.cropName}
                    className="cart-img"
                  />

                  <div className="cart-details">
                    <h3>{item.cropName}</h3>
                    <p><b>Farmer ID:</b> {item.farmerId}</p>
                    <p><b>Price:</b> â‚¹{item.price} / kg</p>

                    <div className="qty-box">
                      <button onClick={() => decreaseQty(item.cropId)}>-</button>
                      <span>{item.qty}</span>
                      <button onClick={() => increaseQty(item.cropId)}>+</button>
                    </div>

                    <p className="item-total">
                      Subtotal: â‚¹{item.price * item.qty}
                    </p>
                  </div>

                  <button className="remove-btn" onClick={() => removeItem(item.cropId)}>
                    Remove
                  </button>

                </div>
              ))}
            </div>
          )}

          {/* ---------------- TOTAL ---------------- */}
          {cart.length > 0 && (
            <div className="cart-summary">
              <h3>Total Amount: â‚¹{total}</h3>
              <button className="checkout-btn">Proceed to Checkout</button>
            </div>
          )}

        </section>
      </main>

    </div>
  );
}

export default Cart;
