import React, { useState, useEffect } from "react";
import "../Css/Buy.css";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

function Buy() {
  const [search, setSearch] = useState("");
  const [crops, setCrops] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);

  /* =========================
     LOAD CART FROM STORAGE
  ========================= */
  useEffect(() => {
    const saved = localStorage.getItem("cart");
    if (saved) setCart(JSON.parse(saved));
  }, []);

  /* =========================
     SAVE CART TO STORAGE
  ========================= */
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  /* =========================
     FETCH ALL CROPS
  ========================= */
  useEffect(() => {
    fetch("http://localhost:9090/backend/api/crop/all")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setCrops(data);
        } else {
          setCrops([]);
        }
      })
      .catch((err) => {
        console.error("Failed to load crops:", err);
        setCrops([]);
      })
      .finally(() => setLoading(false));
  }, []);

  /* =========================
     IMAGE URL ‚Äì FINAL FIX ‚úÖ
  ========================= */
  const getImage = (imgStr) => {
    if (!imgStr || imgStr.trim() === "") {
      return process.env.PUBLIC_URL + "/Images/noimg.png";
    }

    // Handle trailing commas safely
    const list = imgStr
      .split(",")
      .map((i) => i.trim())
      .filter((i) => i.length > 0);

    if (list.length === 0) {
      return process.env.PUBLIC_URL + "/Images/noimg.png";
    }

    // DB stores: uploads/filename.jpg
    // Servlet maps: /uploads/*
    const fileName = list[0].replace("uploads/", "");

    return `http://localhost:9090/backend/uploads/${fileName}`;
  };

  /* =========================
     ADD TO CART
  ========================= */
  const addToCart = (crop) => {
    const available =
      Number(crop.availableStock ?? crop.quantity - crop.soldQty) || 0;

    const exists = cart.find((c) => c.cropId === crop.cropId);

    if (exists) {
      if (exists.qty < available) {
        setCart(
          cart.map((c) =>
            c.cropId === crop.cropId ? { ...c, qty: c.qty + 1 } : c
          )
        );
      } else {
        alert("Not enough stock available");
        return;
      }
    } else {
      const newItem = {
        cropId: crop.cropId,
        farmerId: crop.farmerId,
        cropName: crop.cropName,
        price: Number(crop.price),
        images: crop.images,
        quantity: Number(crop.quantity),
        soldQty: Number(crop.soldQty),
        availableStock: available,
        qty: 1,
      };

      setCart([...cart, newItem]);
    }

    alert(`${crop.cropName} added to cart!`);
  };

  /* =========================
     FILTER CROPS
  ========================= */
  const filtered = crops.filter((c) =>
    c.cropName.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <>
      {/* ‚≠ê NAVBAR */}
      <Navbar />

      {/* ‚≠ê MAIN CONTENT */}
      <div className="buy-container" style={{ marginTop: "80px" }}>
        <h2 className="buy-title">
          <i className="fa fa-handshake"></i> Buy Fresh Produce
        </h2>

        {/* üîç SEARCH */}
        <div className="buy-search-box">
          <i className="fa fa-search"></i>
          <input
            type="text"
            placeholder="Search crops... (Tomato, Wheat, etc)"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>

        {loading && <p className="loading">Loading crops...</p>}

        {!loading && filtered.length === 0 && (
          <p className="no-data">No crops found.</p>
        )}

        {/* üåæ CROP CARDS */}
        <div className="buy-grid">
          {filtered.map((item) => (
            <div className="buy-card" key={item.cropId}>
              <img
                src={getImage(item.images)}
                alt={item.cropName}
                className="buy-img"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src =
                    process.env.PUBLIC_URL + "/Images/noimg.png";
                }}
              />

              <h3 className="buy-name">{item.cropName}</h3>

              <p className="buy-price">
                <i className="fa fa-rupee-sign"></i> {item.price} / Kg
              </p>

              <p className="buy-stock">
                <b>Available:</b>{" "}
                {Number(
                  item.availableStock ?? item.quantity - item.soldQty
                )}{" "}
                Kg
              </p>

              <button
                className="buy-btn"
                onClick={() => addToCart(item)}
              >
                <i className="fa fa-shopping-cart"></i> Add to Cart
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* ‚≠ê FOOTER */}
      <Footer />
    </>
  );
}

export default Buy;
