import React, { useEffect, useState } from "react";
import "../Css/Dashboard.css";

function Dashboard() {
  const [user, setUser] = useState(null);
  const [crops, setCrops] = useState([]);
  const [editingCrop, setEditingCrop] = useState(null);
  const [cartCount, setCartCount] = useState(0);

  useEffect(() => {
    // Load logged-in user
    const stored = JSON.parse(localStorage.getItem("userData"));
    if (stored) {
      setUser(stored);

      fetch(`http://localhost:9090/backend/api/crop/farmer?farmerId=${stored.farmerId}`)
        .then((res) => res.json())
        .then((data) => setCrops(data))
        .catch(() => console.error("Failed to load crops"));
    }

    // Load Cart Count
    const cart = JSON.parse(localStorage.getItem("cart")) || [];
    setCartCount(cart.length);
  }, []);

  // BUILD IMAGE URL
  const getFirstImage = (images) => {
    if (!images) return "/Images/noimg.png";
    const first = images.split(",")[0].trim();
    return `http://localhost:9090/backend/uploads/${first}`;
  };

  // DELETE CROP
  const deleteCrop = (cropId, images) => {
    if (!window.confirm("Are you sure you want to delete this crop?")) return;

    fetch("http://localhost:9090/backend/api/crop/delete", {
      method: "POST",
      headers: { "Content-Type": "application/x-www-form-urlencoded" },
      body: `cropId=${cropId}&images=${encodeURIComponent(images)}`,
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "SUCCESS") {
          alert("Crop deleted successfully!");
          setCrops((prev) => prev.filter((c) => c.cropId !== cropId));
        }
      })
      .catch(() => alert("Delete failed"));
  };

  // SAVE EDIT
  const saveEdit = () => {
    const formData = new FormData();
    formData.append("cropId", editingCrop.cropId);
    formData.append("cropName", editingCrop.cropName);
    formData.append("category", editingCrop.category);
    formData.append("quantity", editingCrop.quantity);
    formData.append("price", editingCrop.price);
    formData.append("description", editingCrop.description);

    fetch("http://localhost:9090/backend/api/crop/update", {
      method: "POST",
      body: formData,
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "SUCCESS") {
          alert("Crop updated successfully!");
          setCrops((prev) =>
            prev.map((c) => (c.cropId === editingCrop.cropId ? editingCrop : c))
          );
          setEditingCrop(null);
        }
      })
      .catch(() => alert("Update failed"));
  };

  return (
    <div className="dashboard-wrapper">

      {/* LEFT SIDEBAR */}
      <aside className="sidebar">
        <h2 className="logo">AgroLink</h2>

        <ul className="menu">
          <li className="active">
            <i className="fa-solid fa-gauge"></i> Dashboard
          </li>

          <li>
            <i className="fa-solid fa-leaf"></i> My Crops
          </li>

          {/* ‚≠ê NEW CART MENU ITEM */}
          <li onClick={() => (window.location.href = "/cart")}>
            <i className="fa-solid fa-cart-shopping"></i> Cart

            {/* CART COUNT BADGE */}
            <span className="cart-badge">{cartCount}</span>
          </li>

          <li>
            <i className="fa-solid fa-chart-line"></i> Analytics
          </li>
          <li>
            <i className="fa-solid fa-gear"></i> Settings
          </li>
          <li>
            <i className="fa-solid fa-headset"></i> Support
          </li>
        </ul>
      </aside>

      {/* MAIN CONTENT */}
      <main className="dashboard-main">

        {/* HEADER */}
        <header className="top-header">
          <div>
            <h2>Welcome, {user?.fullName} üëã</h2>
            <p>Your farming insights and performance summary</p>
          </div>

          <div className="profile-box">
            <i className="fa-solid fa-user"></i>
          </div>
        </header>

        {/* STATS */}
        <section className="stats-container">
          <div className="stat-card">
            <i className="fa-solid fa-wheat-awn"></i>
            <h3>{crops.length}</h3>
            <p>Total Crops Listed</p>
          </div>

          <div className="stat-card">
            <i className="fa-solid fa-wallet"></i>
            <h3>‚Çπ0</h3>
            <p>Total Earnings (Coming Soon)</p>
          </div>

          <div className="stat-card">
            <i className="fa-solid fa-chart-line"></i>
            <h3>Growing</h3>
            <p>Market Trends</p>
          </div>
        </section>

        {/* ---------------- GRAPH SECTION ---------------- */}
        <section className="graph-section">
          <h3>Market Activity Overview</h3>

          <div className="graph-container">
            {crops.length > 0 ? (() => {
              const maxQty = Math.max(...crops.map(c => Number(c.quantity) || 0));
              const step = Math.ceil(maxQty / 5);
              const yValues = [];
              for (let i = 5; i >= 0; i--) yValues.push(i * step);

              return (
                <div className="graph-wrapper">

                  {/* Y Axis */}
                  <div className="y-axis">
                    {yValues.map((v, i) => (
                      <span key={i}>{v}</span>
                    ))}
                  </div>

                  {/* Graph Bars */}
                  <div className="graph-area">
                    {crops.map((c, index) => {
                      const height = (Number(c.quantity) / (step * 5)) * 200;

                      return (
                        <div key={index} className="graph-col">
                          <div
                            className="bar"
                            style={{ height: `${height}px` }}
                          ></div>
                          <span className="x-label">{c.cropName}</span>
                        </div>
                      );
                    })}
                  </div>
                </div>
              );
            })() : (
              <p className="no-graph">No data available</p>
            )}

            {/* Highest List */}
            <div className="highest-list">
              <h4>Highest Available Crops</h4>

              {crops
                .slice()
                .sort((a, b) => Number(b.quantity) - Number(a.quantity))
                .map((c, index) => (
                  <div className="list-item" key={index}>
                    <span>{index + 1}. {c.cropName}</span>
                    <strong>{c.quantity} Kg</strong>
                  </div>
                ))}
            </div>
          </div>
        </section>

        {/* -------- CROPS TABLE -------- */}
        <section className="crop-table-section">
          <h3>Your Crop Listings</h3>

          <table className="crop-table">
            <thead>
              <tr>
                <th>Image</th>
                <th>Crop ID</th>
                <th>Crop</th>
                <th>Category</th>
                <th>Qty</th>
                <th>Price</th>
                <th>Actions</th>
              </tr>
            </thead>

            <tbody>
              {crops.length === 0 ? (
                <tr>
                  <td colSpan="7" className="no-data">No crops posted yet.</td>
                </tr>
              ) : (
                crops.map((c) => (
                  <tr key={c.cropId}>
                    <td>
                      <img src={getFirstImage(c.images)} className="crop-img" alt="crop" />
                    </td>

                    <td className="crop-id">{c.cropId}</td>

                    <td>{c.cropName}</td>
                    <td>{c.category}</td>
                    <td>{c.quantity} kg</td>
                    <td>‚Çπ{c.price}</td>

                    <td className="action-btns">
                      <button className="edit-btn" onClick={() => setEditingCrop(c)}>
                        <i className="fa-solid fa-pen-to-square"></i>
                      </button>

                      <button className="delete-btn" onClick={() => deleteCrop(c.cropId, c.images)}>
                        <i className="fa-solid fa-trash"></i>
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </section>

      </main>

      {/* EDIT POPUP */}
      {editingCrop && (
        <div className="edit-popup">
          <div className="edit-box">
            <h2>Edit Crop</h2>

            <input
              type="text"
              value={editingCrop.cropName}
              onChange={(e) => setEditingCrop({ ...editingCrop, cropName: e.target.value })}
            />

            <input
              type="text"
              value={editingCrop.category}
              onChange={(e) => setEditingCrop({ ...editingCrop, category: e.target.value })}
            />

            <input
              type="number"
              value={editingCrop.quantity}
              onChange={(e) => setEditingCrop({ ...editingCrop, quantity: e.target.value })}
            />

            <input
              type="number"
              value={editingCrop.price}
              onChange={(e) => setEditingCrop({ ...editingCrop, price: e.target.value })}
            />

            <textarea
              value={editingCrop.description}
              onChange={(e) => setEditingCrop({ ...editingCrop, description: e.target.value })}
            ></textarea>

            <div className="edit-actions">
              <button className="save-btn" onClick={saveEdit}>Save</button>
              <button className="cancel-btn" onClick={() => setEditingCrop(null)}>Cancel</button>
            </div>
          </div>
        </div>
      )}

    </div>
  );
}

export default Dashboard;
