import React, { useState } from "react";
import "../Css/ChatWidget.css";

function ChatWidget() {
  const [open, setOpen] = useState(false);
  const [messages, setMessages] = useState([
    { from: "bot", text: "ðŸ‘‹ Hello! Welcome to AgroLink Support." },
    { from: "bot", text: "Here are common questions you can ask:" },
    { from: "bot", text: "â€¢ How do I upload crops?\nâ€¢ How to track my order?\nâ€¢ Payment problems?\nâ€¢ Contact support team" }
  ]);
  const [input, setInput] = useState("");
  const [typing, setTyping] = useState(false);

  // Simulated server-side reply
  const sendToServer = (msg) => {
    setTyping(true);

    setTimeout(() => {
      setTyping(false);
      const reply = `You asked: "${msg}". Our support team will respond soon.`;

      setMessages(prev => [...prev, { from: "bot", text: reply }]);
    }, 900);
  };

  const sendMessage = () => {
    if (!input.trim()) return;

    const userMsg = input;
    setMessages(prev => [...prev, { from: "user", text: userMsg }]);
    setInput("");

    sendToServer(userMsg);
  };

  return (
    <>
      {/* Floating Button */}
      <div className="chat-float-btn" onClick={() => setOpen(!open)}>
        <i className="fa-solid fa-comment-dots"></i>
      </div>

      {/* Chat Window */}
      {open && (
        <div className="chat-window">

          <div className="chat-header">
            <span>Support Chat</span>
            <i className="fa-solid fa-xmark" onClick={() => setOpen(false)}></i>
          </div>

          <div className="chat-body">
            {messages.map((msg, i) => (
              <div key={i} className={`chat-msg ${msg.from}`}>
                <p>{msg.text}</p>
              </div>
            ))}

            {typing && (
              <div className="chat-msg bot typing">
                <p>Typing...</p>
              </div>
            )}
          </div>

          <div className="chat-input">
            <input
              placeholder="Type your message..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && sendMessage()}
            />
            <button onClick={sendMessage}>
              <i className="fa-solid fa-paper-plane"></i>
            </button>
          </div>

        </div>
      )}
    </>
  );
}

export default ChatWidget;
