import React from "react";
import "../Css/Marketplace.css";
import { useNavigate, useLocation } from "react-router-dom";

function Marketplace() {
  const navigate = useNavigate();
  const location = useLocation();

  // Extract active tab from current URL
  const activeTab = location.pathname.split("/")[2] || "";

  const goTo = (path) => {
    navigate(path);
  };

  return (
    <div className="marketplace-container">

      {/* INTERNAL NAVBAR */}
      <div className="marketplace-nav">

        <button
          className={`mp-tab ${activeTab === "sell" ? "active" : ""}`}
          onClick={() => goTo("/marketplace/sell")}
        >
          <i className="fa fa-store"></i> Sell
        </button>

        <button
          className={`mp-tab ${activeTab === "buy" ? "active" : ""}`}
          onClick={() => goTo("/marketplace/buy")}
        >
          <i className="fa fa-handshake"></i> Buy
        </button>

        <button
          className={`mp-tab ${activeTab === "seeds" ? "active" : ""}`}
          onClick={() => goTo("/marketplace/seeds")}
        >
          <i className="fa fa-seedling"></i> Seeds
        </button>

        <button
          className={`mp-tab ${activeTab === "pesticides" ? "active" : ""}`}
          onClick={() => goTo("/marketplace/pesticides")}
        >
          <i className="fa fa-vial"></i> Pesticides
        </button>

        <button
          className={`mp-tab ${activeTab === "fertilizers" ? "active" : ""}`}
          onClick={() => goTo("/marketplace/fertilizers")}
        >
          <i className="fa fa-leaf"></i> Fertilizers
        </button>

      </div>

    </div>
  );
}

export default Marketplace;
