import React, { useEffect, useState } from "react";
import "../Css/Dashboard.css"; 
import "../Css/Cart.css";

function Cart() {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);

  // Load cart + refresh
  useEffect(() => {
    const refreshCart = () => {
      const saved = JSON.parse(localStorage.getItem("cart")) || [];
      setCart(saved);
    };

    refreshCart();
    window.addEventListener("focus", refreshCart);
    return () => window.removeEventListener("focus", refreshCart);
  }, []);

  // Load user
  useEffect(() => {
    const savedUser = JSON.parse(localStorage.getItem("userData"));
    if (savedUser) setUser(savedUser);
  }, []);

  // Save changes
  const updateCart = (newCart) => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
  };

  // ‚≠ê UPDATE BUY KG
  const updateKg = (cropId, enteredQty, availableQty) => {
    let qty = Number(enteredQty);

    if (qty < 1) qty = 1;
    if (qty > availableQty) qty = availableQty; // ‚ùó cannot exceed stock

    const updated = cart.map((item) =>
      item.cropId === cropId ? { ...item, qty } : item
    );

    updateCart(updated);
  };

  // Remove item
  const removeItem = (id) => {
    const updated = cart.filter((c) => c.cropId !== id);
    updateCart(updated);
  };

  // Total amount
  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  // Image builder
  const getImage = (imgStr) => {
    if (!imgStr) return "/Images/noimg.png";
    const first = imgStr.split(",")[0].trim();
    return `http://localhost:9090/backend/uploads/${first}`;
  };

  return (
    <div className="dashboard-wrapper">

      {/* SIDEBAR */}
      <aside className="sidebar">
        <h2 className="logo">AgroLink</h2>

        <ul className="menu">
          <li onClick={() => (window.location.href = "/dashboard")}>
            <i className="fa-solid fa-gauge"></i> Dashboard
          </li>

          <li className="active">
            <i className="fa-solid fa-cart-shopping"></i> Cart
            <span className="cart-badge">{cart.length}</span>
          </li>
        </ul>
      </aside>

      {/* MAIN */}
      <main className="dashboard-main">

        {/* HEADER */}
        <header className="top-header">
          <h2>Hello, {user?.fullName} üëã</h2>
          <p>Your shopping cart summary</p>
        </header>

        {/* CART SECTION */}
        <section className="cart-container">

          <h2 className="cart-title">üõí Your Cart</h2>

          {cart.length === 0 ? (
            <p className="empty-cart">Your cart is empty.</p>
          ) : (
            <div className="cart-list">
              {cart.map((item) => {
                const available = item.quantity || item.availableQty || 0;

                return (
                  <div className="cart-card" key={item.cropId}>
                    
                    <img
                      src={getImage(item.images)}
                      alt={item.cropName}
                      className="cart-img"
                    />

                    <div className="cart-details">
                      <h3>{item.cropName}</h3>

                      <p><b>Farmer ID:</b> {item.farmerId}</p>
                      <p><b>Price:</b> ‚Çπ{item.price} / kg</p>

                      {/* ‚≠ê AVAILABLE STOCK DISPLAY */}
                      <p className="available-stock">
                        <b>Available Stock:</b> {available} Kg
                      </p>

                      {/* ‚≠ê ENTER BUY KG INPUT */}
                      <div className="qty-input-box">
                        <label><b>Buy Quantity (Kg):</b></label>
                        <input
                          type="number"
                          min="1"
                          max={available}
                          value={item.qty}
                          onChange={(e) =>
                            updateKg(item.cropId, e.target.value, available)
                          }
                        />
                      </div>

                      <p className="item-total">
                        Subtotal: ‚Çπ{item.price * item.qty}
                      </p>
                    </div>

                    <button className="remove-btn" onClick={() => removeItem(item.cropId)}>
                      Remove
                    </button>
                  </div>
                );
              })}
            </div>
          )}

          {/* TOTAL BOX */}
          {cart.length > 0 && (
            <div className="cart-summary">
              <h3>Total Amount: ‚Çπ{total}</h3>
              <button className="checkout-btn">Proceed to Checkout</button>
            </div>
          )}

        </section>
      </main>
    </div>
  );
}

export default Cart;
