import React, { useEffect, useState } from "react";
import "../Css/Profile.css";

function Profile() {
  const mobile = localStorage.getItem("userMobile");

  const [user, setUser] = useState({
    fullName: "",
    email: "",
    address: "",
    state: "",
    district: "",
    pincode: "",
    farmSize: "",
    mainCrops: "",
    bankAccount: "",
    ifsc: "",
    gst: "",
    pan: "",
    aadhar: "",
    profilePhoto: "",
    verified: 0,
    role: "Farmer", 
  });

  const [photoFile, setPhotoFile] = useState(null);
  const [doc1, setDoc1] = useState(null);
  const [doc2, setDoc2] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:9090/backend/api/user/get-profile?mobile=${mobile}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setUser((prev) => ({ ...prev, ...data.user }));
        }
      });
  }, [mobile]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleSave = async () => {
    const formData = new FormData();

    // Append all user data
    Object.keys(user).forEach((key) => {
      formData.append(key, user[key] ?? "");
    });

    formData.append("mobile", mobile);

    // Append file data
    if (photoFile) formData.append("profilePhoto", photoFile);
    if (doc1) formData.append("document1", doc1);
    if (doc2) formData.append("document2", doc2);

    const res = await fetch(
      "http://localhost:9090/backend/api/user/update-profile",
      {
        method: "POST",
        body: formData,
      }
    );

    const data = await res.json();
    alert(data.message);
  };

  const logout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  const getProfilePhotoUrl = () => {
    if (photoFile) return URL.createObjectURL(photoFile);
    if (user.profilePhoto) return "http://localhost:9090/backend/" + user.profilePhoto;
    return "/default-avatar.png";
  };

  return (
    <div className="profile-page-background">
      <div className="profile-card">
        <header className="profile-header">
          <h2>User Profile Setup</h2>
          <button className="logout-btn" onClick={logout}>
            Logout
          </button>
        </header>
        
        <div className="profile-form-layout">
          
          {/* LEFT COLUMN: Personal Details */}
          <div className="profile-column column-left">
            <h3 className="section-title">Personal & Contact Details</h3>

            <div className="photo-upload-container">
              <img
                src={getProfilePhotoUrl()}
                alt="profile"
                className="profile-photo"
              />
              <div className="file-input-wrapper">
                <input 
                  type="file" 
                  id="photo-file"
                  onChange={(e) => setPhotoFile(e.target.files[0])} 
                />
                <label htmlFor="photo-file" className="custom-file-label">
                  Choose File
                </label>
              </div>
            </div>

            <input
              type="text"
              name="fullName"
              placeholder="Full Name"
              value={user.fullName}
              onChange={handleChange}
              className="form-input"
            />

            <input
              type="email"
              name="email"
              placeholder="Email"
              value={user.email}
              onChange={handleChange}
              className="form-input"
            />

            <textarea
              name="address"
              placeholder="Address"
              value={user.address}
              onChange={handleChange}
              className="form-input textarea"
            />

            <div className="input-group-row">
              <input
                type="text"
                name="state"
                placeholder="State"
                value={user.state}
                onChange={handleChange}
                className="form-input"
              />
              <input
                type="text"
                name="district"
                placeholder="District"
                value={user.district}
                onChange={handleChange}
                className="form-input"
              />
            </div>

            <div className="input-group-row">
              <input
                type="text"
                name="pincode"
                placeholder="Pincode"
                value={user.pincode}
                onChange={handleChange}
                className="form-input"
              />
              <input
                type="text"
                name="role"
                placeholder="Role (e.g. Farmer)"
                value={user.role}
                onChange={handleChange}
                className="form-input"
              />
            </div>
            
            <div className="aadhar-section">
                <input
                    type="text"
                    name="aadhar"
                    placeholder="Aadhar Number"
                    value={user.aadhar}
                    onChange={handleChange}
                    className="form-input"
                />
                 <span className="verify-status">
                    {user.verified === 1 ? (
                        <span className="verified">✔ Verified</span>
                    ) : (
                        <span className="pending">⏳ Pending</span>
                    )}
                </span>
            </div>

          </div>

          {/* RIGHT COLUMN: Business & Bank Details */}
          <div className="profile-column column-right">
            <h3 className="section-title">Agricultural & Business Information</h3>

            <div className="input-group-row">
              <input
                type="text"
                name="farmSize"
                placeholder="Farm Size (Acres)"
                value={user.farmSize}
                onChange={handleChange}
                className="form-input"
              />
              <input
                type="text"
                name="mainCrops"
                placeholder="Main Crops"
                value={user.mainCrops}
                onChange={handleChange}
                className="form-input"
              />
            </div>

            <div className="input-group-row">
              <input
                type="text"
                name="gst"
                placeholder="GST (Optional)"
                value={user.gst}
                onChange={handleChange}
                className="form-input"
              />
              <input
                type="text"
                name="pan"
                placeholder="PAN Number"
                value={user.pan}
                onChange={handleChange}
                className="form-input"
              />
            </div>
            

            <h3 className="section-title">Bank Account Information</h3>
            <input
              type="text"
              name="bankAccount"
              placeholder="Bank Account Number"
              value={user.bankAccount}
              onChange={handleChange}
              className="form-input"
            />

            <input
              type="text"
              name="ifsc"
              placeholder="IFSC Code"
              value={user.ifsc}
              onChange={handleChange}
              className="form-input"
            />

            <h3 className="section-title">Upload Documents</h3>
            <div className="file-upload-block">
                <label className="file-upload-label">Document 1 (Land Proof):</label>
                <input type="file" onChange={(e) => setDoc1(e.target.files[0])} />
            </div>
            
            <div className="file-upload-block">
                <label className="file-upload-label">Document 2 (Other Proof):</label>
                <input type="file" onChange={(e) => setDoc2(e.target.files[0])} />
            </div>

          </div>
        </div>

        <button className="save-profile-btn" onClick={handleSave}>
          Save Profile
        </button>

      </div>
    </div>
  );
}

export default Profile;
