import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../Css/Cart.css";
import Sidebar from "../components/Sidebar";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

function Cart() {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);
  const [discountCode, setDiscountCode] = useState("");
  const [discountPct, setDiscountPct] = useState(0);
  const navigate = useNavigate();

  // Load cart when page loads or regains focus
  useEffect(() => {
    const refreshCart = () => {
      const saved = JSON.parse(localStorage.getItem("cart")) || [];
      setCart(saved);
    };

    refreshCart();
    window.addEventListener("focus", refreshCart);

    return () => window.removeEventListener("focus", refreshCart);
  }, []);

  // Load logged-in user's data
  useEffect(() => {
    const savedUser = JSON.parse(localStorage.getItem("userData"));
    if (savedUser) setUser(savedUser);
  }, []);

  // Save cart to localStorage
  const updateCart = (newCart) => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
    window.dispatchEvent(new Event("cartUpdated"));
  };

  const clearCart = () => {
    updateCart([]);
  };

  // Update quantity with limits
  const updateKg = (cropId, enteredQty, availableQty) => {
    let qty = Number(enteredQty);

    if (qty < 1) qty = 1;
    if (qty > availableQty) qty = availableQty;

    const updated = cart.map((item) =>
      item.cropId === cropId ? { ...item, qty } : item
    );

    updateCart(updated);
  };

  // Remove item from cart
  const removeItem = (id) => {
    const updated = cart.filter((c) => c.cropId !== id);
    updateCart(updated);
  };

  // Totals
  const subtotal = cart.reduce((sum, item) => sum + item.price * item.qty, 0);
  const discountAmount = Math.round(subtotal * discountPct);
  const taxable = Math.max(subtotal - discountAmount, 0);
  const tax = Math.round(taxable * 0.06);
  const total = taxable + tax;

  const fmt = (n) => new Intl.NumberFormat("en-IN").format(n);

  const applyDiscount = () => {
    const code = discountCode.trim().toUpperCase();
    if (code === "SAVE10") {
      setDiscountPct(0.1);
      return;
    }
    setDiscountPct(0);
  };

  // â­ UPDATED IMAGE HANDLER â€” Supports Crops + Fertilizers
  const getImage = (imgStr) => {
    if (!imgStr) return "/Images/noimg.png";

    // Fertilizer images come as full URL or base64 â€” return directly
    if (imgStr.startsWith("http") || imgStr.startsWith("data:image")) {
      return imgStr;
    }

    // Crop images stored in Tomcat
    const first = imgStr.split(",")[0].trim();
    return `http://localhost:9090/backend/uploads/${first}`;
  };

  return (
    <div className="dashboard-wrapper">
      {/* Sidebar */}
      <Sidebar cartCount={cart.length} />

      {/* Right side */}
      <div className="dashboard-content">
        <Navbar />

        <main className="dashboard-main">
          <div className="cart-header">
            <h2>Hello, {user?.fullName} ðŸ‘‹</h2>
            <p>Your shopping cart summary</p>
          </div>

          <div className="cart-layout">
            {/* LEFT â€” ITEMS */}
            <div className="cart-items">
              <h2 className="cart-title">Your Cart</h2>

              {cart.length === 0 ? (
                <p className="empty-cart">
                  Your cart is empty.{" "}
                  <Link className="empty-cart-cta" to="/marketplace">
                    Explore Marketplace
                  </Link>
                </p>
              ) : (
                <div className="cart-list">
                  {cart.map((item) => {
                    // Prefer availableStock â†’ quantity â†’ default 0
                    const available =
                      Number(
                        item.availableStock ??
                          item.availableQty ??
                          item.quantity
                      ) || 0;

                    return (
                      <div className="cart-card-pro" key={item.cropId}>
                        {/* IMAGE */}
                        <img
                          src={getImage(item.images)}
                          alt={item.cropName}
                          className="cart-img-pro"
                        />

                        {/* DETAILS */}
                        <div className="cart-details-pro">
                          <h3>{item.cropName}</h3>

                          {/* Farmer ID only for crops */}
                          {item.farmerId && (
                            <p>
                              <b>Farmer ID:</b> {item.farmerId}
                            </p>
                          )}

                          <p>
                            <b>Price:</b> â‚¹{item.price} / kg
                          </p>

                          <p className="available-stock">
                            Available Stock: <b>{available} Kg</b>
                          </p>

                          <p className="stock-status">
                            {available > 5 ? (
                              <span className="in-stock">âœ” In Stock</span>
                            ) : available > 0 ? (
                              <span className="limited-stock">
                                âš  Limited Stock
                              </span>
                            ) : (
                              <span className="out-stock">âœ– Out of Stock</span>
                            )}
                          </p>

                          {/* QUANTITY SELECTOR */}
                          <div className="qty-wrapper">
                            <label>
                              <b>Buy Quantity (Kg)</b>
                            </label>
                            <div className="qty-box">
                              <button
                                onClick={() =>
                                  updateKg(
                                    item.cropId,
                                    item.qty - 1,
                                    available
                                  )
                                }
                                disabled={item.qty <= 1}
                              >
                                -
                              </button>

                              <input
                                type="number"
                                min="1"
                                max={available}
                                value={item.qty}
                                onChange={(e) =>
                                  updateKg(
                                    item.cropId,
                                    e.target.value,
                                    available
                                  )
                                }
                              />

                              <button
                                onClick={() =>
                                  updateKg(
                                    item.cropId,
                                    item.qty + 1,
                                    available
                                  )
                                }
                                disabled={item.qty >= available}
                              >
                                +
                              </button>
                            </div>
                          </div>

                          <p className="item-total">
                            Item Total: <b>â‚¹{fmt(item.price * item.qty)}</b>
                          </p>
                        </div>

                        {/* REMOVE BUTTON */}
                        <button
                          className="remove-btn-pro"
                          onClick={() => removeItem(item.cropId)}
                        >
                          Remove
                        </button>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>

            {/* RIGHT â€” SUMMARY */}
            <div className="cart-summary-card">
              <h2>Order Summary</h2>

              <div className="summary-row">
                <span>Subtotal</span>
                <b>â‚¹{fmt(subtotal)}</b>
              </div>

              {discountPct > 0 && (
                <div className="summary-row">
                  <span>
                    Discount ({Math.round(discountPct * 100)}%)
                  </span>
                  <b>-â‚¹{fmt(discountAmount)}</b>
                </div>
              )}

              <div className="summary-row">
                <span>Shipping</span>
                <b>â‚¹0</b>
              </div>

              <div className="summary-row">
                <span>Taxes (6%)</span>
                <b>â‚¹{fmt(tax)}</b>
              </div>

              <hr />

              <div className="summary-row total">
                <span>Grand Total</span>
                <b>â‚¹{fmt(total)}</b>
              </div>

              <button
                className="checkout-btn-pro"
                disabled={cart.length === 0}
                onClick={() => navigate("/checkout")}
              >
                Proceed to Checkout
              </button>

              <div className="coupon-row">
                <input
                  className="discount-input"
                  placeholder="Enter Discount Code (e.g., SAVE10)"
                  value={discountCode}
                  onChange={(e) => setDiscountCode(e.target.value)}
                />
                <button
                  className="apply-coupon-btn"
                  onClick={applyDiscount}
                >
                  Apply
                </button>
              </div>

              <button
                className="clear-cart-btn"
                onClick={clearCart}
                disabled={cart.length === 0}
              >
                Clear Cart
              </button>
            </div>
          </div>
        </main>

        <Footer />
      </div>
    </div>
  );
}

export default Cart;
