import React from "react";
import "../Css/Fertilizers.css";
import Navbar from "../components/Navbar";   // ⭐ Added
import Footer from "../components/Footer";   // ⭐ Added

function Fertilizers() {
  const fertList = [
    // ⭐ Nitrogen Fertilizers
    {
      id: 1,
      name: "Urea Fertilizer",
      price: 650,
      img: "https://th.bing.com/th/id/OIP.GwyJwAPjA9D9L4PffTv2NgHaI7?w=170&h=205&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3"
    },
    {
      id: 2,
      name: "Ammonium Sulphate",
      price: 720,
      img: "https://tse4.mm.bing.net/th/id/OIP.uaaIslxwYGO7sCPRmewrWAAAAA?pid=ImgDet&w=184&h=265&c=7&dpr=1.3&o=7&rm=3"
    },
    {
      id: 3,
      name: "Calcium Ammonium Nitrate (CAN)",
      price: 800,
      img: "data:image/webp;base64,"
    },

    // ⭐ NPK Fertilizers
    {
      id: 4,
      name: "NPK 20-20-20",
      price: 900,
      img: "https://images.unsplash.com/photo-1584306670952-38a99e0a1e4d?auto=format&fit=crop&w=600&q=80"
    },
    {
      id: 5,
      name: "NPK 10-26-26",
      price: 1100,
      img: "https://images.unsplash.com/photo-1584306670959-7955c1f79c0c?auto=format&fit=crop&w=600&q=80"
    },
    {
      id: 6,
      name: "NPK 12-32-16",
      price: 1050,
      img: "https://images.unsplash.com/photo-1584306670960-3a81e0e5ee3d?auto=format&fit=crop&w=600&q=80"
    },

    // ⭐ Phosphorus Fertilizers
    {
      id: 7,
      name: "Single Super Phosphate (SSP)",
      price: 450,
      img: "https://images.unsplash.com/photo-1559757148-5c350d0d3c56?auto=format&fit=crop&w=600&q=80"
    },
    {
      id: 8,
      name: "DAP Fertilizer (18-46-0)",
      price: 1350,
      img: "https://images.unsplash.com/photo-1559757175-0eb30cd52e17?auto=format&fit=crop&w=600&q=80"
    },

    // ⭐ Potassium Fertilizers
    {
      id: 9,
      name: "Potash Fertilizer (MOP)",
      price: 750,
      img: "https://images.unsplash.com/photo-1584306670950-79d2c214d8e0?auto=format&fit=crop&w=600&q=80"
    },
    {
      id: 10,
      name: "SOP – Sulphate of Potash",
      price: 880,
      img: "https://images.unsplash.com/photo-1584306670962-8b9bf137c551?auto=format&fit=crop&w=600&q=80"
    },

    // ⭐ Organic Fertilizers
    {
      id: 11,
      name: "Organic Compost",
      price: 300,
      img: "https://images.unsplash.com/photo-1589924691995-400dc9eccb9b?auto=format&fit=crop&w=600&q=80"
    },
    {
      id: 12,
      name: "Vermicompost",
      price: 450,
      img: "https://images.unsplash.com/photo-1590411209323-1ca1d2e2eb06?auto=format&fit=crop&w=600&q=80"
    },
    {
      id: 13,
      name: "Seaweed Extract Fertilizer",
      price: 550,
      img: "https://images.unsplash.com/photo-1501004318641-b39e6451bec6?auto=format&fit=crop&w=600&q=80"
    },
    {
      id: 14,
      name: "Humic Acid Granules",
      price: 480,
      img: "https://images.unsplash.com/photo-1590411209314-89424b8c0f0f?auto=format&fit=crop&w=600&q=80"
    }
  ];

  return (
    <>
      {/* ⭐ NAVBAR */}
      <Navbar />

      <div className="fert-container" style={{ marginTop: "80px" }}>
        <h2 className="fert-title">
          <i className="fa fa-leaf"></i> Fertilizers & Nutrition
        </h2>

        <div className="fert-grid">
          {fertList.map((item) => (
            <div className="fert-card" key={item.id}>
              <img src={item.img} alt={item.name} className="fert-img" />

              <h3 className="fert-name">{item.name}</h3>

              <p className="fert-price">
                <i className="fa fa-rupee-sign"></i> {item.price}
              </p>

              <button className="fert-btn">
                <i className="fa fa-shopping-cart"></i> Buy Fertilizer
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* ⭐ FOOTER */}
      <Footer />
    </>
  );
}

export default Fertilizers;
