// src/pages/Profile.js
import React, { useEffect, useState } from "react";
import "../Css/Profile.css";

function Profile() {
  const mobile = localStorage.getItem("userMobile");

  const [loading, setLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  const [user, setUser] = useState({
    fullName: "",
    email: "",
    address: "",
    state: "",
    district: "",
    pincode: "",
    farmSize: "",
    mainCrops: "",
    bankAccount: "",
    ifsc: "",
    gst: "",
    pan: "",
    aadhar: "",
    profilePhoto: "",
    document1: "",
    document2: "",
    verified: 0,
    role: "Farmer",
  });

  const [photoFile, setPhotoFile] = useState(null);
  const [doc1, setDoc1] = useState(null);
  const [doc2, setDoc2] = useState(null);

  // -------------------------------------------------
  // LOAD PROFILE
  // -------------------------------------------------
  useEffect(() => {
    if (!mobile) return;

    setLoading(true);
    fetch(`http://localhost:9090/backend/api/user/get-profile?mobile=${mobile}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setUser((prev) => ({ ...prev, ...data.user }));
        }
      })
      .catch((e) => console.error("Fetch failed", e))
      .finally(() => setLoading(false));
  }, [mobile]);

  // -------------------------------------------------
  // HANDLE TEXT CHANGE
  // -------------------------------------------------
  const handleChange = (e) => {
    setUser((p) => ({ ...p, [e.target.name]: e.target.value }));
  };

  // -------------------------------------------------
  // SAVE PROFILE
  // -------------------------------------------------
  const handleSave = async () => {
    const fd = new FormData();

    Object.keys(user).forEach((k) => {
      if (["profilePhoto", "document1", "document2"].includes(k)) return;
      fd.append(k, user[k] ?? "");
    });

    fd.append("mobile", mobile);

    if (photoFile) fd.append("profilePhoto", photoFile);
    if (doc1) fd.append("document1", doc1);
    if (doc2) fd.append("document2", doc2);

    setLoading(true);

    const res = await fetch("http://localhost:9090/backend/api/user/update-profile", {
      method: "POST",
      body: fd,
    });

    const data = await res.json();
    setLoading(false);

    if (data.status === "success") {
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 2500);
    } else {
      alert("Update failed: " + data.message);
    }
  };

  // -------------------------------------------------
  // LOGOUT
  // -------------------------------------------------
  const logout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  const profileImg = photoFile
    ? URL.createObjectURL(photoFile)
    : user.profilePhoto
    ? "http://localhost:9090/backend/" + user.profilePhoto
    : "/default-avatar.png";

  // -------------------------------------------------
  // UI RENDER
  // -------------------------------------------------
  return (
    <div className="profile-page-background">

      {showSuccess && (
        <div className="success-popup">
          <div className="success-box">
            <div className="success-icon">✔</div>
            <h3>Profile Updated</h3>
            <p>Your profile has been saved successfully.</p>
          </div>
        </div>
      )}

      <div className="profile-card">
        <div className="profile-header">
          <h2>User Profile Setup</h2>
          <button className="logout-btn" onClick={logout}>Logout</button>
        </div>

        {loading && <p>Loading...</p>}

        <div className="profile-form-layout">

          {/* LEFT SIDE */}
          <div>
            <h3 className="section-title">Personal Details</h3>

            <div className="photo-upload-container">
              <img src={profileImg} className="profile-photo" />

              <label className="custom-file-label">
                Choose Photo
                <input type="file" accept="image/*"
                  onChange={(e) => setPhotoFile(e.target.files[0])}
                />
              </label>
            </div>

            <select className="form-input" name="role" value={user.role} onChange={handleChange}>
              <option value="Farmer">Farmer</option>
              <option value="Buyer">Buyer</option>
            </select>

            <input className="form-input" name="fullName" placeholder="Full Name"
              value={user.fullName} onChange={handleChange} />

            <input className="form-input" name="email" placeholder="Email"
              value={user.email} onChange={handleChange} />

            <textarea className="form-input textarea" name="address"
              placeholder="Address" value={user.address} onChange={handleChange} />

            <div className="input-group-row">
              <input className="form-input" name="state" placeholder="State"
                value={user.state} onChange={handleChange} />
              <input className="form-input" name="district" placeholder="District"
                value={user.district} onChange={handleChange} />
            </div>

            <input className="form-input" name="pincode" placeholder="Pincode"
              value={user.pincode} onChange={handleChange} />

            <div className="aadhar-section">
              <input className="form-input" name="aadhar" placeholder="Aadhar Number"
                value={user.aadhar} onChange={handleChange} />

              {user.verified === 1 ? (
                <span className="verified">✔ Verified</span>
              ) : (
                <span className="pending">⏳ Pending</span>
              )}
            </div>
          </div>

          {/* RIGHT SIDE */}
          <div>
            <h3 className="section-title">Farm & Business Details</h3>

            <div className="input-group-row">
              <input className="form-input" name="farmSize" placeholder="Farm Size"
                value={user.farmSize} onChange={handleChange} />

              <input className="form-input" name="mainCrops" placeholder="Main Crops"
                value={user.mainCrops} onChange={handleChange} />
            </div>

            <div className="input-group-row">
              <input className="form-input" name="gst" placeholder="GST"
                value={user.gst} onChange={handleChange} />

              <input className="form-input" name="pan" placeholder="PAN"
                value={user.pan} onChange={handleChange} />
            </div>

            <h3 className="section-title">Bank Info</h3>
            <input className="form-input" name="bankAccount" placeholder="Bank Account"
              value={user.bankAccount} onChange={handleChange} />

            <input className="form-input" name="ifsc" placeholder="IFSC"
              value={user.ifsc} onChange={handleChange} />

            {/* DOCUMENT UPLOAD */}
            <h3 className="section-title">Upload Documents</h3>

            {/* DOCUMENT 1 */}
            <div className="doc-upload-container">
              <label className="doc-upload-label">Land Proof:</label>
              <label className="circle-upload">
                <img src="/Images/cloud-upload.png" className="upload-icon" />
                <input type="file" name="document1"
                  onChange={(e) => setDoc1(e.target.files[0])} />
              </label>

              <span className="uploaded-file-name">
                {doc1 ? doc1.name : user.document1?.split("/").pop()}
              </span>
            </div>

            {/* DOCUMENT 2 */}
            <div className="doc-upload-container">
              <label className="doc-upload-label">Other Document:</label>
              <label className="circle-upload">
                <img src="/Images/cloud-upload.png" className="upload-icon" />
                <input type="file" name="document2"
                  onChange={(e) => setDoc2(e.target.files[0])} />
              </label>

              <span className="uploaded-file-name">
                {doc2 ? doc2.name : user.document2?.split("/").pop()}
              </span>
            </div>

          </div>
        </div>

        <button className="save-profile-btn" onClick={handleSave} disabled={loading}>
          {loading ? "Saving..." : "Save Profile"}
        </button>
      </div>
    </div>
  );
}

export default Profile;
