import React, { useState, useEffect } from "react";
import "../Css/Buy.css";

function Buy() {
  const [search, setSearch] = useState("");
  const [crops, setCrops] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);

  // Load existing cart
  useEffect(() => {
    const saved = localStorage.getItem("cart");
    if (saved) setCart(JSON.parse(saved));
  }, []);

  // Save cart
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  // Fetch crops
  useEffect(() => {
    fetch("http://localhost:9090/backend/api/crop/all")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setCrops(data);
      })
      .catch((err) => console.error("Failed to load crops:", err))
      .finally(() => setLoading(false));
  }, []);

  // Make image URL
  const getImage = (imgStr) => {
    if (!imgStr) return "/Images/noimg.png";
    const first = imgStr.split(",")[0].trim();
    return `http://localhost:9090/backend/uploads/${first}`;
  };

  // ⭐ Toast Message
  const showToast = (msg) => {
    const toast = document.createElement("div");
    toast.className = "toast-message";
    toast.innerText = msg;

    document.body.appendChild(toast);

    setTimeout(() => toast.classList.add("show"), 50);

    setTimeout(() => {
      toast.classList.remove("show");
      setTimeout(() => toast.remove(), 300);
    }, 2000);
  };

  // ⭐ Add to Cart
  const addToCart = (item) => {
    const exists = cart.find((c) => c.cropId === item.cropId);

    let updatedCart;
    if (exists) {
      updatedCart = cart.map((c) =>
        c.cropId === item.cropId ? { ...c, qty: c.qty + 1 } : c
      );
    } else {
      updatedCart = [
        ...cart,
        {
          cropId: item.cropId,
          farmerId: item.farmerId,
          cropName: item.cropName,
          price: item.price,
          images: item.images,
          qty: 1,
        },
      ];
    }

    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));

    // ⭐ Update other pages instantly
    window.dispatchEvent(new Event("cartUpdated"));

    // ⭐ Show toast
    showToast(`${item.cropName} added to cart`);
  };

  // Search filter
  const filtered = crops.filter((c) =>
    c.cropName.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="buy-container">
      <h2 className="buy-title">
        <i className="fa fa-handshake"></i> Buy Fresh Produce
      </h2>

      <div className="buy-search-box">
        <i className="fa fa-search"></i>
        <input
          type="text"
          placeholder="Search crops..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      {loading && <p>Loading crops...</p>}

      <div className="buy-grid">
        {filtered.map((item) => (
          <div className="buy-card" key={item.cropId}>
            <img
              src={getImage(item.images)}
              className="buy-img"
              alt={item.cropName}
            />

            <h3 className="buy-name">{item.cropName}</h3>

            <p className="buy-price">₹{item.price} / Kg</p>

            <button className="buy-btn" onClick={() => addToCart(item)}>
              <i className="fa fa-shopping-cart"></i> Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Buy;
