import React, { useEffect, useState } from "react";
import "../Css/Profile.css";

function Profile() {
  const mobile = localStorage.getItem("userMobile"); // saved at login

  const [user, setUser] = useState({
    fullName: "",
    email: "",
    address: "",
    state: "",
    district: "",
    pincode: "",
    role: "farmer",
    farmSize: "",
    mainCrops: "",
    bankAccount: "",
    ifsc: "",
    gst: "",
    pan: "",
    aadhar: "",
    profilePhoto: "",
    verified: 0,
  });

  const [photoFile, setPhotoFile] = useState(null);
  const [doc1, setDoc1] = useState(null);
  const [doc2, setDoc2] = useState(null);

  // ⭐ FIXED useEffect with dependency
  useEffect(() => {
    fetch(
      `http://localhost:9090/backend/api/user/get-profile?mobile=${mobile}`
    )
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setUser((prev) => ({ ...prev, ...data.user }));
        }
      });
  }, [mobile]);

  // SAVE PROFILE
  const handleSave = async () => {
    const formData = new FormData();

    Object.keys(user).forEach((key) => {
      formData.append(key, user[key] ?? "");
    });

    formData.append("mobile", mobile);

    if (photoFile) formData.append("profilePhoto", photoFile);
    if (doc1) formData.append("document1", doc1);
    if (doc2) formData.append("document2", doc2);

    const res = await fetch(
      "http://localhost:9090/backend/api/user/update-profile",
      {
        method: "POST",
        body: formData,
      }
    );

    const data = await res.json();
    alert(data.message);
  };

  const logout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  return (
    <div className="profile-page">
      <div className="profile-card">

        {/* PROFILE PHOTO */}
        <div className="profile-photo-section">
          <img
            src={
              photoFile
                ? URL.createObjectURL(photoFile)
                : user.profilePhoto
                ? "http://localhost:9090/backend/" + user.profilePhoto
                : "/default-avatar.png"
            }
            alt="profile"
            className="profile-img"
          />

          <input
            type="file"
            onChange={(e) => setPhotoFile(e.target.files[0])}
          />
        </div>

        <h2>Edit Profile</h2>

        {/* NAME */}
        <input
          type="text"
          value={user.fullName}
          placeholder="Full Name"
          onChange={(e) => setUser({ ...user, fullName: e.target.value })}
        />

        {/* EMAIL */}
        <input
          type="email"
          value={user.email}
          placeholder="Email Address"
          onChange={(e) => setUser({ ...user, email: e.target.value })}
        />

        {/* ADDRESS */}
        <textarea
          value={user.address}
          placeholder="Address"
          onChange={(e) => setUser({ ...user, address: e.target.value })}
        />

        {/* STATE & DISTRICT */}
        <div className="row">
          <input
            type="text"
            value={user.state}
            placeholder="State"
            onChange={(e) => setUser({ ...user, state: e.target.value })}
          />
          <input
            type="text"
            value={user.district}
            placeholder="District"
            onChange={(e) => setUser({ ...user, district: e.target.value })}
          />
        </div>

        {/* PINCODE */}
        <input
          type="text"
          value={user.pincode}
          placeholder="Pincode"
          onChange={(e) => setUser({ ...user, pincode: e.target.value })}
        />

        {/* ROLE */}
        <select
          value={user.role}
          onChange={(e) => setUser({ ...user, role: e.target.value })}
        >
          <option value="farmer">Farmer</option>
          <option value="buyer">Buyer</option>
        </select>

        {/* FARMER FIELDS */}
        {user.role === "farmer" && (
          <>
            <input
              type="text"
              value={user.farmSize}
              placeholder="Farm Size (in acres)"
              onChange={(e) =>
                setUser({ ...user, farmSize: e.target.value })
              }
            />

            <input
              type="text"
              value={user.mainCrops}
              placeholder="Crops (e.g. Rice, Tomato)"
              onChange={(e) =>
                setUser({ ...user, mainCrops: e.target.value })
              }
            />

            <input
              type="text"
              value={user.bankAccount}
              placeholder="Bank Account Number"
              onChange={(e) =>
                setUser({ ...user, bankAccount: e.target.value })
              }
            />

            <input
              type="text"
              value={user.ifsc}
              placeholder="IFSC Code"
              onChange={(e) => setUser({ ...user, ifsc: e.target.value })}
            />

            <input
              type="text"
              value={user.aadhar}
              placeholder="Aadhar Number"
              onChange={(e) => setUser({ ...user, aadhar: e.target.value })}
            />
          </>
        )}

        {/* BUYER FIELDS */}
        {user.role === "buyer" && (
          <>
            <input
              type="text"
              value={user.gst}
              placeholder="GST Number"
              onChange={(e) => setUser({ ...user, gst: e.target.value })}
            />

            <input
              type="text"
              value={user.pan}
              placeholder="PAN Number"
              onChange={(e) => setUser({ ...user, pan: e.target.value })}
            />
          </>
        )}

        {/* DOCUMENTS */}
        <label>Upload Document 1</label>
        <input
          type="file"
          onChange={(e) => setDoc1(e.target.files[0])}
        />

        <label>Upload Document 2 (Optional)</label>
        <input
          type="file"
          onChange={(e) => setDoc2(e.target.files[0])}
        />

        {/* VERIFIED STATUS */}
        <p className="verify-status">
          Status:
          {user.verified === 1 ? (
            <span className="verified"> ✔ Verified</span>
          ) : (
            <span className="pending"> ⏳ Pending</span>
          )}
        </p>

        {/* SAVE BUTTON */}
        <button className="save-btn" onClick={handleSave}>
          Save Profile
        </button>

        {/* LOGOUT */}
        <button className="logout-btn" onClick={logout}>
          Logout
        </button>
      </div>
    </div>
  );
}

export default Profile;
