import React, { useEffect, useState, useRef } from "react";
import "../Css/Navbar.css";
import { Link, useNavigate, useLocation } from "react-router-dom";

function Navbar() {
  const [userName, setUserName] = useState(null);
  const [showMenu, setShowMenu] = useState(false);
  const navigate = useNavigate();
  const location = useLocation(); // ⭐ IMPORTANT
  const dropdownRef = useRef(null);

  /* ==============================
     LOAD USER FROM SESSION
     (RUNS ON EVERY ROUTE CHANGE)
  ============================== */
  useEffect(() => {
    const loadProfile = async () => {
      try {
        const res = await fetch(
          "http://localhost:9090/backend/api/user/get-profile",
          { credentials: "include" }
        );

        const data = await res.json();
        console.log("NAVBAR PROFILE RESPONSE:", data);

        if (data?.fullName) {
          setUserName(data.fullName);
        } else {
          setUserName(null);
        }
      } catch (err) {
        setUserName(null);
      }
    };

    loadProfile();
  }, [location.pathname]); // ⭐ KEY FIX

  /* ==============================
     CLOSE DROPDOWN ON OUTSIDE CLICK
  ============================== */
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowMenu(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () =>
      document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  /* ==============================
     LOGOUT
  ============================== */
  const handleLogout = async () => {
    try {
      await fetch(
        "http://localhost:9090/backend/api/auth/logout",
        {
          method: "POST",
          credentials: "include",
        }
      );
    } catch {}
    setUserName(null);
    navigate("/login");
  };

  return (
    <nav className="nav">
      {/* LOGO */}
      <div
        className="nav-left"
        onClick={() => navigate("/home")}
        style={{ cursor: "pointer" }}
      >
        <img src="/Images/Logo1.png" alt="AgroLink Logo" className="logo" />
      </div>

      {/* MENU */}
      <ul className="nav-menu">
        <li onClick={() => navigate("/home")}>Home</li>
        <li onClick={() => navigate("/marketplace")}>Marketplace</li>
        <li onClick={() => navigate("/marketplace/seeds")}>Inputs</li>
        <li onClick={() => navigate("/loans")}>Loans</li>
        <li onClick={() => navigate("/insurance")}>Insurance</li>
        <li onClick={() => navigate("/impact")}>Impact</li>
        <li onClick={() => navigate("/contact")}>Contact</li>
      </ul>

      {/* USER PROFILE / LOGIN */}
      {userName ? (
        <div className="profile-dropdown-container" ref={dropdownRef}>
          <div
            className="profile-mini"
            onClick={() => setShowMenu(!showMenu)}
          >
            <div className="profile-circle">
              {userName.charAt(0)}
            </div>
            <span className="profile-text">{userName}</span>
          </div>

          {showMenu && (
            <div className="profile-dropdown">
              <p onClick={() => navigate("/profile")}>My Profile</p>
              <p onClick={() => navigate("/dashboard")}>Dashboard</p>
              <p className="logout" onClick={handleLogout}>
                Logout
              </p>
            </div>
          )}
        </div>
      ) : (
        <div className="nav-btns">
          <Link to="/signup">
            <button className="signup">Sign Up</button>
          </Link>
          <Link to="/login">
            <button className="login">Log In</button>
          </Link>
        </div>
      )}
    </nav>
  );
}

export default Navbar;
