import React, { useEffect, useState } from "react";
import "../Css/Dashboard.css";
import "../Css/Cart.css";

function Cart() {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);

  // Load + auto-refresh cart
  useEffect(() => {
    const loadCart = () => {
      const saved = JSON.parse(localStorage.getItem("cart")) || [];
      setCart(saved);
    };

    loadCart();
    window.addEventListener("focus", loadCart);
    window.addEventListener("cartUpdated", loadCart);

    return () => {
      window.removeEventListener("focus", loadCart);
      window.removeEventListener("cartUpdated", loadCart);
    };
  }, []);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("userData"));
    if (stored) setUser(stored);
  }, []);

  const updateCart = (newCart) => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
    window.dispatchEvent(new Event("cartUpdated"));
  };

  // KG input update
  const updateKg = (id, value, available) => {
    let qty = Number(value);

    if (qty < 1) qty = 1;
    if (qty > available) qty = available; // ❗ cannot buy more than available stock

    const updated = cart.map((c) =>
      c.cropId === id ? { ...c, qty } : c
    );

    updateCart(updated);
  };

  // remove item
  const removeItem = (id) => {
    const updated = cart.filter((c) => c.cropId !== id);
    updateCart(updated);
  };

  const total = cart.reduce((sum, c) => sum + c.price * c.qty, 0);

  const getImage = (imgStr) => {
    if (!imgStr) return "/Images/noimg.png";
    return `http://localhost:9090/backend/uploads/${imgStr.split(",")[0]}`;
  };

  return (
    <div className="dashboard-wrapper">

      {/* SIDEBAR */}
      <aside className="sidebar">
        <h2 className="logo">AgroLink</h2>

        <ul className="menu">
          <li onClick={() => (window.location.href = "/dashboard")}>
            <i className="fa-solid fa-gauge"></i> Dashboard
          </li>

          <li className="active">
            <i className="fa-solid fa-cart-shopping"></i> Cart
            <span className="cart-badge">{cart.length}</span>
          </li>
        </ul>
      </aside>

      {/* MAIN */}
      <main className="dashboard-main">
        <header className="top-header">
          <h2>Your Cart</h2>
        </header>

        <section className="cart-container">
          {cart.length === 0 ? (
            <p className="empty-cart">Your cart is empty.</p>
          ) : (
            cart.map((item) => (
              <div className="cart-card" key={item.cropId}>

                <img src={getImage(item.images)} className="cart-img" alt="" />

                <div className="cart-details">
                  <h3>{item.cropName}</h3>
                  <p>Farmer ID: {item.farmerId}</p>

                  {/* ⭐ AVAILABLE STOCK DISPLAY */}
                  <p className="available">
                    <b>Available:</b> {item.availableQty || item.quantity || 0} Kg
                  </p>

                  <p>Price: ₹{item.price} / kg</p>

                  {/* ⭐ USER INPUT FOR BUY KG */}
                  <div className="qty-input-box">
                    <label>Enter KG to Buy:</label>
                    <input
                      type="number"
                      min="1"
                      max={item.availableQty || item.quantity || 0}
                      value={item.qty}
                      onChange={(e) =>
                        updateKg(
                          item.cropId,
                          e.target.value,
                          item.availableQty || item.quantity || 0
                        )
                      }
                    />
                  </div>

                  <p className="item-total">
                    Subtotal: ₹{item.price * item.qty}
                  </p>
                </div>

                <button className="remove-btn" onClick={() => removeItem(item.cropId)}>
                  Remove
                </button>

              </div>
            ))
          )}

          <div className="cart-summary">
            <h3>Total: ₹{total}</h3>
            <button className="checkout-btn">Proceed to Checkout</button>
          </div>

        </section>
      </main>
    </div>
  );
}

export default Cart;
