// src/pages/Profile.js
import React, { useEffect, useState } from "react";
import "../Css/Profile.css";

function Profile() {
  const mobile = localStorage.getItem("userMobile");
  const [loading, setLoading] = useState(false);
  const [user, setUser] = useState({
    fullName: "",
    email: "",
    address: "",
    state: "",
    district: "",
    pincode: "",
    farmSize: "",
    mainCrops: "",
    bankAccount: "",
    ifsc: "",
    gst: "",
    pan: "",
    aadhar: "",
    profilePhoto: "",
    document1: "",
    document2: "",
    verified: 0,
    role: "Farmer",
  });

  const [photoFile, setPhotoFile] = useState(null);
  const [doc1, setDoc1] = useState(null);
  const [doc2, setDoc2] = useState(null);

  useEffect(() => {
    if (!mobile) return;
    setLoading(true);
    fetch(`http://localhost:9090/backend/api/user/get-profile?mobile=${mobile}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success" && data.user) {
          setUser((prev) => ({ ...prev, ...data.user }));
        } else {
          console.warn("Get profile:", data);
          // leave user as default if not found
        }
      })
      .catch((err) => {
        console.error("Fetch profile failed", err);
      })
      .finally(() => setLoading(false));
  }, [mobile]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((p) => ({ ...p, [name]: value }));
  };

  const handleSave = async () => {
    if (!mobile) {
      alert("Mobile not found in localStorage. Please login again.");
      return;
    }

    const fd = new FormData();

    // append textual fields
    Object.keys(user).forEach((k) => {
      // don't append the server file paths (profilePhoto/document1/document2)
      if (["profilePhoto", "document1", "document2"].includes(k)) return;
      fd.append(k, user[k] ?? "");
    });

    fd.append("mobile", mobile);

    // append files with the exact parameter names expected by servlet
    if (photoFile) fd.append("profilePhoto", photoFile, photoFile.name);
    if (doc1) fd.append("document1", doc1, doc1.name);
    if (doc2) fd.append("document2", doc2, doc2.name);

    try {
      setLoading(true);
      const res = await fetch(
        "http://localhost:9090/backend/api/user/update-profile",
        {
          method: "POST",
          body: fd,
        }
      );

      const data = await res.json();
      if (data.status === "success") {
        alert(data.message || "Profile updated");
        // refresh profile from server to get updated file paths
        const ref = await fetch(
          `http://localhost:9090/backend/api/user/get-profile?mobile=${mobile}`
        );
        const rd = await ref.json();
        if (rd.status === "success") setUser((p) => ({ ...p, ...rd.user }));
      } else {
        alert(data.message || "Update failed");
        console.warn("UpdateProfile response", data);
      }
    } catch (err) {
      console.error("Update failed", err);
      alert("Update failed: " + (err.message || err));
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    localStorage.removeItem("userName");
    localStorage.removeItem("userMobile");
    window.location.href = "/login";
  };

  const profileImg = photoFile
    ? URL.createObjectURL(photoFile)
    : user.profilePhoto
    ? "http://localhost:9090/backend/" + user.profilePhoto
    : "/default-avatar.png";

  return (
    <div className="profile-page-background">
      <div className="profile-card">
        <div className="profile-header">
          <h2>User Profile Setup</h2>
          <button className="logout-btn" onClick={logout}>
            Logout
          </button>
        </div>

        {loading && <p style={{ marginBottom: 12 }}>Loading…</p>}

        <div className="profile-form-layout">
          {/* LEFT */}
          <div>
            <h3 className="section-title">Personal Details</h3>

            <div className="photo-upload-container">
              <img src={profileImg} className="profile-photo" alt="profile" />

              <label className="custom-file-label" style={{ marginLeft: 8 }}>
                Choose Photo
                <input
                  name="profilePhoto"
                  type="file"
                  accept="image/*"
                  onChange={(e) => setPhotoFile(e.target.files[0])}
                />
              </label>
            </div>

            <select
              name="role"
              value={user.role}
              onChange={handleChange}
              className="form-input"
            >
              <option value="Farmer">Farmer</option>
              <option value="Buyer">Buyer</option>
            </select>

            <input
              className="form-input"
              name="fullName"
              placeholder="Full Name"
              value={user.fullName}
              onChange={handleChange}
            />

            <input
              className="form-input"
              name="email"
              placeholder="Email"
              value={user.email}
              onChange={handleChange}
            />

            <textarea
              className="form-input textarea"
              name="address"
              placeholder="Address"
              value={user.address}
              onChange={handleChange}
            />

            <div className="input-group-row">
              <input
                className="form-input"
                name="state"
                placeholder="State"
                value={user.state}
                onChange={handleChange}
              />
              <input
                className="form-input"
                name="district"
                placeholder="District"
                value={user.district}
                onChange={handleChange}
              />
            </div>

            <input
              className="form-input"
              name="pincode"
              placeholder="Pincode"
              value={user.pincode}
              onChange={handleChange}
            />

            <div className="aadhar-section">
              <input
                className="form-input"
                name="aadhar"
                placeholder="Aadhar Number"
                value={user.aadhar}
                onChange={handleChange}
              />
              <span className="verify-status">
                {user.verified === 1 ? (
                  <span className="verified">✔ Verified</span>
                ) : (
                  <span className="pending">⏳ Pending</span>
                )}
              </span>
            </div>
          </div>

          {/* RIGHT */}
          <div>
            <h3 className="section-title">Farm & Business Details</h3>

            <div className="input-group-row">
              <input
                className="form-input"
                name="farmSize"
                placeholder="Farm Size (Acres)"
                value={user.farmSize}
                onChange={handleChange}
              />
              <input
                className="form-input"
                name="mainCrops"
                placeholder="Main Crops"
                value={user.mainCrops}
                onChange={handleChange}
              />
            </div>

            <div className="input-group-row">
              <input
                className="form-input"
                name="gst"
                placeholder="GST (optional)"
                value={user.gst}
                onChange={handleChange}
              />
              <input
                className="form-input"
                name="pan"
                placeholder="PAN Number"
                value={user.pan}
                onChange={handleChange}
              />
            </div>

            <h3 className="section-title">Bank Information</h3>
            <input
              className="form-input"
              name="bankAccount"
              placeholder="Bank Account Number"
              value={user.bankAccount}
              onChange={handleChange}
            />
            <input
              className="form-input"
              name="ifsc"
              placeholder="IFSC Code"
              value={user.ifsc}
              onChange={handleChange}
            />

            <h3 className="section-title">Upload Documents</h3>


			{/* ---- DOCUMENT 1 ---- */}
			<div className="doc-upload-container">
			  <label className="doc-upload-label">Land proof (Doc):</label>

			  <label className="circle-upload" title="Upload Land Proof">
			    <img 
			      src="/Images/upload-icon.png" 
			      alt="upload"
			      className="upload-icon"
			    />
			    <input
			      name="document1"
			      type="file"
			      className="hidden-file-input"
			      onChange={(e) => setDoc1(e.target.files[0])}
			    />
			  </label>

			  <span className="uploaded-file-name">
			    {doc1 ? doc1.name : user.document1 ? user.document1.split("/").pop() : ""}
			  </span>
			</div>


			{/* ---- DOCUMENT 1 ---- */}
			<div className="doc-upload-container">
			  <label className="doc-upload-label">Other Document:</label>

			  <label className="circle-upload" title="Upload Land Proof">
			    <img 
			      src="/Images/upload-icon.png" 
			      alt="upload"
			      className="upload-icon"
			    />
			    <input
			      name="document1"
			      type="file"
			      className="hidden-file-input"
			      onChange={(e) => setDoc1(e.target.files[0])}
			    />
			  </label>

			  <span className="uploaded-file-name">
			    {doc1 ? doc1.name : user.document1 ? user.document1.split("/").pop() : ""}
			  </span>
			</div>
          </div>
        </div>

        <button className="save-profile-btn" onClick={handleSave} disabled={loading}>
          {loading ? "Saving..." : "Save Profile"}
        </button>
      </div>
    </div>
  );
}

export default Profile;
