import React, { useEffect, useState } from "react";
import "../Css/Profile.css";

function Profile() {
  const mobile = localStorage.getItem("userMobile");

  const [user, setUser] = useState({
    fullName: "",
    email: "",
    address: "",
    state: "",
    district: "",
    pincode: "",
    farmSize: "",
    mainCrops: "",
    bankAccount: "",
    ifsc: "",
    profilePhoto: "",
    verified: 0,
  });

  const [photoFile, setPhotoFile] = useState(null);
  const [doc1, setDoc1] = useState(null);
  const [doc2, setDoc2] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:9090/backend/api/user/get-profile?mobile=${mobile}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setUser((prev) => ({ ...prev, ...data.user }));
        }
      });
  }, [mobile]);

  const handleSave = async () => {
    const formData = new FormData();
    Object.keys(user).forEach((key) => formData.append(key, user[key] ?? ""));
    formData.append("mobile", mobile);

    if (photoFile) formData.append("profilePhoto", photoFile);
    if (doc1) formData.append("document1", doc1);
    if (doc2) formData.append("document2", doc2);

    const res = await fetch(
      "http://localhost:9090/backend/api/user/update-profile",
      { method: "POST", body: formData }
    );

    const data = await res.json();
    alert(data.message);
  };

  const logout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  return (
    <div className="profile-container">

      {/* LEFT PANEL - Personal Details */}
      <div className="profile-section">

        {/* Photo */}
        <div className="photo-upload">
          <img
            src={
              photoFile
                ? URL.createObjectURL(photoFile)
                : user.profilePhoto
                ? "http://localhost:9090/backend/" + user.profilePhoto
                : "/default-avatar.png"
            }
            alt="profile"
            className="profile-pic"
          />
          <input type="file" onChange={(e) => setPhotoFile(e.target.files[0])} />
        </div>

        <h3>Personal Details</h3>

        <input
          type="text"
          placeholder="Full Name"
          value={user.fullName}
          onChange={(e) => setUser({ ...user, fullName: e.target.value })}
        />

        <input
          type="email"
          placeholder="Email"
          value={user.email}
          onChange={(e) => setUser({ ...user, email: e.target.value })}
        />

        <textarea
          placeholder="Address"
          value={user.address}
          onChange={(e) => setUser({ ...user, address: e.target.value })}
        />

        <input
          type="text"
          placeholder="State"
          value={user.state}
          onChange={(e) => setUser({ ...user, state: e.target.value })}
        />

        <input
          type="text"
          placeholder="District"
          value={user.district}
          onChange={(e) => setUser({ ...user, district: e.target.value })}
        />

        <input
          type="text"
          placeholder="Pincode"
          value={user.pincode}
          onChange={(e) => setUser({ ...user, pincode: e.target.value })}
        />

        <button className="logout-btn" onClick={logout}>Logout</button>
      </div>

      {/* RIGHT PANEL - Farm & Bank Details */}
      <div className="profile-section">
        <h3>Farm & Bank Details</h3>

        <input
          type="text"
          placeholder="Farm Size (Acres)"
          value={user.farmSize}
          onChange={(e) => setUser({ ...user, farmSize: e.target.value })}
        />

        <input
          type="text"
          placeholder="Main Crops"
          value={user.mainCrops}
          onChange={(e) => setUser({ ...user, mainCrops: e.target.value })}
        />

        <input
          type="text"
          placeholder="Bank Account Number"
          value={user.bankAccount}
          onChange={(e) => setUser({ ...user, bankAccount: e.target.value })}
        />

        <input
          type="text"
          placeholder="IFSC Code"
          value={user.ifsc}
          onChange={(e) => setUser({ ...user, ifsc: e.target.value })}
        />

        <label>Upload Document 1</label>
        <input type="file" onChange={(e) => setDoc1(e.target.files[0])} />

        <label>Upload Document 2</label>
        <input type="file" onChange={(e) => setDoc2(e.target.files[0])} />

        <button className="save-btn" onClick={handleSave}>Save Profile</button>
      </div>

    </div>
  );
}

export default Profile;
