import React, { useEffect, useState } from "react";
import "../Css/Profile.css";

function Profile() {
  const mobile = localStorage.getItem("userMobile");

  const [user, setUser] = useState({
    fullName: "",
    email: "",
    address: "",
    state: "",
    district: "",
    pincode: "",
    role: "farmer",
    farmSize: "",
    mainCrops: "",
    bankAccount: "",
    ifsc: "",
    gst: "",
    pan: "",
    aadhar: "",
    profilePhoto: "",
    verified: 0,
  });

  const [photoFile, setPhotoFile] = useState(null);
  const [doc1, setDoc1] = useState(null);
  const [doc2, setDoc2] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:9090/backend/api/user/get-profile?mobile=${mobile}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setUser((prev) => ({ ...prev, ...data.user }));
        }
      });
  }, [mobile]);

  const handleSave = async () => {
    const formData = new FormData();

    Object.keys(user).forEach((key) => formData.append(key, user[key] ?? ""));
    formData.append("mobile", mobile);

    if (photoFile) formData.append("profilePhoto", photoFile);
    if (doc1) formData.append("document1", doc1);
    if (doc2) formData.append("document2", doc2);

    const res = await fetch(
      "http://localhost:9090/backend/api/user/update-profile",
      { method: "POST", body: formData }
    );

    const data = await res.json();
    alert(data.message);
  };

  const logout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  return (
    <div className="profile-container">

      <div className="profile-card">

        {/* LEFT SIDE SECTION */}
        <div className="left-section">

          {/* Profile Photo */}
          <div className="photo-box">
            <img
              src={
                photoFile
                  ? URL.createObjectURL(photoFile)
                  : user.profilePhoto
                  ? "http://localhost:9090/backend/" + user.profilePhoto
                  : "/default-avatar.png"
              }
              className="profile-photo"
              alt="profile"
            />
            <input type="file" onChange={(e) => setPhotoFile(e.target.files[0])} />
          </div>

          <h3 className="section-title">Personal Details</h3>

          <div className="form-group">
            <label>Full Name</label>
            <input
              type="text"
              value={user.fullName}
              onChange={(e) => setUser({ ...user, fullName: e.target.value })}
            />
          </div>

          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={user.email}
              onChange={(e) => setUser({ ...user, email: e.target.value })}
            />
          </div>

          <div className="form-group">
            <label>Address</label>
            <textarea
              value={user.address}
              onChange={(e) => setUser({ ...user, address: e.target.value })}
            />
          </div>

          <div className="row">
            <div className="form-group">
              <label>State</label>
              <input
                type="text"
                value={user.state}
                onChange={(e) => setUser({ ...user, state: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label>District</label>
              <input
                type="text"
                value={user.district}
                onChange={(e) => setUser({ ...user, district: e.target.value })}
              />
            </div>
          </div>

          <div className="form-group">
            <label>Pincode</label>
            <input
              type="text"
              value={user.pincode}
              onChange={(e) => setUser({ ...user, pincode: e.target.value })}
            />
          </div>

          <button className="logout-btn" onClick={logout}>Logout</button>
        </div>

        {/* RIGHT SIDE SECTION */}
        <div className="right-section">
          <h3 className="section-title">Farm & Bank Details</h3>

          <div className="form-group">
            <label>Role</label>
            <select
              value={user.role}
              onChange={(e) => setUser({ ...user, role: e.target.value })}
            >
              <option value="farmer">Farmer</option>
              <option value="buyer">Buyer</option>
            </select>
          </div>

          {user.role === "farmer" && (
            <>
              <div className="form-group">
                <label>Farm Size (in acres)</label>
                <input
                  type="text"
                  value={user.farmSize}
                  onChange={(e) => setUser({ ...user, farmSize: e.target.value })}
                />
              </div>

              <div className="form-group">
                <label>Main Crops</label>
                <input
                  type="text"
                  value={user.mainCrops}
                  onChange={(e) => setUser({ ...user, mainCrops: e.target.value })}
                />
              </div>
            </>
          )}

          <div className="form-group">
            <label>Bank Account</label>
            <input
              type="text"
              value={user.bankAccount}
              onChange={(e) => setUser({ ...user, bankAccount: e.target.value })}
            />
          </div>

          <div className="form-group">
            <label>IFSC Code</label>
            <input
              type="text"
              value={user.ifsc}
              onChange={(e) => setUser({ ...user, ifsc: e.target.value })}
            />
          </div>

          {user.role === "buyer" && (
            <>
              <div className="form-group">
                <label>GST Number</label>
                <input
                  type="text"
                  value={user.gst}
                  onChange={(e) => setUser({ ...user, gst: e.target.value })}
                />
              </div>

              <div className="form-group">
                <label>PAN Number</label>
                <input
                  type="text"
                  value={user.pan}
                  onChange={(e) => setUser({ ...user, pan: e.target.value })}
                />
              </div>
            </>
          )}

          <label className="doc-label">Upload Document 1</label>
          <input type="file" onChange={(e) => setDoc1(e.target.files[0])} />

          <label className="doc-label">Upload Document 2</label>
          <input type="file" onChange={(e) => setDoc2(e.target.files[0])} />

          <p className="verify-status">
            Status:
            {user.verified === 1 ? (
              <span className="verified"> ✔ Verified</span>
            ) : (
              <span className="pending"> ⏳ Pending</span>
            )}
          </p>

          <button className="save-btn" onClick={handleSave}>
            Save Profile
          </button>
        </div>
      </div>
    </div>
  );
}

export default Profile;
