import React, { useState } from "react";
import "../Css/LoanPage.css";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

function LoanPage() {
  // ===============================
  // EMI calculator
  // ===============================
  const [loanAmount, setLoanAmount] = useState(50000);
  const [interest, setInterest] = useState(7);
  const [tenure, setTenure] = useState(12);

  const emi =
    (loanAmount * interest * 0.01 * Math.pow(1 + interest * 0.01, tenure)) /
    (Math.pow(1 + interest * 0.01, tenure) - 1);

  // ===============================
  // MULTI-STEP state
  // ===============================
  const [step, setStep] = useState(1);
  const [showSuccess, setShowSuccess] = useState(false);

  const [formData, setFormData] = useState({
    fullName: "",
    dob: "",
    aadhaar: "",
    address: "",
    state: "",
    pinCode: "",
    phone: "",
    email: "",
    loanAmountNeeded: "",
    loanPurpose: "",
    cropActivity: "",
    landArea: "",
    totalLandActivity: "",
    idProof: null,
    addressProof: null,
    landProof: null,
    declaration: false,
  });

  // ===============================
  // Handlers
  // ===============================

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((p) => ({
      ...p,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setFormData((p) => ({
      ...p,
      [name]: files && files[0] ? files[0] : null,
    }));
  };

  const handleSendOtp = () => {
    alert("OTP sent (simulated).");
  };

  // ===============================
  // Step Validation
  // ===============================
  const validateStep = () => {
    if (step === 1) {
      const { fullName, dob, aadhaar, address, state, pinCode, phone } =
        formData;
      if (!fullName || !dob || !aadhaar || !address || !state || !pinCode || !phone) {
        alert("Please fill all personal details.");
        return false;
      }
    }

    if (step === 2) {
      const {
        loanAmountNeeded,
        loanPurpose,
        cropActivity,
        landArea,
        totalLandActivity,
      } = formData;

      if (
        !loanAmountNeeded ||
        !loanPurpose ||
        !cropActivity ||
        !landArea ||
        !totalLandActivity
      ) {
        alert("Please fill all agricultural details.");
        return false;
      }
    }

    if (step === 3) {
      const { idProof, addressProof, landProof, declaration } = formData;

      if (!idProof || !addressProof || !landProof) {
        alert("Upload all required documents.");
        return false;
      }

      if (!declaration) {
        alert("Please accept Terms & Conditions.");
        return false;
      }
    }

    return true;
  };

  // ===============================
  // Step Navigation
  // ===============================
  const nextStep = () => {
    if (validateStep()) setStep((s) => Math.min(3, s + 1));
  };

  const prevStep = () => setStep((s) => Math.max(1, s - 1));

  // ===============================
  // BACKEND INTEGRATION (SERVLET)
  // ===============================
  const handleSubmitApplication = async () => {
    if (!validateStep()) return;

    try {
      const data = new FormData();

      // TEXT FIELDS
      Object.keys(formData).forEach((key) => {
        if (
          key !== "idProof" &&
          key !== "addressProof" &&
          key !== "landProof"
        ) {
          data.append(key, formData[key]);
        }
      });

      // FILE UPLOADS
      data.append("idProof", formData.idProof);
      data.append("addressProof", formData.addressProof);
      data.append("landProof", formData.landProof);

      // ðŸš€ POST request to Java servlet
      const response = await fetch("http://localhost:8080/backend/LoanApplyServlet", {
        method: "POST",
        body: data,
      });

      if (!response.ok) {
        alert("Server error! Please try again.");
        return;
      }

      const result = await response.text();
      console.log("Server Response:", result);

      setShowSuccess(true);
    } catch (error) {
      console.error("Loan Application Error:", error);
      alert("Failed to submit application.");
    }
  };

  const progressPercent = (step / 3) * 100;

  // ===============================
  // UI RETURN
  // ===============================
  return (
    <>
      <Navbar />

      <div className="loan-container">
        
        {/* HERO */}
        <div className="loan-hero">
          <h1>Apply for Your Agri-Loan</h1>
          <p>Quick, hassle-free financing designed for your crop cycle.</p>
        </div>

        {/* PRODUCTS */}
        <h2 className="section-title">Our Loan Products</h2>

        <div className="loan-products-container">
          <div className="loan-product-card">
            <i className="fa-solid fa-wheat-awn loan-product-icon"></i>
            <h3>Crop Loan</h3>
            <p>Finance for seeds, fertilizers, pesticides & more.</p>
            <span>7% Interest â€¢ Short Term</span>
          </div>

          <div className="loan-product-card">
            <i className="fa-solid fa-tractor loan-product-icon"></i>
            <h3>Equipment Loan</h3>
            <p>Buy tractors, harvesters, irrigation systems.</p>
            <span>6.5% Interest â€¢ Medium Term</span>
          </div>

          <div className="loan-product-card">
            <i className="fa-solid fa-cow loan-product-icon"></i>
            <h3>Livestock Loan</h3>
            <p>Dairy, cattle, goats, poultry & more.</p>
            <span>8% Interest â€¢ Medium Term</span>
          </div>

          <div className="loan-product-card">
            <i className="fa-solid fa-shop loan-product-icon"></i>
            <h3>Agri-Business Loan</h3>
            <p>Fund agri-shops, processing units, cold storage.</p>
            <span>9% Interest â€¢ Long Term</span>
          </div>
        </div>

        {/* EMI */}
        <h2 className="section-title">EMI Calculator</h2>

        <div className="emi-box">
          <div className="emi-left">
            <label>Loan Amount (â‚¹)</label>
            <input
              type="number"
              value={loanAmount}
              onChange={(e) => setLoanAmount(Number(e.target.value))}
            />

            <label>Interest Rate (%)</label>
            <input
              type="number"
              value={interest}
              onChange={(e) => setInterest(Number(e.target.value))}
            />

            <label>Tenure (Months)</label>
            <input
              type="number"
              value={tenure}
              onChange={(e) => setTenure(Number(e.target.value))}
            />
          </div>

          <div className="emi-right">
            <h3>Calculated EMI</h3>
            <p className="emi-value">â‚¹ {emi ? emi.toFixed(2) : "0.00"}</p>
            <span>per month</span>
          </div>
        </div>

        {/* APPLICATION FORM */}
        <h2 className="section-title">Apply for Loan</h2>

        <div className="loan-application-card">
          
          {/* Progress Bar */}
          <div className="loan-app-progress">
            <div
              className="loan-app-progress-fill"
              style={{ width: `${progressPercent}%` }}
            ></div>
          </div>

          {/* ================= STEP 1 ================= */}
          {step === 1 && (
            <div className="loan-step-content">
              <h4 className="loan-step-title">Step 1: Personal Details</h4>

              <div className="loan-grid">
                <div className="field">
                  <label>Full Name *</label>
                  <input
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>DOB *</label>
                  <input
                    type="date"
                    name="dob"
                    value={formData.dob}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>Aadhaar/PAN *</label>
                  <input
                    name="aadhaar"
                    value={formData.aadhaar}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>Phone *</label>
                  <input
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                  />
                </div>

                <div className="field wide">
                  <label>Address *</label>
                  <input
                    name="address"
                    value={formData.address}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>State *</label>
                  <input
                    name="state"
                    value={formData.state}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>Pin Code *</label>
                  <input
                    name="pinCode"
                    value={formData.pinCode}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div className="loan-step-actions">
                <button className="next-btn" onClick={nextStep}>
                  Next
                </button>
              </div>
            </div>
          )}

          {/* ================= STEP 2 ================= */}
          {step === 2 && (
            <div className="loan-step-content">
              <h4 className="loan-step-title">
                Step 2: Loan Requirements
              </h4>

              <div className="loan-grid">
                <div className="field">
                  <label>Loan Amount Needed *</label>
                  <input
                    name="loanAmountNeeded"
                    value={formData.loanAmountNeeded}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>Loan Purpose *</label>
                  <input
                    name="loanPurpose"
                    value={formData.loanPurpose}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>Crop/Activity *</label>
                  <input
                    name="cropActivity"
                    value={formData.cropActivity}
                    onChange={handleChange}
                  />
                </div>

                <div className="field">
                  <label>Land Area (Acres) *</label>
                  <input
                    name="landArea"
                    value={formData.landArea}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div className="loan-step-actions">
                <button className="back-btn" onClick={prevStep}>
                  Back
                </button>
                <button className="next-btn" onClick={nextStep}>
                  Next
                </button>
              </div>
            </div>
          )}

          {/* ================= STEP 3 ================= */}
          {step === 3 && (
            <div className="loan-step-content">
              <h4 className="loan-step-title">Step 3: Document Uploads</h4>

              <div className="upload-grid">
                <label className="upload-card">
                  <i className="fa-solid fa-id-card upload-fa-icon"></i>
                  <p>ID Proof</p>
                  <span>{formData.idProof?.name ?? "Upload ID Proof"}</span>
                  <input type="file" name="idProof" onChange={handleFileChange} />
                </label>

                <label className="upload-card">
                  <i className="fa-solid fa-location-dot upload-fa-icon"></i>
                  <p>Address Proof</p>
                  <span>{formData.addressProof?.name ?? "Upload Address Proof"}</span>
                  <input type="file" name="addressProof" onChange={handleFileChange} />
                </label>

                <label className="upload-card">
                  <i className="fa-solid fa-file-contract upload-fa-icon"></i>
                  <p>Land Proof</p>
                  <span>{formData.landProof?.name ?? "Upload Land Proof"}</span>
                  <input type="file" name="landProof" onChange={handleFileChange} />
                </label>
              </div>

              <label className="declaration-row">
                <input
                  type="checkbox"
                  name="declaration"
                  checked={formData.declaration}
                  onChange={handleChange}
                />
                <span>
                  I declare that the provided information is true and valid.
                </span>
              </label>

              <div className="loan-step-actions single">
                <button className="back-btn" onClick={prevStep}>
                  Back
                </button>

                <button className="submit-main-btn" onClick={handleSubmitApplication}>
                  Submit Application
                </button>
              </div>
            </div>
          )}
        </div>

        {/* SUCCESS POPUP */}
        {showSuccess && (
          <div className="success-popup">
            <div className="success-box">
              <h2>ðŸŽ‰ Application Submitted!</h2>
              <p>
                Thank you! Your Agri-loan application has been submitted successfully.
                Our team will contact you soon.
              </p>
              <button onClick={() => setShowSuccess(false)}>Close</button>
            </div>
          </div>
        )}
      </div>

      <Footer />
    </>
  );
}

export default LoanPage;
