import React, { useEffect, useState } from "react";
import "../Css/Cart.css";
import Sidebar from "../components/Sidebar";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

function Cart() {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);

  /* =========================
     SAFE CART LOAD
  ========================= */
  useEffect(() => {
    const refreshCart = () => {
      try {
        const raw = localStorage.getItem("cart");
        const parsed = raw ? JSON.parse(raw) : [];
        setCart(Array.isArray(parsed) ? parsed : []);
      } catch {
        setCart([]);
      }
    };

    refreshCart();
    window.addEventListener("focus", refreshCart);
    return () => window.removeEventListener("focus", refreshCart);
  }, []);

  /* =========================
     LOAD USER (SESSION)
  ========================= */
  useEffect(() => {
    fetch("/api/user/get-profile", {
      credentials: "include",
    })
      .then((res) => res.json())
      .then((data) => {
        if (!data || data.status === "error") return;
        setUser(data);
      })
      .catch(() => setUser(null));
  }, []);

  return (
    <div className="dashboard-wrapper">
      <Sidebar cartCount={cart.length} />

      <div className="dashboard-content">
        <Navbar />

        <main className="dashboard-main">
          <div className="cart-header">
            <h2>Hello, {user?.fullName || "Guest"} ðŸ‘‹</h2>
            <p>Your shopping cart summary</p>
          </div>

          {cart.length === 0 ? (
            <p style={{ padding: 20 }}>Your cart is empty.</p>
          ) : (
            <p style={{ padding: 20 }}>
              Items in cart: <b>{cart.length}</b>
            </p>
          )}
        </main>

        <Footer />
      </div>
    </div>
  );
}

export default Cart;
