import "../Css/LoanPage.css";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

function LoanPage() {
  const [loanAmount, setLoanAmount] = useState(50000);
  const [interest, setInterest] = useState(7);
  const [tenure, setTenure] = useState(12);

  const emi =
    (loanAmount * interest * 0.01 * Math.pow(1 + interest * 0.01, tenure)) /
    (Math.pow(1 + interest * 0.01, tenure) - 1);

  // ---------------- MULTISTEP FORM STATES ----------------
  const [step, setStep] = useState(1);
  const [showSuccess, setShowSuccess] = useState(false);

  const [formData, setFormData] = useState({
    fullName: "",
    phone: "",
    email: "",
    aadhar: "",
    loanType: "Crop Loan",
    amount: "",
    landSize: "",
    purpose: "",
    documents: null,
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({ ...formData, [name]: files ? files : value });
  };

  // ---------------- VALIDATION ----------------
  const validateStep = () => {
    if (step === 1) {
      if (!formData.fullName || !formData.phone || !formData.email || !formData.aadhar) {
        alert("Please complete Personal Details");
        return false;
      }
    }
    if (step === 2) {
      if (!formData.amount || !formData.landSize || !formData.purpose) {
        alert("Please complete Loan Details");
        return false;
      }
    }
    return true;
  };

  const nextStep = () => {
    if (validateStep()) setStep(step + 1);
  };

  const prevStep = () => setStep(step - 1);

  const submitForm = () => {
    if (!formData.documents) {
      alert("Please upload loan documents");
      return;
    }
    setShowSuccess(true);
  };

  return (
    <>
      <Navbar />

      <div className="loan-container">

        {/* HERO */}
        <div className="loan-hero">
          <h1>Farmer Loan Services</h1>
          <p>Instant loans for crop management and agriculture growth.</p>
        </div>

        {/* CRITERIA */}
        <h2 className="section-title">Loan Approval Criteria</h2>
        <div className="loan-eligibility">
          <p>âœ” Age minimum: 18 years</p>
          <p>âœ” Must be an Indian citizen</p>
          <p>âœ” Active farming activities</p>
          <p>âœ” Land documents or lease papers</p>
          <p>âœ” 6-month bank statement</p>
          <p>âœ” Credit score 650+ (recommended)</p>
        </div>

        {/* PRODUCTS */}
        <h2 className="section-title">Loan Products</h2>
        <div className="loan-cards">
          <div className="loan-card"><h3>Crop Loan</h3><p>For seeds & fertilizers.</p><span>7%</span></div>
          <div className="loan-card"><h3>Equipment Loan</h3><p>For tractors & tools.</p><span>6.5%</span></div>
          <div className="loan-card"><h3>Livestock Loan</h3><p>For cattle & dairy.</p><span>8%</span></div>
          <div className="loan-card"><h3>Agri-Business Loan</h3><p>Storage & shops.</p><span>9%</span></div>
        </div>

        {/* EMI CALCULATOR */}
        <h2 className="section-title">EMI Calculator</h2>
        <div className="emi-section">
          <div className="emi-inputs">
            <label>Loan Amount</label>
            <input type="number" value={loanAmount} onChange={(e) => setLoanAmount(Number(e.target.value))} />

            <label>Interest Rate</label>
            <input type="number" value={interest} onChange={(e) => setInterest(Number(e.target.value))} />

            <label>Tenure (Months)</label>
            <input type="number" value={tenure} onChange={(e) => setTenure(Number(e.target.value))} />
          </div>

          <div className="emi-result">
            <h3>Your EMI</h3>
            <p>â‚¹ {emi.toFixed(2)} / month</p>
          </div>
        </div>

        {/* DOCUMENT LIST */}
        <h2 className="section-title">Required Documents</h2>
        <ul className="doc-list">
          <li>âœ” Aadhar card</li>
          <li>âœ” Land proof</li>
          <li>âœ” Bank statement</li>
          <li>âœ” Income proof</li>
          <li>âœ” Crop / livestock proof</li>
        </ul>

        {/* MULTISTEP FORM */}
        <h2 className="section-title">Apply for Loan</h2>

        {/* ---------------- PROGRESS BAR ---------------- */}
        <div className="progress-container">
          <div className={`progress-step ${step >= 1 ? "active" : ""}`}>1</div>
          <div className={`progress-line ${step >= 2 ? "active" : ""}`}></div>
          <div className={`progress-step ${step >= 2 ? "active" : ""}`}>2</div>
          <div className={`progress-line ${step >= 3 ? "active" : ""}`}></div>
          <div className={`progress-step ${step >= 3 ? "active" : ""}`}>3</div>
        </div>

        <div className="multi-form">

          {/* STEP 1 */}
          {step === 1 && (
            <div className="form-step">
              <h2>Step 1: Personal Details</h2>

              <label>Full Name *</label>
              <input name="fullName" value={formData.fullName} onChange={handleChange} />

              <label>Phone Number *</label>
              <input name="phone" value={formData.phone} onChange={handleChange} />

              <label>Email *</label>
              <input name="email" value={formData.email} onChange={handleChange} />

              <label>Aadhar Number *</label>
              <input name="aadhar" value={formData.aadhar} onChange={handleChange} />

              <button className="next-btn" onClick={nextStep}>Next</button>
            </div>
          )}

          {/* STEP 2 */}
          {step === 2 && (
            <div className="form-step">
              <h2>Step 2: Loan Details</h2>

              <label>Loan Type *</label>
              <select name="loanType" value={formData.loanType} onChange={handleChange}>
                <option>Crop Loan</option>
                <option>Equipment Loan</option>
                <option>Livestock Loan</option>
                <option>Agri-Business Loan</option>
              </select>

              <label>Loan Amount *</label>
              <input type="number" name="amount" value={formData.amount} onChange={handleChange} />

              <label>Land Size (acres) *</label>
              <input type="number" name="landSize" value={formData.landSize} onChange={handleChange} />

              <label>Purpose *</label>
              <textarea rows="3" name="purpose" value={formData.purpose} onChange={handleChange}></textarea>

              <div className="button-row">
                <button className="back-btn" onClick={prevStep}>Back</button>
                <button className="next-btn" onClick={nextStep}>Next</button>
              </div>
            </div>
          )}

          {/* STEP 3 */}
          {step === 3 && (
            <div className="form-step">
              <h2>Step 3: Document Upload</h2>

              <label>Upload Required Documents *</label>
              <input type="file" name="documents" onChange={handleChange} />

              <div className="button-row">
                <button className="back-btn" onClick={prevStep}>Back</button>
                <button className="submit-btn" onClick={submitForm}>Submit Application</button>
              </div>
            </div>
          )}
        </div>

        {/* SUCCESS POPUP */}
        {showSuccess && (
          <div className="success-popup">
            <div className="success-box">
              <h2>ðŸŽ‰ Application Submitted!</h2>
              <p>Your loan application was successfully submitted.</p>
              <button onClick={() => setShowSuccess(false)}>Close</button>
            </div>
          </div>
        )}

      </div>

      <Footer />
    </>
  );
}

export default LoanPage;
