import React, { useState } from "react";
import "../Css/Sell.css";

function Sell() {

  const [images, setImages] = useState([]);
  const [marketPrice, setMarketPrice] = useState("");

  const handleMultipleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const previews = files.map((f) => URL.createObjectURL(f));

    setImages(previews);
  };

  // ⭐ FETCH LIVE MARKET PRICE
  const fetchMarketPrice = async (crop) => {
    try {
      const res = await fetch(
        `https://api.data.gov.in/resource/agri-market?crop=${crop}`
      );
      const data = await res.json();

      if (data.records?.length > 0) {
        setMarketPrice(data.records[0].modal_price);
      }
    } catch (err) {
      console.log("Market Price API error:", err);
    }
  };

  const saveCrop = async () => {
    const fd = new FormData();

    let mobile = localStorage.getItem("userMobile");

    fd.append("mobile", mobile);
    fd.append("cropName", document.getElementById("cropName").value);
    fd.append("category", document.getElementById("cropCat").value);
    fd.append("quantity", document.getElementById("qty").value);
    fd.append("price", document.getElementById("price").value);
    fd.append("deliveryTime", document.getElementById("delTime").value);
    fd.append("grade", document.getElementById("grade").value);
    fd.append("description", document.getElementById("desc").value);

    // MULTIPLE IMAGES
    const files = document.getElementById("multiImages").files;
    fd.append("image1", files[0] ?? "");
    fd.append("image2", files[1] ?? "");
    fd.append("image3", files[2] ?? "");

    const res = await fetch("http://localhost:9090/backend/api/crop/save", {
      method: "POST",
      body: fd
    });

    const data = await res.json();
    alert(data.status);
  };

  return (
    <div className="sell-container">

      <h2 className="sell-title"><i className="fa fa-store"></i> Sell Your Crops</h2>

      <div className="sell-layout">

        {/* MULTI IMAGE UPLOAD */}
        <div className="sell-image-box">

          <input
            type="file"
            id="multiImages"
            multiple
            accept="image/*"
            onChange={handleMultipleImageUpload}
          />

          <div className="multi-preview-box">
            {images.map((img, index) => (
              <img key={index} src={img} className="sell-preview-img" alt="crop" />
            ))}
          </div>
        </div>

        {/* FORM */}
        <div className="sell-form">

          <label className="sell-label"><i className="fa fa-leaf"></i> Crop Name</label>
          <input id="cropName" type="text" className="sell-input"
            onBlur={(e) => fetchMarketPrice(e.target.value)}
          />

          {/* AUTO PRICE FILLED */}
          {marketPrice && (
            <p className="market-price-box">Live Market Price: ₹ {marketPrice}</p>
          )}

          <label className="sell-label"><i className="fa fa-list"></i> Category</label>
          <select id="cropCat" className="sell-input">
            <option>Select Category</option>
            <option>Grains</option>
            <option>Vegetables</option>
            <option>Fruits</option>
            <option>Pulses</option>
            <option>Oil Seeds</option>
          </select>

          <div className="sell-row">
            <div>
              <label className="sell-label"><i className="fa fa-weight-hanging"></i> Quantity (kg)</label>
              <input id="qty" type="number" className="sell-input" />
            </div>

            <div>
              <label className="sell-label"><i className="fa fa-rupee-sign"></i> Price per Kg</label>
              <input id="price" type="number" className="sell-input" />
            </div>
          </div>

          <label className="sell-label"><i className="fa fa-clock"></i> Delivery Time</label>
          <select id="delTime" className="sell-input">
            <option>1 Day</option>
            <option>2-3 Days</option>
            <option>Within a Week</option>
          </select>

          <label className="sell-label"><i className="fa fa-star"></i> Grade</label>
          <select id="grade" className="sell-input">
            <option>A (Premium)</option>
            <option>B (Good)</option>
            <option>C (Average)</option>
          </select>

          <label className="sell-label"><i className="fa fa-align-left"></i> Description</label>
          <textarea id="desc" className="sell-textarea" />

          <button className="sell-submit" onClick={saveCrop}>
            <i className="fa fa-check-circle"></i> Post Crop
          </button>
        </div>

      </div>
    </div>
  );
}

export default Sell;
