import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import "../Css/TrackOrder.css";

function TrackOrder() {
  const { orderId } = useParams();
  const navigate = useNavigate();

  const orders = JSON.parse(localStorage.getItem("orders")) || [];
  const order = orders.find((o) => o.orderId === orderId);

  /* =========================
     ORDER STATUS STEPS
  ========================= */
  const steps = [
    { key: "Placed", title: "Order Placed", desc: "We have received your order." },
    { key: "Confirmed", title: "Order Confirmed", desc: "Your order has been confirmed." },
    { key: "Processed", title: "Order Processed", desc: "We are preparing your order." },
    { key: "Delivered", title: "Delivered", desc: "Your order has been delivered." },
  ];

  const getStepIndex = () =>
    steps.findIndex((s) => s.key === order?.status);

  const activeIndex = getStepIndex();

  return (
    <>
      <Navbar />

      <div className="track-wrapper">
        {!order ? (
          <div className="track-empty">
            <h2>Order Not Found</h2>
            <p>Please check your order ID.</p>
            <button onClick={() => navigate("/dashboard")}>
              Go Home
            </button>
          </div>
        ) : (
          <div className="track-card">
            {/* HEADER */}
            <div className="track-header">
              <h2>Track Order</h2>
              <span className="track-cancel" onClick={() => navigate(-1)}>
                Back
              </span>
            </div>

            {/* ORDER META */}
            <div className="track-meta">
              <div>
                <span>Estimated Time</span>
                <b>30 minutes</b>
              </div>
              <div>
                <span>Order Number</span>
                <b>{order.orderId}</b>
              </div>
            </div>

            {/* TIMELINE */}
            <div className="timeline">
              {steps.map((step, index) => {
                const completed = index <= activeIndex;
                const active = index === activeIndex;

                return (
                  <div className="timeline-row" key={step.key}>
                    <div className="timeline-left">
                      <span
                        className={`dot ${
                          completed ? "completed" : ""
                        } ${active ? "active" : ""}`}
                      />
                      {index !== steps.length - 1 && (
                        <span
                          className={`line ${
                            completed ? "completed" : ""
                          }`}
                        />
                      )}
                    </div>

                    <div className="timeline-content">
                      <h4 className={active ? "active-text" : ""}>
                        {step.title}
                      </h4>
                      <p>{step.desc}</p>
                    </div>
                  </div>
                );
              })}
            </div>

            {/* ACTION */}
            <div className="track-footer">
              <button onClick={() => navigate("/dashboard")}>
                Home
              </button>
            </div>
          </div>
        )}
      </div>

      <Footer />
    </>
  );
}

export default TrackOrder;
