import React, { useEffect, useState } from "react";
import "../Css/Cart.css";
import Sidebar from "../components/Sidebar";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

function Cart() {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const refreshCart = () => {
      const saved = JSON.parse(localStorage.getItem("cart")) || [];
      setCart(saved);
    };
    refreshCart();

    window.addEventListener("focus", refreshCart);
    return () => window.removeEventListener("focus", refreshCart);
  }, []);

  useEffect(() => {
    const savedUser = JSON.parse(localStorage.getItem("userData"));
    if (savedUser) setUser(savedUser);
  }, []);

  const updateCart = (newCart) => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
    window.dispatchEvent(new Event("cartUpdated"));
  };

  const changeQty = (cropId, diff, available) => {
    const updated = cart.map((item) => {
      if (item.cropId === cropId) {
        let newQty = item.qty + diff;
        if (newQty < 1) newQty = 1;
        if (newQty > available) newQty = available;
        return { ...item, qty: newQty };
      }
      return item;
    });
    updateCart(updated);
  };

  const removeItem = (id) => {
    const updated = cart.filter((c) => c.cropId !== id);
    updateCart(updated);
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  const getImage = (imgStr) => {
    if (!imgStr) return "/Images/noimg.png";
    const first = imgStr.split(",")[0].trim();
    return `http://localhost:9090/backend/uploads/${first}`;
  };

  const getStockStatus = (available) => {
    if (available === 0) return "out";
    if (available <= 10) return "limited";
    return "in";
  };

  return (
    <div className="dashboard-wrapper">
      {/* SIDEBAR */}
      <Sidebar cartCount={cart.length} />

      {/* RIGHT AREA */}
      <div className="dashboard-content">
        <Navbar />

        <main className="dashboard-main cart-page">

          <div className="cart-header-top">
            <h2>Hello, {user?.fullName} ðŸ‘‹</h2>
            <p>Your shopping cart summary</p>
          </div>

          <h2 className="cart-page-title">ðŸ›’ Your Cart</h2>

          <div className="cart-layout">
            
            {/* LEFT SIDE â€” CART ITEMS */}
            <div className="cart-items">
              {cart.length === 0 ? (
                <p className="empty-cart">Your cart is empty.</p>
              ) : (
                cart.map((item) => {
                  const available = item.quantity || item.availableQty || 0;
                  const stockStatus = getStockStatus(available);

                  return (
                    <div className="cart-card-pro" key={item.cropId}>
                      <img className="cart-img-pro" src={getImage(item.images)} alt="" />

                      <div className="cart-details-pro">
                        <h3>{item.cropName}</h3>

                        <p className="farmer-id">Farmer ID: {item.farmerId}</p>
                        <p className="price">Price: â‚¹{item.price} / kg</p>
                        <p className="stock-line">
                          Available: <b>{available} Kg</b>
                        </p>

                        {/* STOCK BADGE */}
                        <span className={`stock-badge ${stockStatus}`}>
                          {stockStatus === "in" && "âœ” In Stock"}
                          {stockStatus === "limited" && "âš  Limited Stock"}
                          {stockStatus === "out" && "âœ– Out of Stock"}
                        </span>

                        {/* QUANTITY CONTROLS */}
                        <div className="qty-box-pro">
                          <button onClick={() => changeQty(item.cropId, -1, available)}>-</button>
                          <span>{item.qty}</span>
                          <button onClick={() => changeQty(item.cropId, +1, available)}>+</button>
                        </div>

                        <p className="item-total-pro">
                          Item Total: <b>â‚¹{item.qty * item.price}</b>
                        </p>
                      </div>

                      <button className="remove-btn-pro" onClick={() => removeItem(item.cropId)}>
                        Remove
                      </button>
                    </div>
                  );
                })
              )}
            </div>

            {/* RIGHT SIDE â€” SUMMARY CARD */}
            {cart.length > 0 && (
              <div className="cart-summary-card">
                <h3>Order Summary</h3>

                <div className="sum-row">
                  <span>Subtotal</span>
                  <b>â‚¹{total}</b>
                </div>

                <div className="sum-row">
                  <span>Shipping</span>
                  <b>â‚¹0</b>
                </div>

                <div className="sum-row">
                  <span>Taxes (6%)</span>
                  <b>â‚¹{Math.round(total * 0.06)}</b>
                </div>

                <hr />

                <div className="grand-total">
                  <span>Grand Total</span>
                  <b>â‚¹{Math.round(total * 1.06)}</b>
                </div>

                <button className="checkout-btn-pro">
                  Proceed to Checkout
                </button>

                <input type="text" placeholder="Enter Discount Code" className="discount-input" />
              </div>
            )}
          </div>
        </main>

        <Footer />
      </div>
    </div>
  );
}

export default Cart;
