import React, { useEffect, useState } from "react";
import "../Css/Dashboard.css";

function Dashboard() {
  const [user, setUser] = useState(null);
  const [crops, setCrops] = useState([]);
  const [editingCrop, setEditingCrop] = useState(null);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("userData"));
    if (stored) {
      setUser(stored);

      fetch(`http://localhost:9090/backend/api/crop/farmer?farmerId=${stored.farmerId}`)
        .then((res) => res.json())
        .then((data) => setCrops(data))
        .catch((err) => console.error("Failed to load crops", err));
    }
  }, []);

  // ------------------------------
  // FIXED IMAGE URL
  // ------------------------------
  const getFirstImage = (images) => {
    if (!images) return "/Images/noimg.png";

    const first = images.split(",")[0].trim();
    return `http://localhost:9090/backend/uploads/${first}`;
  };

  // ------------------------------
  // DELETE CROP (FIXED)
  // ------------------------------
  const deleteCrop = (cropId, images) => {
    if (!window.confirm("Are you sure you want to delete this crop?")) return;

    fetch("http://localhost:9090/backend/api/crop/delete", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: `cropId=${cropId}&images=${encodeURIComponent(images)}`,
    })
      .then((res) => {
        console.log("RAW RESPONSE:", res);
        return res.json();
      })
      .then((data) => {
        console.log("DELETE RESPONSE:", data);

        if (data.status === "SUCCESS") {
          alert("Crop deleted successfully!");
          setCrops((prev) => prev.filter((c) => c.cropId !== cropId));
        } else {
          alert("Delete failed: " + (data.message || "unknown error"));
        }
      })
      .catch((err) => {
        console.error("DELETE ERROR:", err);
        alert("Delete failed: network error");
      });
  };

  // ------------------------------
  // SAVE EDIT (FIXED)
  // ------------------------------
  const saveEdit = () => {
    const formData = new FormData();
    formData.append("cropId", editingCrop.cropId);
    formData.append("cropName", editingCrop.cropName);
    formData.append("category", editingCrop.category);
    formData.append("quantity", editingCrop.quantity);
    formData.append("price", editingCrop.price);
    formData.append("description", editingCrop.description);

    fetch("http://localhost:9090/backend/api/crop/update", {
      method: "POST",
      body: formData,
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "SUCCESS") {
          alert("Crop updated successfully!");

          // UPDATE UI WITHOUT REFRESH
          setCrops((prev) =>
            prev.map((c) =>
              c.cropId === editingCrop.cropId ? editingCrop : c
            )
          );

          setEditingCrop(null); // close popup
        }
      })
      .catch(() => alert("Update failed"));
  };

  return (
    <div className="dashboard-container">
      {/* Header */}
      <div className="dashboard-header">
        <h2>Welcome, {user?.fullName} ðŸ‘‹</h2>
        <p>Your farming insights and performance summary</p>
      </div>

      {/* Statistics */}
      <div className="dashboard-stats">
        <div className="stat-card">
          <i className="fa-solid fa-wheat-awn"></i>
          <h3>{crops.length}</h3>
          <p>Total Crops Listed</p>
        </div>

        <div className="stat-card">
          <i className="fa-solid fa-wallet"></i>
          <h3>â‚¹0</h3>
          <p>Total Earnings (Coming Soon)</p>
        </div>

        <div className="stat-card">
          <i className="fa-solid fa-chart-line"></i>
          <h3>Growing</h3>
          <p>Market Trends</p>
        </div>
      </div>

      {/* Graph */}
      <div className="graph-section">
        <h3>Market Activity Overview</h3>

        <div className="graph-box">
          {crops.map((c, index) => (
            <div key={index} className="graph-bar">
              <div
                className="bar"
                style={{ height: `${Math.min(Number(c.quantity) * 1.2, 180)}px` }}
              ></div>
              <span>{c.cropName}</span>
            </div>
          ))}

          {crops.length === 0 && <p className="no-graph">No data available</p>}
        </div>
      </div>

      {/* Crop List */}
      <div className="dashboard-section">
        <h3>Your Crop Listings</h3>

        <table className="crop-table">
          <thead>
            <tr>
              <th>Image</th>
              <th>Crop</th>
              <th>Category</th>
              <th>Qty</th>
              <th>Price</th>
              <th>Actions</th>
            </tr>
          </thead>

          <tbody>
            {crops.length === 0 ? (
              <tr>
                <td colSpan="6" style={{ textAlign: "center", padding: "20px" }}>
                  No crops posted yet.
                </td>
              </tr>
            ) : (
              crops.map((c, index) => (
                <tr key={index}>
                  <td>
                    <img
                      src={getFirstImage(c.images)}
                      className="crop-img"
                      alt="crop"
                    />
                  </td>
                  <td>{c.cropName}</td>
                  <td>{c.category}</td>
                  <td>{c.quantity} kg</td>
                  <td>â‚¹{c.price}</td>

                  <td>
                    <button className="edit-btn" onClick={() => setEditingCrop(c)}>
                      <i className="fa-solid fa-pen-to-square"></i>
                    </button>

                    <button
                      className="delete-btn"
                      onClick={() => deleteCrop(c.cropId, c.images)}
                    >
                      <i className="fa-solid fa-trash"></i>
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* EDIT POPUP */}
      {editingCrop && (
        <div className="edit-popup">
          <div className="edit-box">
            <h2>Edit Crop</h2>

            <input
              type="text"
              value={editingCrop.cropName}
              onChange={(e) =>
                setEditingCrop({ ...editingCrop, cropName: e.target.value })
              }
            />

            <input
              type="text"
              value={editingCrop.category}
              onChange={(e) =>
                setEditingCrop({ ...editingCrop, category: e.target.value })
              }
            />

            <input
              type="number"
              value={editingCrop.quantity}
              onChange={(e) =>
                setEditingCrop({ ...editingCrop, quantity: e.target.value })
              }
            />

            <input
              type="number"
              value={editingCrop.price}
              onChange={(e) =>
                setEditingCrop({ ...editingCrop, price: e.target.value })
              }
            />

            <textarea
              value={editingCrop.description}
              onChange={(e) =>
                setEditingCrop({ ...editingCrop, description: e.target.value })
              }
            ></textarea>

            <div className="edit-actions">
              <button className="save-btn" onClick={saveEdit}>
                Save
              </button>
              <button className="cancel-btn" onClick={() => setEditingCrop(null)}>
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
