import React, { useEffect, useState } from "react";
import "../Css/Profile.css";

function Profile() {
  const mobile = localStorage.getItem("userMobile");

  const [user, setUser] = useState({
    fullName: "",
    email: "",
    address: "",
    state: "",
    district: "",
    pincode: "",
    farmSize: "",
    mainCrops: "",
    bankAccount: "",
    ifsc: "",
    gst: "",
    pan: "",
    aadhar: "",
    profilePhoto: "",
    verified: 0,
    role: "Farmer",   // ⭐ Default value
  });

  const [photoFile, setPhotoFile] = useState(null);
  const [doc1, setDoc1] = useState(null);
  const [doc2, setDoc2] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:9090/backend/api/user/get-profile?mobile=${mobile}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          setUser((prev) => ({ ...prev, ...data.user }));
        }
      });
  }, [mobile]);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    const fd = new FormData();
    Object.keys(user).forEach((k) => fd.append(k, user[k] ?? ""));
    fd.append("mobile", mobile);

    if (photoFile) fd.append("profilePhoto", photoFile);
    if (doc1) fd.append("document1", doc1);
    if (doc2) fd.append("document2", doc2);

    const res = await fetch(
      "http://localhost:9090/backend/api/user/update-profile",
      {
        method: "POST",
        body: fd,
      }
    );

    const data = await res.json();
    alert(data.message);
  };

  const logout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  const profileImg = photoFile
    ? URL.createObjectURL(photoFile)
    : user.profilePhoto
    ? "http://localhost:9090/backend/" + user.profilePhoto
    : "/default-avatar.png";

  return (
    <div className="profile-page-background">
      <div className="profile-card">

        {/* HEADER */}
        <div className="profile-header">
          <h2>User Profile Setup</h2>
          <button className="logout-btn" onClick={logout}>
            Logout
          </button>
        </div>

        {/* TWO COLUMN FORM */}
        <div className="profile-form-layout">

          {/* LEFT SIDE */}
          <div>
            <h3 className="section-title">Personal Details</h3>

            {/* PROFILE PHOTO */}
            <div className="photo-upload-container">
              <img src={profileImg} className="profile-photo" alt="profile" />

              <label className="custom-file-label">
                Choose Photo
                <input
                  type="file"
                  hidden
                  onChange={(e) => setPhotoFile(e.target.files[0])}
                />
              </label>
            </div>

            {/* ROLE SELECT — Added */}
            <select
              name="role"
              value={user.role}
              onChange={handleChange}
              className="form-input"
            >
              <option value="Farmer">Farmer</option>
              <option value="Buyer">Buyer</option>
            </select>

            <input
              className="form-input"
              name="fullName"
              placeholder="Full Name"
              value={user.fullName}
              onChange={handleChange}
            />

            <input
              className="form-input"
              name="email"
              placeholder="Email"
              value={user.email}
              onChange={handleChange}
            />

            <textarea
              className="form-input textarea"
              name="address"
              placeholder="Address"
              value={user.address}
              onChange={handleChange}
            />

            <div className="input-group-row">
              <input
                className="form-input"
                name="state"
                placeholder="State"
                value={user.state}
                onChange={handleChange}
              />
              <input
                className="form-input"
                name="district"
                placeholder="District"
                value={user.district}
                onChange={handleChange}
              />
            </div>

            <input
              className="form-input"
              name="pincode"
              placeholder="Pincode"
              value={user.pincode}
              onChange={handleChange}
            />

            {/* Aadhar + Verify */}
            <div className="aadhar-section">
              <input
                className="form-input"
                name="aadhar"
                placeholder="Aadhar Number"
                value={user.aadhar}
                onChange={handleChange}
              />

              <span className="verify-status">
                {user.verified === 1 ? (
                  <span className="verified">✔ Verified</span>
                ) : (
                  <span className="pending">⏳ Pending</span>
                )}
              </span>
            </div>
          </div>

          {/* RIGHT SIDE */}
          <div>
            <h3 className="section-title">Farm & Business Details</h3>

            <div className="input-group-row">
              <input
                className="form-input"
                name="farmSize"
                placeholder="Farm Size (Acres)"
                value={user.farmSize}
                onChange={handleChange}
              />
              <input
                className="form-input"
                name="mainCrops"
                placeholder="Main Crops"
                value={user.mainCrops}
                onChange={handleChange}
              />
            </div>

            <div className="input-group-row">
              <input
                className="form-input"
                name="gst"
                placeholder="GST (optional)"
                value={user.gst}
                onChange={handleChange}
              />
              <input
                className="form-input"
                name="pan"
                placeholder="PAN Number"
                value={user.pan}
                onChange={handleChange}
              />
            </div>

            <h3 className="section-title">Bank Information</h3>

            <input
              className="form-input"
              name="bankAccount"
              placeholder="Bank Account Number"
              value={user.bankAccount}
              onChange={handleChange}
            />

            <input
              className="form-input"
              name="ifsc"
              placeholder="IFSC Code"
              value={user.ifsc}
              onChange={handleChange}
            />

            <h3 className="section-title">Upload Documents</h3>

            <label className="file-upload-label">Document 1:</label>
            <input type="file" onChange={(e) => setDoc1(e.target.files[0])} />

            <label className="file-upload-label">Document 2:</label>
            <input type="file" onChange={(e) => setDoc2(e.target.files[0])} />
          </div>
        </div>

        {/* SAVE BUTTON */}
        <button className="save-profile-btn" onClick={handleSave}>
          Save Profile
        </button>
      </div>
    </div>
  );
}

export default Profile;
