import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import Footer from "../components/Footer";
import "../Css/TrackOrder.css";

function TrackOrder() {
  const { orderId } = useParams();
  const navigate = useNavigate();

  const orders = JSON.parse(localStorage.getItem("orders")) || [];
  const order = orders.find((o) => o.orderId === orderId);

  const steps = [
    { key: "Placed", title: "Order Placed", desc: "We have received your order." },
    { key: "Confirmed", title: "Order Confirmed", desc: "Your order has been confirmed." },
    { key: "Processed", title: "Order Processed", desc: "We are preparing your order." },
    { key: "Delivered", title: "Delivered", desc: "Your order has been delivered." },
  ];

  const activeIndex = steps.findIndex(
    (s) => s.key === order?.status
  );

  return (
    <>
      <Navbar />

      <div className="layout">
        <Sidebar cartCount={0} />

        <div className="track-page">
          {!order ? (
            <div className="track-empty">
              <h2>Order Not Found</h2>
              <button onClick={() => navigate("/dashboard")}>
                Go Home
              </button>
            </div>
          ) : (
            <div className="track-wrapper">
              {/* HEADER */}
              <div className="track-topbar">
                <h2>Track Order</h2>
                <span onClick={() => navigate(-1)}>Back</span>
              </div>

              {/* INFO */}
              <div className="track-info-bar">
                <div>
                  <span>Estimated Time</span>
                  <b>30 minutes</b>
                </div>
                <div>
                  <span>Order Number</span>
                  <b>{order.orderId}</b>
                </div>
              </div>

              {/* TIMELINE */}
              <div className="track-timeline">
                {steps.map((step, index) => {
                  const completed = index <= activeIndex;
                  const active = index === activeIndex;

                  return (
                    <div className="timeline-row" key={step.key}>
                      <div className="timeline-left">
                        <span
                          className={`dot ${completed ? "done" : ""} ${
                            active ? "active" : ""
                          }`}
                        />
                        {index !== steps.length - 1 && (
                          <span
                            className={`line ${completed ? "done" : ""}`}
                          />
                        )}
                      </div>

                      <div className="timeline-right">
                        <h4 className={active ? "active-text" : ""}>
                          {step.title}
                        </h4>
                        <p>{step.desc}</p>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </>
  );
}

export default TrackOrder;
