import React, { useState } from "react";
import "../Css/InsurancePage.css";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

function InsurancePage() {
  const [form, setForm] = useState({
    fullName: "",
    area: "",
    totalLandArea: "",
    landOwnership: "Owned",
    phone: "",
    farmLocation: "",
    cropToInsure: "",
    cropVariety: "",
    expectedSowing: "",
    expectedHarvest: "",
    pastCropHistory: "",
    govIdNumber: "",
    aadhaar: "",
    bankAccount: "",
    ifsc: "",
    coverageType: "",
    landDoc: null,
    idProof: null,
    previousPolicy: null,
    agree: false,
  });

  const [activeDrag, setActiveDrag] = useState(null);
  const [submitting, setSubmitting] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [createdInsuranceId, setCreatedInsuranceId] = useState(null);

  const onChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({ ...prev, [name]: type === "checkbox" ? checked : value }));
  };

  const handleFileSelect = (e) => {
    const { name, files } = e.target;
    if (files && files[0]) setForm((prev) => ({ ...prev, [name]: files[0] }));
  };

  const handleDragOver = (e, field) => { e.preventDefault(); setActiveDrag(field); };
  const handleDragLeave = () => setActiveDrag(null);
  const handleDrop = (e, field) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file) setForm((prev) => ({ ...prev, [field]: file }));
    setActiveDrag(null);
  };

  const DragUpload = ({ field, label }) => (
    <label
      className={`upload-box ${activeDrag === field ? "drag-active" : ""}`}
      onDragOver={(e) => handleDragOver(e, field)}
      onDragLeave={handleDragLeave}
      onDrop={(e) => handleDrop(e, field)}
    >
      <input type="file" name={field} onChange={handleFileSelect} />
      <div className="upload-icon">â¬†</div>
      <div className="upload-text">{label}</div>
      {form[field] && <div className="file-name">ðŸ“„ {form[field].name}</div>}
    </label>
  );

  const validate = () => {
    if (!form.fullName || !form.phone || !form.aadhaar) { alert("Please fill Full Name, Phone and Aadhaar."); return false; }
    if (!form.cropToInsure || !form.totalLandArea) { alert("Please fill crop and land information."); return false; }
    if (!form.landDoc || !form.idProof) { alert("Please upload required documents."); return false; }
    if (!form.agree) { alert("Please accept the declaration."); return false; }
    return true;
  };

  const handleSubmit = async () => {
    if (!validate()) return;

    const fd = new FormData();

    // add all text fields
    const textFields = [
      "fullName","area","totalLandArea","landOwnership","phone","farmLocation",
      "cropToInsure","cropVariety","expectedSowing","expectedHarvest","pastCropHistory",
      "govIdNumber","aadhaar","bankAccount","ifsc","coverageType"
    ];
    textFields.forEach((k) => fd.append(k, form[k] ?? ""));

    // add files
    if (form.landDoc) fd.append("landDoc", form.landDoc);
    if (form.idProof) fd.append("idProof", form.idProof);
    if (form.previousPolicy) fd.append("previousPolicy", form.previousPolicy);

    // add farmerId from localStorage (key: farmerId as you confirmed)
    const user = JSON.parse(localStorage.getItem("userData") || "null");
    const farmerId = user?.farmerId ?? "";
    fd.append("farmerId", farmerId);

    try {
      setSubmitting(true);

      const res = await fetch("http://localhost:9090/backend/api/insurance/apply", {
        method: "POST",
        body: fd,
      });

      // Expecting JSON: { status: "SUCCESS", insuranceId: "INS-..." }
      const json = await res.json();

      if (res.ok && json.status === "SUCCESS") {
        setCreatedInsuranceId(json.insuranceId);
        setShowSuccess(true);
        // Optionally clear form or keep values
      } else {
        alert("Submission failed.");
      }
    } catch (err) {
      console.error(err);
      alert("Network error.");
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <>
      <Navbar />

      <div className="insurance-page">
        <header className="insurance-hero">
          <div className="circle-right-1"></div>
          <div className="circle-right-2"></div>
          <div className="circle-left-3"></div>
          <div className="circle-left-4"></div>

          <h1>Apply for Farm Insurance</h1>
          <p>Secure your hard work against unpredictable weather and market changes.</p>
        </header>

        <main className="insurance-main container">
          {/* Row 1 */}
          <section className="grid-row">
            <div className="card">
              <div className="card-badge">1</div>
              <h3>Applicant Details</h3>
              <div className="form-row">
                <label>Full Name<input name="fullName" value={form.fullName} onChange={onChange} /></label>
                <label>Area (Acres / Hectares)<input name="area" value={form.area} onChange={onChange} /></label>
              </div>
              <div className="form-row">
                <label>Total Land Area<input name="totalLandArea" value={form.totalLandArea} onChange={onChange} /></label>
                <label>Land Ownership<select name="landOwnership" value={form.landOwnership} onChange={onChange}><option>Owned</option><option>Leased</option></select></label>
              </div>
              <div className="form-row">
                <label>Phone Number<input name="phone" value={form.phone} onChange={onChange} /></label>
                <label>Farm Location<input name="farmLocation" value={form.farmLocation} onChange={onChange} /></label>
              </div>

              <DragUpload field="landDoc" label="Upload Land Ownership / Lease Document" />
            </div>

            <div className="card">
              <div className="card-badge">2</div>
              <h3>Farm & Land Information</h3>

              <div className="form-row">
                <label>Crop to Insure<input name="cropToInsure" value={form.cropToInsure} onChange={onChange} /></label>
                <label>Crop Variety<input name="cropVariety" value={form.cropVariety} onChange={onChange} /></label>
              </div>

              <div className="form-row">
                <label>Expected Sowing Date<input type="date" name="expectedSowing" value={form.expectedSowing} onChange={onChange} /></label>
                <label>Expected Harvest Date<input type="date" name="expectedHarvest" value={form.expectedHarvest} onChange={onChange} /></label>
              </div>

              <label className="textarea-label">Past Crop History
                <textarea name="pastCropHistory" rows={4} value={form.pastCropHistory} onChange={onChange} />
              </label>
            </div>
          </section>

          {/* Row 2 */}
          <section className="grid-row">
            <div className="card">
              <div className="card-badge">3</div>
              <h3>Identification & Bank Details</h3>

              <div className="form-row">
                <label>Government ID Number<input name="govIdNumber" value={form.govIdNumber} onChange={onChange} /></label>
                <label>Aadhaar Number<input name="aadhaar" value={form.aadhaar} onChange={onChange} /></label>
              </div>

              <div className="form-row">
                <label>Bank Account Number<input name="bankAccount" value={form.bankAccount} onChange={onChange} /></label>
                <label>IFSC Code<input name="ifsc" value={form.ifsc} onChange={onChange} /></label>
              </div>

              <div className="coverage-wrapper">
                <span className="coverage-label">Coverage Type</span>
                <select className="coverage-select" name="coverageType" value={form.coverageType} onChange={onChange}>
                  <option value="">Select Coverage</option>
                  <option value="Crop">Crop Insurance</option>
                  <option value="Livestock">Livestock Insurance</option>
                  <option value="Equipment">Equipment Insurance</option>
                  <option value="Health">Farmer Health Insurance</option>
                </select>
              </div>

              <div className="upload-inline">
                <DragUpload field="idProof" label="Upload ID Proof" />
                <DragUpload field="previousPolicy" label="Previous Policy (Optional)" />
              </div>
            </div>

            <div className="card">
              <div className="card-badge">4</div>
              <h3>Declaration</h3>

              <p className="notes">Please ensure all provided information is correct. Incorrect information may lead to rejection.</p>

              <label className="declaration">
                <input type="checkbox" name="agree" checked={form.agree} onChange={onChange} />
                I confirm the above information is true.
              </label>
            </div>
          </section>

          <div className="submit-row">
            <button className="submit-btn" onClick={handleSubmit} disabled={submitting}>
              {submitting ? "Submitting..." : "Submit Application"}
            </button>
          </div>
        </main>

        {showSuccess && (
          <div className="success-popup">
            <div className="success-box">
              <h2>Application Submitted</h2>
              <p>Thank you! Your Insurance ID:</p>
              <p style={{fontWeight:700, color:"#137a38"}}>{createdInsuranceId}</p>
              <button onClick={() => { setShowSuccess(false); setCreatedInsuranceId(null); }}>OK</button>
            </div>
          </div>
        )}
      </div>

      <Footer />
    </>
  );
}

export default InsurancePage;
