import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import { useNavigate } from "react-router-dom";
import "../Css/AdminDashboard.css";

function AdminDashboard() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const loadAdminData = async () => {
      try {
        const res = await fetch("/api/admin/dashboard", {
          credentials: "include",
          cache: "no-store",
        });

        // ðŸ” NOT AUTHORIZED
        if (res.status === 403) {
          throw new Error("NOT_AUTHORIZED");
        }

        if (!res.ok) {
          throw new Error("SERVER_ERROR");
        }

        const json = await res.json();
        setData(json);
      } catch (err) {
        console.error("Admin dashboard error:", err);

        if (err.message === "NOT_AUTHORIZED") {
          setError("You are not authorized to view this page.");
          // â³ Redirect after message
          setTimeout(() => navigate("/home"), 1500);
        } else {
          setError("Something went wrong. Try again.");
        }
      } finally {
        setLoading(false);
      }
    };

    loadAdminData();
  }, [navigate]);

  /* ================= LOADING ================= */
  if (loading) {
    return (
      <>
        <Navbar />
        <p style={{ textAlign: "center", marginTop: "100px" }}>
          Loading admin dashboard...
        </p>
      </>
    );
  }

  /* ================= ERROR ================= */
  if (error) {
    return (
      <>
        <Navbar />
        <p style={{ textAlign: "center", marginTop: "100px", color: "red" }}>
          {error}
        </p>
      </>
    );
  }

  /* ================= DASHBOARD ================= */
  return (
    <>
      <Navbar />
      <div className="admin-container">
        <h1>Admin Dashboard</h1>

        {/* USERS */}
        <section>
          <h2>Users</h2>
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Mobile</th>
                <th>Role</th>
                <th>System Role</th>
              </tr>
            </thead>
            <tbody>
              {data.users.map((u, i) => (
                <tr key={i}>
                  <td>{u.fullName}</td>
                  <td>{u.mobile}</td>
                  <td>{u.role}</td>
                  <td>{u.systemRole}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>

        {/* CROPS */}
        <section>
          <h2>Crops</h2>
          <table>
            <thead>
              <tr>
                <th>Crop</th>
                <th>Farmer</th>
                <th>Category</th>
                <th>Quantity</th>
                <th>Price</th>
              </tr>
            </thead>
            <tbody>
              {data.crops.map((c, i) => (
                <tr key={i}>
                  <td>{c.cropName}</td>
                  <td>{c.farmerId}</td>
                  <td>{c.category}</td>
                  <td>{c.quantity}</td>
                  <td>â‚¹{c.price}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>

        {/* LOANS */}
        <section>
          <h2>Loan Applications</h2>
          <table>
            <thead>
              <tr>
                <th>Loan ID</th>
                <th>Farmer</th>
                <th>Amount</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {data.loans.map((l, i) => (
                <tr key={i}>
                  <td>{l.loanId}</td>
                  <td>{l.farmerId}</td>
                  <td>â‚¹{l.amount}</td>
                  <td>{l.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>

        {/* INSURANCE */}
        <section>
          <h2>Insurance Applications</h2>
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Farmer</th>
                <th>Crop</th>
                <th>Coverage</th>
              </tr>
            </thead>
            <tbody>
              {data.insurance.map((i, idx) => (
                <tr key={idx}>
                  <td>{i.insuranceId}</td>
                  <td>{i.farmerId}</td>
                  <td>{i.crop}</td>
                  <td>{i.coverage}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      </div>
    </>
  );
}

export default AdminDashboard;
