import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import { useNavigate } from "react-router-dom";
import "../Css/AdminDashboard.css";

function AdminDashboard() {
  const [data, setData] = useState({
    users: [],
    crops: [],
    loans: [],
    insurance: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const loadAdminData = async () => {
      try {
        const res = await fetch("/api/admin/dashboard", {
          credentials: "include",
          cache: "no-store",
        });

        if (res.status === 403) {
          throw new Error("NOT_AUTHORIZED");
        }

        if (!res.ok) {
          throw new Error("SERVER_ERROR");
        }

        const json = await res.json();
        setData({
          users: json.users || [],
          crops: json.crops || [],
          loans: json.loans || [],
          insurance: json.insurance || []
        });
      } catch (err) {
        console.error("Admin dashboard error:", err);

        if (err.message === "NOT_AUTHORIZED") {
          setError("You are not authorized to view this page.");
          setTimeout(() => navigate("/home"), 1500);
        } else {
          setError("Something went wrong. Try again.");
        }
      } finally {
        setLoading(false);
      }
    };

    loadAdminData();
  }, [navigate]);

  /* ================= LOADING ================= */
  if (loading) {
    return (
      <>
        <Navbar />
        <div className="admin-loading">Loading admin dashboard...</div>
      </>
    );
  }

  /* ================= ERROR ================= */
  if (error) {
    return (
      <>
        <Navbar />
        <div className="admin-loading error">{error}</div>
      </>
    );
  }

  // Helper to format cell values consistently
  const formatCell = (val) => {
    if (val === null || val === undefined || val === "") return "-";
    if (Array.isArray(val)) return val.join(", ");
    if (typeof val === "object") return JSON.stringify(val);
    return String(val);
  };

  // Generic table section that renders all keys in the dataset
  const TableSection = ({ title, rows }) => {
    const cols = rows && rows.length
      ? Array.from(new Set(rows.reduce((acc, r) => acc.concat(Object.keys(r)), [])))
      : [];

    return (
      <section className="admin-section" style={{ width: "100%" }}>
        <h2>{title}</h2>
        <div className="admin-table" style={{ width: "100%" }}>
          <table style={{ width: "100%" }}>
            <thead>
              <tr>
                {cols.map((col) => (
                  <th key={col}>{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {rows && rows.length ? (
                rows.map((r, idx) => (
                  <tr key={idx}>
                    {cols.map((col) => (
                      <td key={col}>{formatCell(r[col])}</td>
                    ))}
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={Math.max(cols.length, 1)} className="empty">
                    No records found
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </section>
    );
  };

  /* ================= DASHBOARD ================= */
  return (
    <>
      <Navbar />

      <div className="admin-container" style={{ width: "100%", margin: 0 }}>
        <h1>Admin Dashboard</h1>
        <p className="admin-subtitle">
          Overview of platform statistics and records
        </p>

        {/* ===== STATS ===== */}
        <div className="admin-stats">
          <div className="stat-card">
            <h3>Total Users</h3>
            <p>{data.users.length}</p>
          </div>
          <div className="stat-card">
            <h3>Total Crops</h3>
            <p>{data.crops.length}</p>
          </div>
          <div className="stat-card">
            <h3>Loan Applications</h3>
            <p>{data.loans.length}</p>
          </div>
          <div className="stat-card">
            <h3>Insurance Applications</h3>
            <p>{data.insurance.length}</p>
          </div>
        </div>

        {/* ===== USERS ===== */}
        <TableSection title="Users" rows={data.users} />

        {/* ===== CROPS ===== */}
        <TableSection title="Crops" rows={data.crops} />

        {/* ===== LOANS ===== */}
        <TableSection title="Loan Applications" rows={data.loans} />

        {/* ===== INSURANCE ===== */}
        <TableSection title="Insurance Applications" rows={data.insurance} />
      </div>
    </>
  );
}

export default AdminDashboard;
