import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../Css/Checkout.css";
import Navbar from "./Navbar";
import Sidebar from "./Sidebar";
import Footer from "./Footer";

function Checkout() {
  const navigate = useNavigate();

  const [cart, setCart] = useState([]);
  const [placing, setPlacing] = useState(false);

  const [form, setForm] = useState({
    fullName: "",
    phone: "",
    addressLine1: "",
    addressLine2: "",
    city: "",
    state: "",
    pincode: "",
    payment: "cod",
  });

  /* =========================
     LOAD CART + USER DATA
  ========================= */
  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(savedCart);

    const savedUser = JSON.parse(localStorage.getItem("userData"));
    if (savedUser) {
      setForm((f) => ({
        ...f,
        fullName: savedUser.fullName || "",
        phone: savedUser.mobile || savedUser.phone || "",
      }));
    }
  }, []);

  /* =========================
     IMAGE HANDLER
  ========================= */
  const getImage = (imgStr) => {
    if (!imgStr) return "/Images/noimg.png";
    if (imgStr.startsWith("http") || imgStr.startsWith("data:")) return imgStr;

    const first = imgStr.split(",")[0].trim();
    return `http://localhost:9090/backend/uploads/${first}`;
  };

  /* =========================
     TOTALS
  ========================= */
  const totals = useMemo(() => {
    const subtotal = cart.reduce(
      (sum, item) => sum + item.price * item.qty,
      0
    );
    const tax = Math.round(subtotal * 0.06);
    const total = subtotal + tax;
    return { subtotal, tax, total };
  }, [cart]);

  const fmt = (n) => new Intl.NumberFormat("en-IN").format(n);

  /* =========================
     VALIDATION
  ========================= */
  const canPlace =
    cart.length > 0 &&
    form.fullName.trim() &&
    form.phone.trim().length === 10 &&
    form.addressLine1.trim() &&
    form.city.trim() &&
    form.state.trim() &&
    form.pincode.trim().length === 6;

  /* =========================
     PLACE ORDER
  ========================= */
  const placeOrder = async () => {
    if (!canPlace || placing) return;

    try {
      setPlacing(true);

      // ðŸ”¹ Replace with backend API when ready
      await new Promise((res) => setTimeout(res, 800));

      localStorage.setItem("cart", JSON.stringify([]));
      window.dispatchEvent(new Event("cartUpdated"));

      alert("Order placed successfully! ðŸŽ‰");
      navigate("/dashboard", { replace: true });
    } catch {
      alert("Failed to place order");
    } finally {
      setPlacing(false);
    }
  };

  return (
    <>
      <Navbar />

      <div className="layout">
        <Sidebar cartCount={cart.length} />

        <div className="checkout-content">
          <main className="checkout-main">
            <header className="checkout-header">
              <h2>Checkout</h2>
              <p>Confirm shipping and payment details</p>
            </header>

            {cart.length === 0 ? (
              <p style={{ padding: 20 }}>Your cart is empty.</p>
            ) : (
              <div className="checkout-grid">
                {/* ================= SHIPPING ================= */}
                <section className="card form-card">
                  <h3>Shipping Details</h3>

                  <label>Full Name</label>
                  <input
                    type="text"
                    placeholder="Enter full name"
                    value={form.fullName}
                    onChange={(e) =>
                      setForm({ ...form, fullName: e.target.value })
                    }
                  />

                  <label>Phone</label>
                  <input
                    type="tel"
                    placeholder="10-digit mobile number"
                    value={form.phone}
                    onChange={(e) =>
                      setForm({ ...form, phone: e.target.value })
                    }
                  />

                  <label>Address Line 1</label>
                  <input
                    placeholder="House no, street"
                    value={form.addressLine1}
                    onChange={(e) =>
                      setForm({ ...form, addressLine1: e.target.value })
                    }
                  />

                  <label>Address Line 2</label>
                  <input
                    placeholder="Area, landmark (optional)"
                    value={form.addressLine2}
                    onChange={(e) =>
                      setForm({ ...form, addressLine2: e.target.value })
                    }
                  />

                  <div className="two-col">
                    <div>
                      <label>City</label>
                      <input
                        value={form.city}
                        onChange={(e) =>
                          setForm({ ...form, city: e.target.value })
                        }
                      />
                    </div>

                    <div>
                      <label>State</label>
                      <input
                        value={form.state}
                        onChange={(e) =>
                          setForm({ ...form, state: e.target.value })
                        }
                      />
                    </div>
                  </div>

                  <label>Pincode</label>
                  <input
                    type="number"
                    placeholder="6-digit pincode"
                    value={form.pincode}
                    onChange={(e) =>
                      setForm({ ...form, pincode: e.target.value })
                    }
                  />

				  <h3>Payment</h3>

				  <div className="payment-group">

				    {/* CASH ON DELIVERY */}
				    <label className={`payment-option ${form.payment === "cod" ? "active" : ""}`}>
				      <input
				        type="radio"
				        name="payment"
				        checked={form.payment === "cod"}
				        onChange={() => setForm({ ...form, payment: "cod" })}
				      />
				      <span>Cash on Delivery</span>
				    </label>

				    {/* GOOGLE PAY */}
				    <label className={`payment-option ${form.payment === "gpay" ? "active" : ""}`}>
				      <input
				        type="radio"
				        name="payment"
				        checked={form.payment === "gpay"}
				        onChange={() => setForm({ ...form, payment: "gpay" })}
				      />
				      <span>Google Pay (UPI)</span>
				    </label>

				    {/* PHONEPE */}
				    <label className={`payment-option ${form.payment === "phonepe" ? "active" : ""}`}>
				      <input
				        type="radio"
				        name="payment"
				        checked={form.payment === "phonepe"}
				        onChange={() => setForm({ ...form, payment: "phonepe" })}
				      />
				      <span>PhonePe (UPI)</span>
				    </label>

				    {/* PAYTM */}
				    <label className={`payment-option ${form.payment === "paytm" ? "active" : ""}`}>
				      <input
				        type="radio"
				        name="payment"
				        checked={form.payment === "paytm"}
				        onChange={() => setForm({ ...form, payment: "paytm" })}
				      />
				      <span>Paytm (UPI)</span>
				    </label>

				  </div>


                </section>

                {/* ================= SUMMARY ================= */}
                <aside className="card summary-card">
                  <h3>Order Summary</h3>

                  {cart.map((item) => (
                    <div className="item-row" key={item.cropId}>
                      <img
                        src={getImage(item.images)}
                        alt={item.cropName}
                        className="item-thumb"
                      />
                      <div>
                        <strong>{item.cropName}</strong>
                        <span>
                          {item.qty} Ã— â‚¹{fmt(item.price)}
                        </span>
                      </div>
                      <b>â‚¹{fmt(item.qty * item.price)}</b>
                    </div>
                  ))}

                  <div className="summary-rows">
                    <div className="row">
                      <span>Subtotal</span>
                      <b>â‚¹{fmt(totals.subtotal)}</b>
                    </div>
                    <div className="row">
                      <span>Tax (6%)</span>
                      <b>â‚¹{fmt(totals.tax)}</b>
                    </div>
                    <div className="row total">
                      <span>Total</span>
                      <b>â‚¹{fmt(totals.total)}</b>
                    </div>
                  </div>

                  <button
                    className="place-order-btn"
                    disabled={!canPlace || placing}
                    onClick={placeOrder}
                  >
                    {placing ? "Placing Order..." : "Place Order"}
                  </button>
                </aside>
              </div>
            )}
          </main>

          <Footer />
        </div>
      </div>
    </>
  );
}

export default Checkout;
