import React, { useEffect, useState } from "react";
import "../Css/Analytics.css";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import Footer from "../components/Footer";

function Analytics() {
  const [crops, setCrops] = useState([]);
  const [hoverValue, setHoverValue] = useState(null);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("userData"));
    if (!user) return;

    fetch(`http://localhost:9090/backend/api/crop/farmer?farmerId=${user.farmerId}`)
      .then((res) => res.json())
      .then((data) => setCrops(data))
      .catch(() => console.error("Analytics error"));
  }, []);

  return (
    <>
      <Navbar />

      <div className="layout">
        <Sidebar />

        <div className="content-area">
          <main className="analytics-container">

            <h2>ðŸ“Š Analytics Overview</h2>
            <p className="subtitle">Track your crop availability & insights</p>

            {/* Floating Animated Chart */}
            <section className="analytics-chart">
              <h3>Crop Availability (Kg)</h3>

              <div className="bar-chart">
                {crops.length === 0 ? (
                  <p>No data</p>
                ) : (
                  crops.map((c, i) => (
                    <div
                      key={i}
                      className="bar-item"
                      onMouseEnter={() => setHoverValue(c)}
                      onMouseLeave={() => setHoverValue(null)}
                    >
                      {/* Hover tooltip */}
                      {hoverValue?.cropId === c.cropId && (
                        <div className="tooltip">
                          {c.cropName}: {c.quantity} Kg
                        </div>
                      )}

                      <div
                        className="bar animated"
                        style={{
                          height: `${Number(c.quantity) * 2}px`,
                          animationDelay: `${i * 0.15}s`
                        }}
                      ></div>

                      <span>{c.cropName}</span>
                    </div>
                  ))
                )}
              </div>
            </section>

          </main>

          <Footer />
        </div>
      </div>
    </>
  );
}

export default Analytics;
