import React, { useState, useEffect } from "react";
import "../Css/Sell.css";

function Sell() {

  const [cropImages, setCropImages] = useState([]);
  const [user, setUser] = useState(null);

  // Load logged in user (farmerId stored from login)
  useEffect(() => {
    const u = JSON.parse(localStorage.getItem("user"));
    setUser(u);
  }, []);

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const previewFiles = files.map(file => ({
      file,
      preview: URL.createObjectURL(file)
    }));
    setCropImages([...cropImages, ...previewFiles]);
  };

  const removeImage = (index) => {
    setCropImages(cropImages.filter((_, i) => i !== index));
  };

  const submitCrop = async () => {

    if (!user || !user.farmerId) {
      alert("Farmer ID not found. Please login again.");
      return;
    }

    const formData = new FormData();
    formData.append("farmerId", user.farmerId);
    formData.append("cropName", document.getElementById("cropName").value);
    formData.append("category", document.getElementById("category").value);
    formData.append("quantity", document.getElementById("quantity").value);
    formData.append("price", document.getElementById("price").value);
    formData.append("deliveryTime", document.getElementById("deliveryTime").value);
    formData.append("grade", document.getElementById("grade").value);
    formData.append("description", document.getElementById("description").value);

    cropImages.forEach(img => {
      formData.append("images", img.file);
    });

    const res = await fetch("http://localhost:8080/AgroLink/sellingcrop", {
      method: "POST",
      body: formData
    });

    const data = await res.json();

    if (data.status === "SUCCESS") {
      alert("Crop Posted Successfully!");
      setCropImages([]);
    } else {
      alert("Failed to save crop");
    }
  };


  return (
    <div className="sell-container">

      <h2 className="sell-title">
        <i className="fa fa-store"></i> Sell Your Crops
      </h2>

      <div className="sell-layout">

        {/* IMAGE UPLOAD */}
        <div className="sell-image-box">

          <div className="sell-placeholder">
            <i className="fa fa-image"></i>
            <p>Upload Crop Images</p>
          </div>

          <label className="upload-btn">
            <i className="fa fa-upload"></i> Upload Images
            <input type="file" accept="image/*" multiple onChange={handleImageUpload} />
          </label>

          <div className="preview-section">
            {cropImages.map((img, index) => (
              <div className="preview-item" key={index}>
                <img src={img.preview} className="preview-img" alt="" />
                <button className="preview-remove" onClick={() => removeImage(index)}>✖</button>
              </div>
            ))}
          </div>

        </div>

        {/* FORM */}
        <div className="sell-form">

          <label className="sell-label">
            <i className="fa fa-leaf"></i> Crop Name
          </label>
          <input id="cropName" type="text" className="sell-input" />

          <label className="sell-label">
            <i className="fa fa-list"></i> Category
          </label>
          <select id="category" className="sell-input">
            <option>Grains</option>
            <option>Vegetables</option>
            <option>Fruits</option>
            <option>Pulses</option>
            <option>Oil Seeds</option>
          </select>

          <div className="sell-row">
            <div>
              <label className="sell-label">
                <i className="fa fa-weight-hanging"></i> Qty (Kg)
              </label>
              <input id="quantity" type="number" className="sell-input" />
            </div>

            <div>
              <label className="sell-label">
                <i className="fa fa-rupee-sign"></i> Price/Kg
              </label>
              <input id="price" type="number" className="sell-input" />
            </div>
          </div>

          <label className="sell-label">
            <i className="fa fa-clock"></i> Delivery Time
          </label>
          <select id="deliveryTime" className="sell-input">
            <option>1 Day</option>
            <option>2–3 Days</option>
            <option>Within a Week</option>
          </select>

          <label className="sell-label">
            <i className="fa fa-star"></i> Grade
          </label>
          <select id="grade" className="sell-input">
            <option>A (Premium)</option>
            <option>B (Good)</option>
            <option>C (Average)</option>
          </select>

          <label className="sell-label">
            <i className="fa fa-align-left"></i> Description
          </label>
          <textarea id="description" className="sell-textarea" />

          <button className="sell-submit" onClick={submitCrop}>
            <i className="fa fa-check-circle"></i> Post Crop For Sale
          </button>

        </div>
      </div>
    </div>
  );
}

export default Sell;
