import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import Footer from "../components/Footer";
import "../Css/TrackOrder.css";

function TrackOrder() {
  const { orderId } = useParams();
  const navigate = useNavigate();

  const orders = JSON.parse(localStorage.getItem("orders")) || [];
  const order = orders.find((o) => o.orderId === orderId);

  const steps = [
    { label: "Order Placed", desc: "We have received your order." },
    { label: "Order Confirmed", desc: "Your order has been confirmed." },
    { label: "Order Processed", desc: "We are preparing your order." },
    { label: "Delivered", desc: "Your order has been delivered." },
  ];

  const activeIndex = steps.findIndex(
    (s) => s.label.toLowerCase() === order?.status?.toLowerCase()
  );

  return (
    <>
      <Navbar />

      <div className="layout">
        <Sidebar cartCount={0} />

        {/* CONTENT AREA (same as My Crops) */}
        <div className="track-content">
          <main className="track-main">
            {!order ? (
              <div className="track-empty">
                <h2>Order Not Found</h2>
                <button onClick={() => navigate("/dashboard")}>
                  Go Home
                </button>
              </div>
            ) : (
              <>
                {/* HEADER */}
                <div className="track-header">
                  <h2>Track Order</h2>
                  <button onClick={() => navigate(-1)}>Back</button>
                </div>

                {/* INFO BAR */}
                <div className="track-info">
                  <div>
                    <span>Estimated Time</span>
                    <b>30 minutes</b>
                  </div>
                  <div>
                    <span>Order Number</span>
                    <b>{order.orderId}</b>
                  </div>
                </div>

                {/* TIMELINE */}
                <div className="track-timeline">
                  {steps.map((step, i) => (
                    <div
                      key={i}
                      className={`track-step ${
                        i <= activeIndex ? "active" : ""
                      }`}
                    >
                      <div className="dot" />
                      <div className="step-text">
                        <h4>{step.label}</h4>
                        <p>{step.desc}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </>
            )}
          </main>

          <Footer />
        </div>
      </div>
    </>
  );
}

export default TrackOrder;
