const BASE_URL = "http://localhost:9090/backend/api/auth";

/* =========================
   LOGIN (SESSION SAFE)
========================= */
export const loginUser = async (mobile, password) => {
  const res = await fetch(`${BASE_URL}/login`, {
    method: "POST",
    credentials: "include", // ⭐ REQUIRED for session
    headers: {
      "Content-Type": "application/x-www-form-urlencoded",
    },
    body: new URLSearchParams({
      mobile,
      password,
    }),
  });

  // ❗ Handle non-200 responses safely
  if (!res.ok) {
    throw new Error("Login request failed");
  }

  return res.json();
};

/* =========================
   SIGNUP (SESSION READY)
========================= */
export const signupUser = async (fullName, mobile, password) => {
  const res = await fetch(`${BASE_URL}/signup`, {
    method: "POST",
    credentials: "include", // ⭐ IMPORTANT (future auto-login support)
    headers: {
      "Content-Type": "application/x-www-form-urlencoded",
    },
    body: new URLSearchParams({
      fullName,
      mobile,
      password,
    }),
  });

  if (!res.ok) {
    throw new Error("Signup request failed");
  }

  return res.json();
};

/* =========================
   LOGOUT (OPTIONAL BUT BEST)
========================= */
export const logoutUser = async () => {
  const res = await fetch(`${BASE_URL}/logout`, {
    method: "POST",
    credentials: "include",
  });

  return res.json();
};
